UPDATE `locales_creature_text` SET `entry` = 11502,`groupid` = 0,`id` = 0,`text_loc3` = 'Seht Ragnaros - den Feuerfürsten! Er, der schon uralt war, als eure Welt noch jung war. Verneigt euch vor ihm, Sterbliche! Verneigt euch vor eurem Untergang!' WHERE `locales_creature_text`.`entry` = 11502 AND `locales_creature_text`.`groupid` = 0 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 11502,`groupid` = 1,`id` = 0,`text_loc3` = 'ZU FRÜH! IHR HABT MICH ZU FRÜH ERWECKT, EXEKUTUS! WAS HAT DIESE STÖRUNG ZU BEDEUTEN???' WHERE `locales_creature_text`.`entry` = 11502 AND `locales_creature_text`.`groupid` = 1 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 11502,`groupid` = 2,`id` = 0,`text_loc3` = 'Diese ungläubigen Sterblichen, mein Fürst! Sie drangen in Euer Allerheiligstes ein und trachteten danach Eure Geheimnisse zu stehlen!' WHERE `locales_creature_text`.`entry` = 11502 AND `locales_creature_text`.`groupid` = 2 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 11502,`groupid` = 3,`id` = 0,`text_loc3` = 'NARR! DU LÄSST ES ZU, DAS SICH DIESE INSEKTEN IM GEHEILIGTEN KERN HERUMTREIBEN? UND NUN FÜHRST DU SIE AUCH NOCH IN MEINE HEIMSTÄTTE? DU ENTTÄUSCHT MICH, EXECUTUS! IN DER TAT WERDE ICH GERECHTIGKRILT WALTEN LASSEN!' WHERE `locales_creature_text`.`entry` = 11502 AND `locales_creature_text`.`groupid` = 3 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 11502,`groupid` = 4,`id` = 0,`text_loc3` = 'NUN ZU EUCH, INSEKTEN! MUTIG HABT IHR NACH DER MACHT VON RAGNAROS GESUCHT. NUN SOLLT IHR SIE AUS ERSTER HAND ERFAHREN!' WHERE `locales_creature_text`.`entry` = 11502 AND `locales_creature_text`.`groupid` = 4 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 11502,`groupid` = 5,`id` = 0,`text_loc3` = 'Kommt herbei, meine Diener! Verteidigt euren Herren!' WHERE `locales_creature_text`.`entry` = 11502 AND `locales_creature_text`.`groupid` = 5 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 11502,`groupid` = 6,`id` = 0,`text_loc3` = 'NIEMAND BESIEGT DIE LEBENDE FLAMME! KOMMT IHR DIENER DES FEUERS! VORWÄRTS IHR KREATUREN DES HASSES! EUER MEISTER RUFT EUCH!!' WHERE `locales_creature_text`.`entry` = 11502 AND `locales_creature_text`.`groupid` = 6 AND `locales_creature_text`.`id` = 0;
