UPDATE `locales_creature_text` SET `entry` = 15990,`groupid` = 5,`id` = 0,`text_loc3` = 'Nein!!! Ich verfluche euch, Eindringlinge! Die Armeen des Lichkönigs werden euch jagen und vernichten. Ihr werdet eurem Schicksal nicht entrinnen...' WHERE `locales_creature_text`.`entry` = 15990 AND `locales_creature_text`.`groupid` = 5 AND `locales_creature_text`.`id` = 0;
