UPDATE `locales_creature_text` SET `entry` = 16812,`groupid` = 0,`id` = 0,`text_loc3` = 'Meine Damen und Herren, herzlich Willkommen zu unserer heutigen Vorstellung!' WHERE `locales_creature_text`.`entry` = 16812 AND `locales_creature_text`.`groupid` = 0 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 16812,`groupid` = 1,`id` = 0,`text_loc3` = 'Heute erforschen wir die Tiefen der menschlichen Seele indem wir ein verirrtes, einsamen Mädchen begleiten, dass -- mit Hilfe ihrer treuen Gefährten -- den Weg nach Hause sucht!' WHERE `locales_creature_text`.`entry` = 16812 AND `locales_creature_text`.`groupid` = 1 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 16812,`groupid` = 2,`id` = 0,`text_loc3` = 'Sie wird jedoch verfolgt... von einem gemeinen und bösartigen Weib!' WHERE `locales_creature_text`.`entry` = 16812 AND `locales_creature_text`.`groupid` = 2 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 16812,`groupid` = 3,`id` = 0,`text_loc3` = 'Wird sie es schaffen? Wird sie überleben? Nur die Zeit wird es zeigen. Und nun... möge die Vorstellung beginnen!' WHERE `locales_creature_text`.`entry` = 16812 AND `locales_creature_text`.`groupid` = 3 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 16812,`groupid` = 4,`id` = 0,`text_loc3` = 'Guten Abend meine Damen und Herren und willkommen zu unserer heutigen Vorstellung!' WHERE `locales_creature_text`.`entry` = 16812 AND `locales_creature_text`.`groupid` = 4 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 16812,`groupid` = 5,`id` = 0,`text_loc3` = 'Heute Abend ist nichts wie es scheint... denn heute Abend dürfen Sie Ihren Augen nicht trauen!' WHERE `locales_creature_text`.`entry` = 16812 AND `locales_creature_text`.`groupid` = 5 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 16812,`groupid` = 6,`id` = 0,`text_loc3` = 'Nehmen Sie zum Beispiel diese ruhige, ältere Dame, die auf den Besuch ihrer Enkeltochter wartet... Sicher muss man vor diesem süßen, grauhaarigen Mütterchen keine Angst haben!' WHERE `locales_creature_text`.`entry` = 16812 AND `locales_creature_text`.`groupid` = 6 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 16812,`groupid` = 7,`id` = 0,`text_loc3` = 'Aber lassen Sie sich nicht von mir hinters Licht führen! Sehen Sie selbst, was sich hinter den Schleiern verbirgt! Und nun... möge die Vorstellung beginnen!' WHERE `locales_creature_text`.`entry` = 16812 AND `locales_creature_text`.`groupid` = 7 AND `locales_creature_text`.`id` = 0;
