UPDATE `locales_creature_text` SET `entry` = 20129,`groupid` = 0,`id` = 0,`text_loc3` = 'Seid gegrüßt, $N. Ich werde Euch durch die Höhle führen. Versucht bitte mit mir Schritt zu halten.' WHERE `locales_creature_text`.`entry` = 20129 AND `locales_creature_text`.`groupid` = 0 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 20129,`groupid` = 1,`id` = 0,`text_loc3` = 'Wir wissen nicht, ob die Höhlen der Zeit schon immer für Sterbliche zugänglich waren. Genau genommen ist dies nur schwer zu sagen, denn der Zeitlose ruht niemals, ständig verändert er den Fluss der Zeit nach seinem Ermessen. Was Ihr heute hier seht, ist womöglich morgen schon nicht mehr existent. Es ist nicht auszuschließen, dass Ihr urplötzlich im Gestern aufwacht, ohne jegliche Erinnerungen an diesem Ort.' WHERE `locales_creature_text`.`entry` = 20129 AND `locales_creature_text`.`groupid` = 1 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 20129,`groupid` = 2,`id` = 0,`text_loc3` = 'Es ist seltsam, ich weiß... Die meisten Sterblichen verstehen nicht, was sie hier erblicken, und genauso oft ist das, was sie sehen, nicht mehr mit ihrer eigenen Wahrnehmung der Realität vereinbar.' WHERE `locales_creature_text`.`entry` = 20129 AND `locales_creature_text`.`groupid` = 2 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 20129,`groupid` = 3,`id` = 0,`text_loc3` = 'Folgt mir, bitte.' WHERE `locales_creature_text`.`entry` = 20129 AND `locales_creature_text`.`groupid` = 3 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 20129,`groupid` = 4,`id` = 0,`text_loc3` = '' WHERE `locales_creature_text`.`entry` = 20129 AND `locales_creature_text`.`groupid` = 4 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 20129,`groupid` = 5,`id` = 0,`text_loc3` = 'Als Bewahrer der Zeit wachen wir über Nozdormus Reich. Der Meister ist momentan nicht zugegen, was bedeutet, dass momentan Bestrebungen im Gange sind, den Verlauf der Zeit massiv zu verändern. Niemal würde der Meister sich in die Angelegenheiten der Sterblichen einmischen, vielmehr korrigiert er, duch andere verursachte, Manipulationen der Zeit.' WHERE `locales_creature_text`.`entry` = 20129 AND `locales_creature_text`.`groupid` = 5 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 20129,`groupid` = 6,`id` = 0,`text_loc3` = 'Für die reguläre Überwachung der Zeit, sind die Hüter mehr als ausreichend qualifiziert. Wir sind in der Lage selbstständig mit den meisten der herkömmlichen Störungen fertig zu werden. Ich spreche hier von kleinen Zwischenfällen, wie abtrünnigen Magiern, die versuchen, ein Ereignis in der Vergangenheit zu ihren Gunsten zu verändern, um so ihren Reichtum oder gesellschaftlichen Stand in der Gegenwart zu verbessern.' WHERE `locales_creature_text`.`entry` = 20129 AND `locales_creature_text`.`groupid` = 6 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 20129,`groupid` = 7,`id` = 0,`text_loc3` = 'Die Tunnel, die Ihr hier seht, sind die so genannten Pfade der Zeit. Ihre Zahl ist endlos. Diejenigen, welche momentan in Eurer Realität existieren, erchtet der Meister als ''Problemzonen''. Die verschiedenen Problemzonen unterscheiden sich in ihrer Problematik, aber sie alle entspringen derselben Ursache. Das heißt, sie verdanken ihre Existenz ein und derselben temporalen Störung. Denkt daran, falls Ihr eine davon betreten solltet...' WHERE `locales_creature_text`.`entry` = 20129 AND `locales_creature_text`.`groupid` = 7 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 20129,`groupid` = 8,`id` = 0,`text_loc3` = 'Dieser Pfad der Zeit befindet sich in gröter Unordnung! Wir haben einige unserer Agenten hereingeschickt, um die Ordnung wiederherzustellen. Die mir zur Verfügung stehenden Informationen weisen darauf hin, dass Thralls Freiheit in Gefahrt ist. Eine finstere Vereinigung, bekannt als der ewige Drachenschwarm, versucht seine Flucht zu verhindern. Ich befürchte, dass ohne Unterstützung von außen, alle verloren ist.' WHERE `locales_creature_text`.`entry` = 20129 AND `locales_creature_text`.`groupid` = 8 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 20129,`groupid` = 9,`id` = 0,`text_loc3` = 'Über diesen Pfad der Zeit stehen uns nur wenige Informationen zur Verfügung. Momentan kümmert sich Sa''at um diese Angelegenheit, er befindet sich im Inneren des Pfades. Soweit wir es seinen Botschaften entnehmen konnten, versucht der ewige Drachenschwarm erneut den Verlauf der Zeit zu ändern. Kann es tatsächlich möglich sein, dass sie planen, die Öffnung des Dunklen Portals zu verhindern? Ich hoffe nicht...' WHERE `locales_creature_text`.`entry` = 20129 AND `locales_creature_text`.`groupid` = 9 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 20129,`groupid` = 10,`id` = 0,`text_loc3` = '' WHERE `locales_creature_text`.`entry` = 20129 AND `locales_creature_text`.`groupid` = 10 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 20129,`groupid` = 11,`id` = 0,`text_loc3` = 'Wir haben die Pfade der Zeit nach ihrem Katastrophengrad, vom leichtesten bis zum schwersten, eingeteilt. Beachtet bitte, dass sie alle als katastrophal für das Gefüge der Zeit eingestuft wurden. Sollte nur ein einziger von ihnen kollabieren, würde dies das Ende Eurer Welt bedeuten. Die von uns vorgenommene Einteilung soll es den eintreffenden Helden leichter machen, Pfade der Zeit zu finden, die ihre Fähigkeiten entsprechen.' WHERE `locales_creature_text`.`entry` = 20129 AND `locales_creature_text`.`groupid` = 11 AND `locales_creature_text`.`id` = 0;
