UPDATE `locales_creature_text` SET `entry` = 26497,`groupid` = 0,`id` = 0,`text_loc3` = 'Arthas! Ihr könnt nicht einfach...' WHERE `locales_creature_text`.`entry` = 26497 AND `locales_creature_text`.`groupid` = 0 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26497,`groupid` = 1,`id` = 0,`text_loc3` = 'Tut mir leid, Arthas ... ich kann das nicht mit ansehen.' WHERE `locales_creature_text`.`entry` = 26497 AND `locales_creature_text`.`groupid` = 1 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 0,`id` = 0,`text_loc3` = 'Wie schön, dass Ihr es geschafft habt, Uther.' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 0 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 1,`id` = 0,`text_loc3` = 'Als könnte ich das vergessen. Hört her, Uther... Ihr solltet etwas über diese Seuche wissen...' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 1 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 2,`id` = 0,`text_loc3` = 'Oh nein. Wir kommen zu spät. Diese Leute sind allesamt infiziert. Noch sehen sie normal aus, aber es ist nur noch eine Frage der Zeit, bis sie Untote werden!' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 2 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 3,`id` = 0,`text_loc3` = 'Diese ganze Stadt muss gesäubert werden.' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 3 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 4,`id` = 0,`text_loc3` = 'Verdammt, Uther! Als künftiger König befehle ich Euch, die Stadt zu säubern!' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 4 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 5,`id` = 0,`text_loc3` = 'Dann muss ich das als Hochverrat ansehen!' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 5 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 6,`id` = 0,`text_loc3` = 'Nicht? Lord Uther, durch das Recht der Erbfolge und die Herrschaft der Krone... hiermit entbinde ich Euch von Eurem Kommando und entlasse Eure Paladine aus meinen Diensten.' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 6 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 7,`id` = 0,`text_loc3` = 'Diesmal seid Ihr zu weit gegangen, Arthas...' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 7 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 8,`id` = 0,`text_loc3` = 'Jaina?' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 8 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 9,`id` = 0,`text_loc3` = 'Bezieht hier Stellung und ich führe einen Trupp nach Stratholme und beginne mit der Säuberung. Wir müssen die Infizierten aufhalten und eliminieren, zum Wohle Lordaerons!' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 9 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 10,`id` = 0,`text_loc3` = 'Alle sind bereit. Vergesst nicht, diese Leute sind alle infiziert und werden bald sterben. Wir müssen Stratholme säubern, um den Rest Lordaerons vor der Geißel zu schützen. Los jetzt.' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 10 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 11,`id` = 0,`text_loc3` = 'Ich kann Euch nur mit einem schnellen Tod helfen.' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 11 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 12,`id` = 0,`text_loc3` = 'Das war erst der Anfang.' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 12 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 13,`id` = 0,`text_loc3` = 'Das lasse ich nicht zu, Mal''Ganis! Lieber sollen diese Leute durch meine Hand sterben, als Euch im Tode als Sklaven zu dienen!' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 13 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 14,`id` = 0,`text_loc3` = 'Mal''Ganis wird sicher seine Geißeldiener schicken, um uns aufzuhalten. Diejenigen mit dem stärksten Stahl und Zauber unter Euch bilden die Vorhut, um sie zu vernichten. Ich führe den Rest meiner Streitkräfte bei Stratholmes Säuberung von den Infizierten an.' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 14 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 15,`id` = 0,`text_loc3` = 'Recken, trefft mich sofort am Rathaus. Wir müssen Mal''Ganis in den Kampf verwickeln.' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 15 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 16,`id` = 0,`text_loc3` = 'Folgt mir, ich kenne den Weg.' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 16 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 17,`id` = 0,`text_loc3` = 'Ja, ich bin froh, dass ich vor der Seuche zu Euch gelangt bin.' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 17 AND `locales_creature_text`.`id` = 0;
