UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 19,`id` = 0,`text_loc3` = 'Mal''Ganis scheint mehr als nur die Geißel in seinem Arsenal zu haben. Wir sollten uns beeilen.' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 19 AND `locales_creature_text`.`id` = 0;
