UPDATE `locales_creature_text` SET `entry` = 26499,`groupid` = 31,`id` = 0,`text_loc3` = 'Lasst uns diese Stelle so schnell wie möglich passieren. Falls uns die Untoten nicht kriegen, dann vielleicht das Feuer.' WHERE `locales_creature_text`.`entry` = 26499 AND `locales_creature_text`.`groupid` = 31 AND `locales_creature_text`.`id` = 0;
