UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 0,`id` = 0,`text_loc3` = 'So verdient man sich die Brötchen!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 0 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 0,`id` = 1,`text_loc3` = 'Vorwärts, so wirds gemacht.' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 0 AND `locales_creature_text`.`id` = 1;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 0,`id` = 2,`text_loc3` = 'Macht weiter, es ist genug Tod für alle da!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 0 AND `locales_creature_text`.`id` = 2;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 1,`id` = 0,`text_loc3` = 'Mich hats überall erwischt. Schaff es... vielleicht nicht.' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 1 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 2,`id` = 0,`text_loc3` = 'Noch nicht, noch ni...' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 2 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 3,`id` = 0,`text_loc3` = 'Ich tue, was ich kann!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 3 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 3,`id` = 1,`text_loc3` = 'Das Licht schütze Euch.' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 3 AND `locales_creature_text`.`id` = 1;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 3,`id` = 2,`text_loc3` = 'Hoffentlich ist es das alles wert!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 3 AND `locales_creature_text`.`id` = 2;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 4,`id` = 0,`text_loc3` = 'Zeit für ein paar Antworten! Lasst und loslegen!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 4 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 5,`id` = 0,`text_loc3` = 'Keine Sorge! Der alte Brann ist da! Kümmert Euch um das Metallmontser und ich schau'', ob ich der Maschine lieb zureden kann, Euch zu helfen!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 5 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 6,`id` = 0,`text_loc3` = 'Das is''n bisschen kniffliger als vorher... Oh, verdammt... da kommt was!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 6 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 7,`id` = 0,`text_loc3` = 'Was in Madorans Namen hat DAS gemacht? Oh, wartet, Ich hab''s gleich...' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 7 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 8,`id` = 0,`text_loc3` = 'Ha, das war''s! Hilfe ist im Anmarsch! Nehmt dies glühender Eisenrohling!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 8 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 9,`id` = 0,`text_loc3` = 'Nehmt Euch einen Moment und genießt das mit mir. Bald... wird alles offenbart. Alles klar, dann mal los!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 9 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 10,`id` = 0,`text_loc3` = 'Haltet jetzt die Augen offen! Ich werde die Sache hier im Handumgrehen...' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 10 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 11,`id` = 0,`text_loc3` = 'Oh, das hört sich nicht gut an. Wir haben veilleicht ein Problem oder zwei...' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 11 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 12,`id` = 0,`text_loc3` = 'Ihr wollt es also auf die harte Tour, was? Die hab ich am liebsten!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 12 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 13,`id` = 0,`text_loc3` = 'Noch''n paar Minuten und ich werde...' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 13 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 14,`id` = 0,`text_loc3` = 'Erhöht? Und was ist die gute Nachricht' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 14 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 15,`id` = 0,`text_loc3` = 'Das war also das Problem! Jetzt komm'' ich voran...' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 15 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 16,`id` = 0,`text_loc3` = 'Sekunde! Hier wird niemand gesäubert, solange ich ein Wörtchen mitzureden hab''!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 16 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 17,`id` = 0,`text_loc3` = 'Ha! Die alten, magischen Finger haben''s letztendlich doch geschafft! Machen wir uns jetzt an...' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 17 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 18,`id` = 0,`text_loc3` = 'Säubern? Nein, nein, nein, nein, nein... Wo habe ich... Ah, das sollte helfen...' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 18 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 19,`id` = 0,`text_loc3` = 'Frage? Was glaubt Ihr, wozu ich hier bin, Kaffee und Kuchen? Spuckt es endlich aus!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 19 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 20,`id` = 0,`text_loc3` = 'Sagt mir, wo die Zwerge herkommen, und fangt von ganz vorne an!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 20 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 21,`id` = 0,`text_loc3` = 'Gut, gut... Ich weiß, dass die Irdenen aus Stein geschaffen wurden, um die Tiefen der Welt zu formen. Aber was ist mit den Anomalien? Matrixprobleme und sowas?' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 21 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 22,`id` = 0,`text_loc3` = 'Nekro-was-fürn-Zeug? Sprecht so, dass man es versteht!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 22 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 23,`id` = 0,`text_loc3` = 'Alte Götter wie? Die haben also den Fluch des Fleisches auf die Irdenen geleget. Und was dann?' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 23 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 24,`id` = 0,`text_loc3` = 'Wenn sie die alten Götter getötet hätten, so wäre Azeroth zerstört worden.' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 24 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 25,`id` = 0,`text_loc3` = 'Welche Beschützer?' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 25 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 26,`id` = 0,`text_loc3` = 'Aesir und Vanir. Ok. Also hat die Seelenschmiede neue Irdenen hergestellt. Aber was passierte mit den Alten?' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 26 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 27,`id` = 0,`text_loc3` = 'Moment mal! Die Aesir und Vanir zogen in den Krieg? Warum?' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 27 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 28,`id` = 0,`text_loc3` = 'Dieser Loken kling wie ein übler Zeitgenosse. Gut das wir uns um sowas keine Sorgen mehr machen müssen!  Also, wenn ich das richtig verstehe, dann erwachten die ursprünglichen Irdenen irgendwann aus der Stasis. Und da hatte des Destabilietät sie schon in echte Zwerge verwandelt. Oder zumindest in Zwergvorfahren.' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 28 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 29,`id` = 0,`text_loc3` = 'Nun denn. Das muss erstmal verdaut werden. Ich brauch etwas Zeit bis sich das alles gesetzt hat. Habt Dank!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 29 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 30,`id` = 0,`text_loc3` = 'Loken?! Das ist doch einfach lästig... Wir haben vielleicht die Eisenzwerge ausgeschaltet, doch ich möchte wetten, dass es irgendwo noch eine weitere Maschine gibt, die am laufenden Band massenweise Eisenvrykul ausspuckt.' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 30 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 31,`id` = 0,`text_loc3` = 'Ich werde die Schmiede benutzen, um ''ne Ladung Irdene zu erschaffen, die Wache stehen können... Aber unsere größte Herausforderung bleibt bestehen: Loken zu finden und aufzuhalten!' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 31 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 28070,`groupid` = 32,`id` = 0,`text_loc3` = 'Ihr habt Recht, damit kann ich auch später noch weiter machen. Ich glaube, es ist an der Zeit herauszufinden, was sich hinter der Tür neben dem Eingang befindet. Ich werde mal hübsch leise rüberschleichen. Trfft mich bei der Tür und ich werde uns reinlassen.' WHERE `locales_creature_text`.`entry` = 28070 AND `locales_creature_text`.`groupid` = 32 AND `locales_creature_text`.`id` = 0;
