UPDATE `locales_creature_text` SET `entry` = 30897,`groupid` = 0,`id` = 0,`text_loc3` = 'Bedrohungsindexgrenze überschritten. Himmelsarchiv abgebrochen. Alarmstufe erhöht.' WHERE `locales_creature_text`.`entry` = 30897 AND `locales_creature_text`.`groupid` = 0 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 30897,`groupid` = 1,`id` = 0,`text_loc3` = 'Weiterer Hintergrund für Anfrage benötigt. Folgend auf globalen Kampf zwischen Aesri und Vanir.' WHERE `locales_creature_text`.`entry` = 30897 AND `locales_creature_text`.`groupid` = 1 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 30897,`groupid` = 2,`id` = 0,`text_loc3` = 'Unbekannt. Daten lassen darauf schließen das Ursache für den globalen Kampf, bei Haupteinheit Loken zufinden ist. Haupteinheit Loken neutralisierte alle verbliebenen Aesir und Vanir, was zur Terminierung des Konfliktes führte. Haupteinheit Loken initialisierte dann Stasiszustand über einige Spezies, einschließlich Irdene, Riesen und Vrykul in designierten Lagereinrichtungen.' WHERE `locales_creature_text`.`entry` = 30897 AND `locales_creature_text`.`groupid` = 2 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 30897,`groupid` = 3,`id` = 0,`text_loc3` = 'Im Wesentlichen ist das korrekt!' WHERE `locales_creature_text`.`entry` = 30897 AND `locales_creature_text`.`groupid` = 3 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 30898,`groupid` = 0,`id` = 0,`text_loc3` = 'Sicherheitsverletzung. Setze Analyse historischer Archive auf niedrige Priorität. Leite Gegenmaßnahmen ein.' WHERE `locales_creature_text`.`entry` = 30898 AND `locales_creature_text`.`groupid` = 0 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 30898,`groupid` = 1,`id` = 0,`text_loc3` = 'Zugriff, Schöpfer eingetroffen um symbiotische Infektionen auszumerzen. Beurteilung zeigt bösartige Verseuchung durch alte Götter. Extraktion der Parasiten würde zum Verlust des Wirts führen!' WHERE `locales_creature_text`.`entry` = 30898 AND `locales_creature_text`.`groupid` = 1 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 30898,`groupid` = 2,`id` = 0,`text_loc3` = 'Korrekt. Schöpfer neutralisierten Parasitenbedrohung und fingen sie im Wirt. Seelenschmiede und andere Systeme, wurden zur Erschaffung neuer Irdener installiert. Schutzmaßnahmen wurden erstellt und Beschützer wurden ernannt.' WHERE `locales_creature_text`.`entry` = 30898 AND `locales_creature_text`.`groupid` = 2 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 30898,`groupid` = 3,`id` = 0,`text_loc3` = 'Benennung: Aesir und Vanir oder andere Nomenklatur, Sturm und Erdriesen. Schildwache Loken zu Befehlshaber ernannt. Drachenaspekte zur Aufsicht über die Evolution Azeroths bestimmt.' WHERE `locales_creature_text`.`entry` = 30898 AND `locales_creature_text`.`groupid` = 3 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 30899,`groupid` = 0,`id` = 0,`text_loc3` = 'Warnung: Struktur der Lebensform nicht erkannt. Archivierungsprozess abgebrochen. Fortgesetzte Interferenz wird in gezielter Antwort resultieren.' WHERE `locales_creature_text`.`entry` = 30899 AND `locales_creature_text`.`groupid` = 0 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 30899,`groupid` = 1,`id` = 0,`text_loc3` = 'Kritischer Bedrohungsindex. Leerenanalyse umgeleitet. Initialisiere Säuberungsprotokoll.' WHERE `locales_creature_text`.`entry` = 30899 AND `locales_creature_text`.`groupid` = 1 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 30899,`groupid` = 2,`id` = 0,`text_loc3` = 'Alarm: Sicherungssystem deaktiviert. Beginne Speichersäuberung und...' WHERE `locales_creature_text`.`entry` = 30899 AND `locales_creature_text`.`groupid` = 2 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 30899,`groupid` = 3,`id` = 0,`text_loc3` = 'System bereit. Lebensform identifiziert. Willkommen Branbronze. Frage?' WHERE `locales_creature_text`.`entry` = 30899 AND `locales_creature_text`.`groupid` = 3 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 30899,`groupid` = 4,`id` = 0,`text_loc3` = 'Zugriff auf prähistorische Daten... Empfangen. Am Anfang wurden Irdene erschaffen, um...' WHERE `locales_creature_text`.`entry` = 30899 AND `locales_creature_text`.`groupid` = 4 AND `locales_creature_text`.`id` = 0;
UPDATE `locales_creature_text` SET `entry` = 30899,`groupid` = 5,`id` = 0,`text_loc3` = 'Zugriff... Im frühen Entstehungsprozess litt Azeroth unter Infektionen durch parasitäre nekrophotische Symbionten.' WHERE `locales_creature_text`.`entry` = 30899 AND `locales_creature_text`.`groupid` = 5 AND `locales_creature_text`.`id` = 0;
