UPDATE `locales_npc_text` SET `ID` = 1651,`Text0_0_loc3` = 'Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-D, einem Sicherheitsterminal der Stufe Delta.$B$BFür den Zugriff auf die Terminalfunktionen wird eine rote Lochkarte benötigt.$B$B01010011 01101000 01101111 01110010 01110100 00100000 01100001 01101110 01100100 00100000 01110011 01110111 01100101 01100101 01110100',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 1651;
UPDATE `locales_npc_text` SET `ID` = 1652,`Text0_0_loc3` = 'Einführen der roten Lochkarte erfolgreich... Es erfolgt Ausgabe einer Datenkarte der Sicherheitsstufe Delta.$B$B00111100 01101001 01101110 01110011 01100101 01110010 01110100 00100000 01100110 01110101 01101110 01101110 01111001 00100000 01101101 01100101 01110011 01110011 01100001 01100111 01100101 00100000 01101000 01100101 01110010 01100101 00111110',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 1652;
UPDATE `locales_npc_text` SET `ID` = 1653,`Text0_0_loc3` = 'Eraka no Kimbul$BGott der Tiger, Lord der Bestien, König der Katzen, Verhängnis der Beutetiere$B$BUeetay no Mueh''zala$BGott des Todes, Vater des Schlafes, Sohn der Zeit, Freund der Nacht.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 1653;
UPDATE `locales_npc_text` SET `ID` = 1654,`Text0_0_loc3` = 'Elortha no Shadra$B$BGöttin der Spinnen, Mutter des Giftes, Seidentänzerin, Geliebte des Todes.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 1654;
UPDATE `locales_npc_text` SET `ID` = 1655,`Text0_0_loc3` = 'Der Technologie-Wahrheitsdetektor des Matrix-Prägographen hat Euer Können in Sachen Ingenieurskunst bestätigt. Wählt nun unter einer der folgenden Optionen:$B$B01000111 01101111 00100000 01100111 01101111 00100000 01100111 01101111 00100001 00100001',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 1655;
