UPDATE `locales_npc_text` SET `ID` = 2563,`Text0_0_loc3` = 'Ihr sucht nach Shenthul von der Bruderschaft der Schattenläufer, hm? Sucht in der Gasse gegenüber von ''Droffers und Sohn Bergungen'' nach einem Tunnel, der in die Kluft der Schatten hinabführt.',`Text0_1_loc3` = 'Ihr sucht nach Shenthul von der Bruderschaft der Schattenläufer, hm? Sucht in der Gasse gegenüber von ''Droffers und Sohn Bergungen'' nach einem Tunnel, der in die Kluft der Schatten hinabführt.' WHERE `locales_npc_text`.`ID` = 2563;
UPDATE `locales_npc_text` SET `ID` = 2564,`Text0_0_loc3` = 'Ihr sucht Grol''dar und die Dunkelfeuerenklave? Sucht in der unterirdischen Höhle namens Kluft der Schatten danach. Es führen zwei Tunnel in diese Höhle hinab, einer südlich des Tals der Weisheit, einer von der Gasse.',`Text0_1_loc3` = 'Ihr sucht Grol''dar und die Dunkelfeuerenklave? Sucht in der unterirdischen Höhle namens Kluft der Schatten danach. Es führen zwei Tunnel in diese Höhle hinab, einer südlich des Tals der Weisheit, einer von der Gasse.' WHERE `locales_npc_text`.`ID` = 2564;
UPDATE `locales_npc_text` SET `ID` = 2565,`Text0_0_loc3` = 'Sprecht mit Grezz Zornfaust in der Halle der Kriegerhelden. Sie befindet sich im Tal der Ehre. Legt ein gutes Wort für mich ein!',`Text0_1_loc3` = 'Sprecht mit Grezz Zornfaust in der Halle der Kriegerhelden. Sie befindet sich im Tal der Ehre. Legt ein gutes Wort für mich ein!' WHERE `locales_npc_text`.`ID` = 2565;
