UPDATE `locales_npc_text` SET `ID` = 2568,`Text0_0_loc3` = 'Nur sehr wenige suchen nach mir... Was kann ich für Euch tun?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 2568;
UPDATE `locales_npc_text` SET `ID` = 2570,`Text0_0_loc3` = 'Hi, hi... Ihr seid recht neugierig, was, $N? Also, das müsst Ihr tun:$B$BNördlich von hier, in den alten Ruinen, lebt ein höchst übellauniger Gnoll namens Miblon Fletschmaul. Er bewacht die einzige Evowurzelpflanze, die ich kenne, und das tut er Tag und Nacht! Er scheint niemals zu schlafen!$B$BAllerdings könnt Ihr eines tun: ihn bestechen. Ich kann Euch Köder verkaufen, die er lecker finden wird - legt sie einfach in seiner Nähe hin, das lenkt ihn lange genug ab, dass Ihr Euch die Evowurzel schnappen könnt. Viel Glück!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 2570;
UPDATE `locales_npc_text` SET `ID` = 2574,`Text0_0_loc3` = 'Ja, aber Ihr werdet feststellen, dass es sehr viel lohnender ist, wenn Ihr auch einige Fertigkeiten in der Kräuterkunde habt! Damit könnt Ihr besondere Samenkörner anbauen und aufziehen. Mit dem Immergrünbeutel, den Ihr erhaltet, kann zwar selbst ein absoluter Neuling etwas wachsen lassen, aber alle, die über ein bisschen Wissen in der Kräuterkunde verfügen, werden eine reichlichere Ernte erzielen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 2574;
UPDATE `locales_npc_text` SET `ID` = 2575,`Text0_0_loc3` = 'Seid gegrüßt, $C. Was kann dieser bescheidene Diener der Mondfederfeste heute für Euch tun?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 2575;
UPDATE `locales_npc_text` SET `ID` = 2593,`Text0_0_loc3` = 'Ihr sucht doch etwas. Was ist es?',`Text0_1_loc3` = 'Ihr sucht doch etwas. Was ist es?' WHERE `locales_npc_text`.`ID` = 2593;
UPDATE `locales_npc_text` SET `ID` = 2594,`Text0_0_loc3` = 'Welcher Beruf?',`Text0_1_loc3` = 'Welcher Beruf?' WHERE `locales_npc_text`.`ID` = 2594;
UPDATE `locales_npc_text` SET `ID` = 2595,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Der Tod unseres Fleisches bedeutete die Geburt unseres Geistes und unserer heiligen Aufgabe.' WHERE `locales_npc_text`.`ID` = 2595;
UPDATE `locales_npc_text` SET `ID` = 2596,`Text0_0_loc3` = 'Unser Anführer Un''rel hat die Herausforderung gehört.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 2596;
