UPDATE `locales_npc_text` SET `ID` = 3014,`Text0_0_loc3` = 'Wo das Auktionshaus ist? Das liegt direkt beim Stadttor von Eisenschmiede, wenn Ihr unsere schöne Stadt betretet. Ihr findet es gleich gegenüber des Tresors.',`Text0_1_loc3` = 'Wo das Auktionshaus ist? Das liegt direkt beim Stadttor von Eisenschmiede, wenn Ihr unsere schöne Stadt betretet. Ihr findet es gleich gegenüber des Tresors.' WHERE `locales_npc_text`.`ID` = 3014;
