UPDATE `locales_npc_text` SET `ID` = 3311,`Text0_0_loc3` = 'Bringt es nicht näher, sterbliches Wesen. Ich spüre das Böse in diesem Siegel schon aus dieser Entfernung.$B$BGebt mir einen Moment Zeit, um den Gegenstand zu inspizieren - haltet ihn gut fest.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 3311;
