UPDATE `locales_npc_text` SET `ID` = 3546,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Willkommen in der Zuflucht, $N. Manche kommen absichtlich hierher, andere finden den Weg durch Zufall... Aber jeder hier hat eine einmalige Geschichte zu erzählen.' WHERE `locales_npc_text`.`ID` = 3546;
