UPDATE `locales_npc_text` SET `ID` = 4279,`Text0_0_loc3` = 'Ich hatte vor kurzem eine unangenehme Begegnung mit ein paar Defias hier aus der Gegend, aber Michelle Belle hat mich wieder ziemlich gut zusammengeflickt. Besucht sie doch im Gasthaus Zur Höhle des Löwen und findet heraus, ob sie bereit ist, Euch Unterricht zu erteilen.',`Text0_1_loc3` = 'Ich hatte vor kurzem eine unangenehme Begegnung mit ein paar Defias hier aus der Gegend, aber Michelle Belle hat mich wieder ziemlich gut zusammengeflickt. Besucht sie doch im Gasthaus Zur Höhle des Löwen und findet heraus, ob sie bereit ist, Euch Unterricht zu erteilen.' WHERE `locales_npc_text`.`ID` = 4279;
