UPDATE `locales_npc_text` SET `ID` = 4877,`Text0_0_loc3` = 'Ich kann Euch die Feinheiten des Tigerreitens beibringen, vorausgesetzt, Ihr habt genug Gold, um die Stunde zu bezahlen!',`Text0_1_loc3` = 'Ich kann Euch die Feinheiten des Tigerreitens beibringen, vorausgesetzt, Ihr habt genug Gold, um die Stunde zu bezahlen!' WHERE `locales_npc_text`.`ID` = 4877;
UPDATE `locales_npc_text` SET `ID` = 4878,`Text0_0_loc3` = 'Ich kann Euch die Feinheiten des Roboschreiterlenkens beibringen, vorausgesetzt, Ihr habt Geld genug, um die Stunde zu bezahlen! Wollt Ihr nun lernen, wie man einen Roboschreiter reitet oder nicht?',`Text0_1_loc3` = 'Ich kann Euch die Feinheiten des Roboschreiterlenkens beibringen, vorausgesetzt, Ihr habt Geld genug, um die Stunde zu bezahlen! Wollt Ihr nun lernen, wie man einen Roboschreiter reitet oder nicht?' WHERE `locales_npc_text`.`ID` = 4878;
UPDATE `locales_npc_text` SET `ID` = 4879,`Text0_0_loc3` = 'Ich kann Euch die Feinheiten des Wolfreitens beibringen, vorausgesetzt, Ihr verfügt über ausreichende Mittel, um die Stunde bezahlen zu können!',`Text0_1_loc3` = 'Ich kann Euch die Feinheiten des Wolfreitens beibringen, vorausgesetzt, Ihr verfügt über ausreichende Mittel, um die Stunde bezahlen zu können!' WHERE `locales_npc_text`.`ID` = 4879;
UPDATE `locales_npc_text` SET `ID` = 4881,`Text0_0_loc3` = 'Es wäre eine Verschwendung sowohl Eures Goldes als auch meiner Zeit, wenn ich Euch beibringen würde, wie man einen Roboschreiter steuert, $C. Ihr seid einfach zu groß dafür!',`Text0_1_loc3` = 'Es wäre eine Verschwendung sowohl Eures Goldes als auch meiner Zeit, wenn ich Euch beibringen würde, wie man einen Roboschreiter steuert, $C. Ihr seid einfach zu groß dafür!' WHERE `locales_npc_text`.`ID` = 4881;
UPDATE `locales_npc_text` SET `ID` = 4888,`Text0_0_loc3` = 'Ich bilde junge Jäger und Jägerinnen aus und vermittle ihnen die Traditionen, die bereits seit vielen Generationen weitergetragen werden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4888;
UPDATE `locales_npc_text` SET `ID` = 4889,`Text0_0_loc3` = 'Vergesst nicht, alle Dinge hängen miteinander zusammen. Wenn es in diesem Land keine Tiere mehr gäbe, dann würde unsereins an Einsamkeit der Seele sterben, denn was den Tieren widerfährt, widerfährt auch den Tauren. Und alles, was Azeroth widerfährt, widerfährt allen, die es bewohnen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4889;
UPDATE `locales_npc_text` SET `ID` = 4890,`Text0_0_loc3` = 'Wenn Ihr hier seid, um Euch in der Taktik und der Strategie des Jagens ausbilden zu lassen, dann seid Ihr am richtigen Ort.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4890;
UPDATE `locales_npc_text` SET `ID` = 4891,`Text0_0_loc3` = 'He, Mann! Seid Ihr schon mal tief in den Ozean hinuntergetaucht? Ich verrat Euch mal ein kleines Geheimnis, ey... Tief unten im Ozean finden sich blubbernde Felsspalten, und dank denen kann man unter Wasser atmen. Aber das bleibt unser Geheimnis, ja? Ich kann keine Konkurrenten für meine Schalentierfallen brauchen... Ihr versteht doch?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4891;
UPDATE `locales_npc_text` SET `ID` = 4893,`Text0_0_loc3` = 'Seht Euch die Weisheit und das Wissen der Tiere an, so seht Ihr Euch selbst. Beobachtet, wie die Eulen das tun, nehmt die Geschicklichkeit der Säblerkatze an und macht Euch den mächtigen Geist des Wolfs zu Eigen.',`Text0_1_loc3` = 'Seht Euch die Weisheit und das Wissen der Tiere an, so seht Ihr Euch selbst. Beobachtet, wie die Eulen das tun, nehmt die Geschicklichkeit der Säblerkatze an und macht Euch den mächtigen Geist des Wolfs zu Eigen.' WHERE `locales_npc_text`.`ID` = 4893;
UPDATE `locales_npc_text` SET `ID` = 4913,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr seid kein Druide!' WHERE `locales_npc_text`.`ID` = 4913;
UPDATE `locales_npc_text` SET `ID` = 4914,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Möchtet Ihr vielleicht nach Rut''theran fliegen, mit freundlicher Unterstützung des Zirkels des Cenarius?' WHERE `locales_npc_text`.`ID` = 4914;
UPDATE `locales_npc_text` SET `ID` = 4915,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wenn Ihr nach Donnerfels zurückfliegen wollt, dann solltet Ihr mit Bunthen Steppenwind reden, meinem Taurenkollegen.' WHERE `locales_npc_text`.`ID` = 4915;
UPDATE `locales_npc_text` SET `ID` = 4916,`Text0_0_loc3` = 'Ihr seid kein Druide!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4916;
UPDATE `locales_npc_text` SET `ID` = 4917,`Text0_0_loc3` = 'Wenn Ihr nach Rut''theran zurückfliegen wollt, dann solltet Ihr mit Silva Filnaveth reden, meinem Gegenstück auf Seiten der Nachtelfen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4917;
UPDATE `locales_npc_text` SET `ID` = 4918,`Text0_0_loc3` = 'Möchtet Ihr vielleicht nach Donnerfels fliegen, mit freundlicher Unterstützung des Zirkels des Cenarius?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4918;
UPDATE `locales_npc_text` SET `ID` = 4933,`Text0_0_loc3` = 'Die Zeiten, in denen wir leben, sind so wechselhaft wie die Winde, $C. Es ist wichtig, dass wir nicht vergessen, was in dieser Welt wahrhaftig und von Dauer ist, und dass wir das bewahren. Alles andere ist nur verlorene Zeit.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4933;
UPDATE `locales_npc_text` SET `ID` = 4934,`Text0_0_loc3` = 'Kein anderes Volk von Azeroth hat so viel gelitten wie das Unsrige, $C. Dem Tod ins Gesicht zu lachen, ist für uns alle zur zweiten Natur geworden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4934;
UPDATE `locales_npc_text` SET `ID` = 4935,`Text0_0_loc3` = 'Seid gegrüßt, $N, und willkommen im Camp Narache. Die Kunde Eures Kommens hat sich wie ein Lauffeuer im Stamm verbreitet.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4935;
UPDATE `locales_npc_text` SET `ID` = 4936,`Text0_0_loc3` = 'Ah, die Schönheit des Laubschattentals erfreut meine Sinne immer wieder!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4936;
UPDATE `locales_npc_text` SET `ID` = 4937,`Text0_0_loc3` = 'Hmmm, Ihr seht mir recht handfest aus. Vielleicht könnt Ihr mir bei ein, zwei Dingen zur Hand gehen. Außer ein paar Lehrlingen, die noch grün hinter den Ohren sind und anderes zu tun haben, gibt es hier nicht viel Hilfe.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4937;
UPDATE `locales_npc_text` SET `ID` = 4938,`Text0_0_loc3` = 'He, Bürger! Ihr seht recht kräftig aus. Wir Wachen sind hier eher spärlich gesät. Daher könnte ich Eure Hilfe gut gebrauchen...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4938;
UPDATE `locales_npc_text` SET `ID` = 4953,`Text0_0_loc3` = 'Da Ihr mir geholfen habt, sage ich Euch nun, was ich über dieses Land und die Dinge, die es hier zu sehen gibt, weiß. Mein Wissen ist nicht groß, doch vielleicht kann ich einige Dinge aufzeigen, die jemanden wie Euch möglicherweise interessieren könnten.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4953;
UPDATE `locales_npc_text` SET `ID` = 4954,`Text0_0_loc3` = 'Mein Wissen ob die Lehren der Nachtelfen ist zwar nur begrenzt, aber es heißt, dass vor langer Zeit - damals, als den Nachtelfen die Kunst des Wirkens von Arkanzaubern noch heilig war - dass jedenfalls damals Azshara die Größte von ihnen allen war. Sie war die Königin und eine wunderschöne Frau. Doch leider sollte sich ihre Sucht nach dem Arkanen eines Tages als Katalysator für die schlimmsten Ereignisse in der Geschichte dieser Welt erweisen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4954;
UPDATE `locales_npc_text` SET `ID` = 4955,`Text0_0_loc3` = 'Einst war das eine große Stadt der Nachtelfen, in der die Hochgeborenen regierten und mächtige Arkanmagie ausübten. Doch nun sind sie verflucht, spuken im ganzen Land, und ihre zerstörte Stadt wurde von den Grollflossennaga überrannt. Ich meine gehört zu haben, dass die Naga in den Ruinen nach mächtigen Artefakten der Hochgeborenen suchen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4955;
UPDATE `locales_npc_text` SET `ID` = 4956,`Text0_0_loc3` = 'Es heißt, im Norden lebe ein mächtiger Magier, ein Mensch, der aus irgendeinem unbekannten Grund Wache über das Land hält. Ich bin selbst noch nicht dorthin gereist, aber es heißt, dass Gäste dort jederzeit willkommen seien und sogar seine Gunst gewinnen könnten, wenn sie bereit sind, Aufträge für ihn zu erledigen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4956;
UPDATE `locales_npc_text` SET `ID` = 4957,`Text0_0_loc3` = 'In den letzten Jahren habe ich mit angesehen, wie den Furbolgs schreckliche Dinge widerfahren sind - sie waren Opfer in vielerlei Hinsicht. Doch was aus diesem Stamm geworden ist, weiß ich nicht so recht. Wurden sie von derselben Seuche verderbt, die auch über den Teufelswald gekommen ist? Oder sind sie vielleicht einfach wütend darüber, was ihrem einst friedlichen Volk widerfahren ist, und versuchen sich lediglich zu verteidigen? Wie dem auch sei, die meisten von ihnen geben sich allen, die in ihr Gebiet eindringen, gegenüber feindlich.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4957;
UPDATE `locales_npc_text` SET `ID` = 4958,`Text0_0_loc3` = 'Die blauen Großdrachen selbst sind nicht hier, aber Anhänger von Malygos halten sich hier auf. Ich weiß nicht warum, aber eines kann ich Euch sagen: Überall, wo die blauen Großdrachen sind, könnt Ihr mit Arkanmagie rechnen. Ich wäre nicht überrascht, wenn die Drachenbrut hier irgendetwas beschützt... vielleicht etwas, das die Hochgeborenen zurückgelassen haben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4958;
UPDATE `locales_npc_text` SET `ID` = 4959,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich sollte mir einen Gehilfen suchen; ich mag es gar nicht, den ganzen Tag nur hier zu sitzen und Angelbedarf zu verkaufen. Ich würde viel lieber angeln gehen und anschließend Angelbedarf verkaufen.' WHERE `locales_npc_text`.`ID` = 4959;
UPDATE `locales_npc_text` SET `ID` = 4960,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Throm-ka, $C. Ich habe heute schlechte Laune, also lasst mich bitte allein, es sei denn, Ihr könnt mir helfen.' WHERE `locales_npc_text`.`ID` = 4960;
UPDATE `locales_npc_text` SET `ID` = 4973,`Text0_0_loc3` = 'Ich kann Euch nicht ausbilden, $C.',`Text0_1_loc3` = 'Ich kann Euch nicht ausbilden, $C.' WHERE `locales_npc_text`.`ID` = 4973;
UPDATE `locales_npc_text` SET `ID` = 4974,`Text0_0_loc3` = 'Mein Name ist Ulysses Rackmore, ich bin Kapitän der GALLANT. Und dies ist das Logbuch, in dem die letzten Tage meines dem Untergang geweihten Schiffes verzeichnet sind.$B$BWir waren mit einer Lieferung Vorräte unterwegs zur Mondfederfeste, als Naga die Seiten der GALLANT angriffen. Wir konnten den Angriff abwehren, aber wir wussten, dass sie zurückkommen würden. Da ich den Verlust meiner wertvollen Besitztümer fürchtete, segelte ich die GALLANT zu einer Insel vor der Küste von Desolace - einer Insel, auf der Ruinen von alten Gebäuden der Nachtelfen zu finden sind. Und dort habe ich meine Schatztruhe zurückgelassen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4974;
UPDATE `locales_npc_text` SET `ID` = 4975,`Text0_0_loc3` = 'Meine Truhe ist eine geniale Konstruktion: Man benötigt zwei Schlüssel, um sie zu öffnen, einen aus Gold und einen aus Silber. Den ersten Schlüssel, den aus Silber, gab ich meinem ersten Maat; den goldenen Schlüssel behielt ich selbst.$B$BDoch wir waren dazu verdammt, beide zu verlieren. Ich verlor meinen Schlüssel während des nächsten Nagaangriffs, und der meines ersten Maats ging am nächsten Tag verloren, als wir von Kaltschnappmakruras angegriffen wurden und diese ihn mit sich in die Tiefe zogen.$B$BIch bin sicher, dass die Naga und Makruras die Schlüssel haben. Ich bin nur froh, dass die beiden Völker verfeindet sind und deswegen die Schlüssel nicht gemeinsam verwenden werden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4975;
UPDATE `locales_npc_text` SET `ID` = 4976,`Text0_0_loc3` = 'Nach dem Angriff der Makruras wussten wir, dass die GALLANT keinem weiteren Angriff standhalten würde. Und so gaben die wenigen überlebenden Matrosen und ich mein Schiff auf.$B$BWir befinden uns jetzt am Strand und werden bald in Richtung Stadt Nijelspitze aufbrechen, doch wir wissen, dass große Gefahren zwischen uns und dieser Zuflucht liegen, und ich fürchte, dass Desolace uns verschlingen wird.$B$BIch schreibe dies jetzt in der Hoffnung, dass jemand von unserer Not erfährt und eines Tages Rache an diesen Scheusalen aus dem Meer nehmen wird, die unseren Untergang bedeuteten.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4976;
UPDATE `locales_npc_text` SET `ID` = 4977,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Der Schrein von Remulos ist für uns vom Zirkel des Cenarius hier in der Mondlichtung ein hochheiliger Ort - also benehmt Euch bitte auf eine Art und Weise, die einem Besucher angemessen ist.$B$BEinem Besucher, der nicht meinen Krummsäbel im Rücken spüren will, versteht sich.' WHERE `locales_npc_text`.`ID` = 4977;
UPDATE `locales_npc_text` SET `ID` = 4978,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Möge Cenarius über Euch wachen, $GBruder:Schwester;.$B$BMein Name ist Tajarri und ich habe geschworen, diesen Schrein zu beschützen. Es ist mir eine Ehre, Bewahrer Remulos als Aufseher in diesem hochheiligen Land zu dienen.' WHERE `locales_npc_text`.`ID` = 4978;
UPDATE `locales_npc_text` SET `ID` = 4979,`Text0_0_loc3` = 'Bevor die Geißel kam, machte ich gute Geschäfte. Ich habe alles an- und verkauft. Einfach alles!$B$BUnd ich hatte geradezu unglaublich irre Preise!$B$BDa könnt Ihr jeden fragen. Jeden, der nicht tot ist, jedenfalls.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4979;
UPDATE `locales_npc_text` SET `ID` = 4980,`Text0_0_loc3` = 'Hallo, $N! Seid Ihr hier, um etwas zu kaufen? Oder vielleicht, um etwas zu verkaufen?$B$BBessere Preise findet Ihr nirgendwo oder ich will nicht Augustus der Meschuggene heißen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4980;
UPDATE `locales_npc_text` SET `ID` = 4982,`Text0_0_loc3` = 'Das hier ist ein gefährlicher Ort, $N. Seid lieber vorsichtig.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4982;
UPDATE `locales_npc_text` SET `ID` = 4984,`Text0_0_loc3` = 'Vergeudet meine Zeit nicht, $C.',`Text0_1_loc3` = 'Vergeudet meine Zeit nicht, $C.' WHERE `locales_npc_text`.`ID` = 4984;
UPDATE `locales_npc_text` SET `ID` = 4985,`Text0_0_loc3` = 'Solche wie Euch bilde ich nicht aus, $C. Sucht Euch einen anderen Lehrer, ehe ich wütend werde.',`Text0_1_loc3` = 'Solche wie Euch bilde ich nicht aus, $C. Sucht Euch einen anderen Lehrer, ehe ich wütend werde.' WHERE `locales_npc_text`.`ID` = 4985;
UPDATE `locales_npc_text` SET `ID` = 4986,`Text0_0_loc3` = 'Immer mit der Ruhe, Mann. Ich bild bloß Kriegers aus.',`Text0_1_loc3` = 'Immer mit der Ruhe, Mann. Ich bild bloß Kriegers aus.' WHERE `locales_npc_text`.`ID` = 4986;
UPDATE `locales_npc_text` SET `ID` = 4987,`Text0_0_loc3` = 'Was kann ich''n für dich tun, Mann?',`Text0_1_loc3` = 'Was kann ich''n für dich tun, Mann?' WHERE `locales_npc_text`.`ID` = 4987;
UPDATE `locales_npc_text` SET `ID` = 4988,`Text0_0_loc3` = 'Aufgrund einer akuten Inkompatibilität Eurer Klassenspezifikationen bin ich leider nicht in der Lage, auf welche Weise auch immer zur Ausmerzung eines eventuellen Defizits im Bereich Eurer Fähigkeiten beizutragen.',`Text0_1_loc3` = 'Aufgrund einer akuten Inkompatibilität Eurer Klassenspezifikationen bin ich leider nicht in der Lage, auf welche Weise auch immer zur Ausmerzung eines eventuellen Defizits im Bereich Eurer Fähigkeiten beizutragen.' WHERE `locales_npc_text`.`ID` = 4988;
UPDATE `locales_npc_text` SET `ID` = 4989,`Text0_0_loc3` = 'Exzellent! Ihr erfüllt die geforderten Parameter und Grundvoraussetzungen! Was kann ich für Euch tun?',`Text0_1_loc3` = 'Exzellent! Ihr erfüllt die geforderten Parameter und Grundvoraussetzungen! Was kann ich für Euch tun?' WHERE `locales_npc_text`.`ID` = 4989;
UPDATE `locales_npc_text` SET `ID` = 4990,`Text0_0_loc3` = 'Hegnar Schlingerschuss, zu Euren Diensten!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4990;
UPDATE `locales_npc_text` SET `ID` = 4991,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4991;
