UPDATE `locales_npc_text` SET `ID` = 4993,`Text0_0_loc3` = 'Euresgleichen kann ich nicht ausbilden, $C.',`Text0_1_loc3` = 'Euresgleichen kann ich nicht ausbilden, $C.' WHERE `locales_npc_text`.`ID` = 4993;
UPDATE `locales_npc_text` SET `ID` = 4994,`Text0_0_loc3` = 'Was kann ich für Euch tun, mein Kind?',`Text0_1_loc3` = 'Was kann ich für Euch tun, mein Kind?' WHERE `locales_npc_text`.`ID` = 4994;
UPDATE `locales_npc_text` SET `ID` = 4995,`Text0_0_loc3` = 'Einst herrschten der Feuerlord und seine Brüder über die ganze Welt, sterbliches Wesen. Er war ein Diener der Alten Götter und am Kampf gegen die Titanen um die Herrschaft über diese Welt beteiligt. Die siegreichen Titanen verbannten meinen Meister und seine Brüder auf die Ebene der Elementare, auf der sie bis zum Ende aller Zeiten verbleiben sollten.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4995;
UPDATE `locales_npc_text` SET `ID` = 4996,`Text0_0_loc3` = 'Ich kann euch nichts lehren, Jungchen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 4996;
UPDATE `locales_npc_text` SET `ID` = 4997,`Text0_0_loc3` = 'Was zieht Ihr vor, Schusswaffen oder Bögen?',`Text0_1_loc3` = 'Was zieht Ihr vor, Schusswaffen oder Bögen?' WHERE `locales_npc_text`.`ID` = 4997;
UPDATE `locales_npc_text` SET `ID` = 4998,`Text0_0_loc3` = 'Ich verfüge nicht über das Wissen, das Ihr sucht.',`Text0_1_loc3` = 'Ich verfüge nicht über das Wissen, das Ihr sucht.' WHERE `locales_npc_text`.`ID` = 4998;
