UPDATE `locales_npc_text` SET `ID` = 5000,`Text0_0_loc3` = 'Bei der Ausbildung kann ich Euch nich'' helfen, $GJungchen:Mädchen;. $GEin:Eine:c; $C, das wär die richtige Wahl, wenn Ihr ''n Lehrer sucht.',`Text0_1_loc3` = 'Bei der Ausbildung kann ich Euch nich'' helfen, $GJungchen:Mädchen;. $GEin:Eine:c; $C, das wär die richtige Wahl, wenn Ihr ''n Lehrer sucht.' WHERE `locales_npc_text`.`ID` = 5000;
UPDATE `locales_npc_text` SET `ID` = 5001,`Text0_0_loc3` = 'Wie?',`Text0_1_loc3` = 'Wie?' WHERE `locales_npc_text`.`ID` = 5001;
UPDATE `locales_npc_text` SET `ID` = 5002,`Text0_0_loc3` = 'Vergeudet meine Zeit nicht.',`Text0_1_loc3` = 'Vergeudet meine Zeit nicht.' WHERE `locales_npc_text`.`ID` = 5002;
UPDATE `locales_npc_text` SET `ID` = 5003,`Text0_0_loc3` = 'Ich kann Euch nicht ausbilden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5003;
UPDATE `locales_npc_text` SET `ID` = 5004,`Text0_0_loc3` = 'He, Maan, du bist $C? Ja dann musst du einen von euren Lehrern finden, Maan.',`Text0_1_loc3` = 'He, Maan, du bist $C? Ja dann musst du einen von euren Lehrern finden, Maan.' WHERE `locales_npc_text`.`ID` = 5004;
UPDATE `locales_npc_text` SET `ID` = 5005,`Text0_0_loc3` = 'Die Geister haben Euch zu mir geführt. Wie kann ich Euch zu Diensten sein?',`Text0_1_loc3` = 'Die Geister haben Euch zu mir geführt. Wie kann ich Euch zu Diensten sein?' WHERE `locales_npc_text`.`ID` = 5005;
UPDATE `locales_npc_text` SET `ID` = 5006,`Text0_0_loc3` = 'Mögen die Geister mit Euch sein.',`Text0_1_loc3` = 'Mögen die Geister mit Euch sein.' WHERE `locales_npc_text`.`ID` = 5006;
UPDATE `locales_npc_text` SET `ID` = 5007,`Text0_0_loc3` = 'Du hast ''nen starken Geist, Mann. Was kann dieser Troll für dich tun?',`Text0_1_loc3` = 'Du hast ''nen starken Geist, Mann. Was kann dieser Troll für dich tun?' WHERE `locales_npc_text`.`ID` = 5007;
UPDATE `locales_npc_text` SET `ID` = 5008,`Text0_0_loc3` = 'Dem Geistern soll''n Euch beschütz''n, Mann.',`Text0_1_loc3` = 'Dem Geistern soll''n Euch beschütz''n, Mann.' WHERE `locales_npc_text`.`ID` = 5008;
UPDATE `locales_npc_text` SET `ID` = 5009,`Text0_0_loc3` = 'Wo wollt Ihr hin? Egal - wenn Ihr schnell dorthin kommen wollt, dann sucht nicht weiter!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5009;
UPDATE `locales_npc_text` SET `ID` = 5010,`Text0_0_loc3` = 'Ich versuche, die Späherkuppe so gut wie möglich versorgt zu halten, aber das geht nicht immer, so oft, wie wir im Einsatz sind.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5010;
UPDATE `locales_npc_text` SET `ID` = 5011,`Text0_0_loc3` = 'Vor gerade mal dreihundert Jahren beschwor ein leichtsinniger Zwerg namens Thaurissan den Meister aus seinem feurigen Reich. Seine Rückkehr in diese jämmerliche Welt verwüstete das umgebende Land und erschuf diesen vulkanischen Kern. Seit jener Zeit schläft der mächtige Ragnaros unter seinem Berg.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5011;
UPDATE `locales_npc_text` SET `ID` = 5012,`Text0_0_loc3` = 'Wir, seine Diener, die wir ihm die Treue geschworen haben, tun, was er uns befiehlt, und setzen seine eiserne Herrschaft durch. Nun, da er von den Energien seines feurigen Reiches abgeschnitten wurde, ist Ragnaros nur noch ein Schatten seines wahren Selbst. Doch es steckt noch mehr als genug Kraft in ihm, um Euch den Garaus zu machen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5012;
UPDATE `locales_npc_text` SET `ID` = 5013,`Text0_0_loc3` = 'Guten Tag, $Gder Herr:gnädige Frau;. Ich würde Euch ja die Hand geben, aber wie Ihr sehen könnt, bin ich gerade mitten in was drin. Kann ich Euch behilflich sein?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5013;
UPDATE `locales_npc_text` SET `ID` = 5014,`Text0_0_loc3` = 'Für den Anfang macht Ihr Euch zwar gut, aber Ihr müsst noch sehr viel lernen, $N.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5014;
UPDATE `locales_npc_text` SET `ID` = 5015,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5015;
UPDATE `locales_npc_text` SET `ID` = 5016,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich wünsche mir nichts sehnlicher, als an den Kämpfen teilnehmen zu können, die vor den Stadtmauern toben. Doch die Pflicht zwingt mich, mein Wissen mit all jenen zu teilen, die an meiner Stelle kämpfen, und so bleibe ich in diesem kleinen Haus hier eingesperrt... vorerst.' WHERE `locales_npc_text`.`ID` = 5016;
UPDATE `locales_npc_text` SET `ID` = 5019,`Text0_0_loc3` = 'Ishnu-alah, $GBruder:Schwester;. Bringt Ihr Neuigkeiten aus fernen Landen? Mein Herz sehnt sich nach Nachrichten aus Teldrassil.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5019;
UPDATE `locales_npc_text` SET `ID` = 5020,`Text0_0_loc3` = 'Es ist mir eine Ehre, mein Wissen mit Euch zu teilen, $N. Setzt es weise ein.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5020;
UPDATE `locales_npc_text` SET `ID` = 5021,`Text0_0_loc3` = 'Ihr müsst Euch einen erfahreneren Lehrer suchen, denn meine Fähigkeiten habt Ihr bereits überschritten. Ich rate Euch, sprecht mit Lilyssia Nachtluft ganz hier in der Nähe - sie verfügt über großes Wissen, das sie Euch vermitteln kann.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5021;
UPDATE `locales_npc_text` SET `ID` = 5022,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die Druiden sagen, dass es nur eine Frage der Zeit sei, ehe die Natur die Horde aus Kalimdor vertreibt. Und dennoch verbreitet sich die Verderbnis immer weiter über unser Land, sogar hier auf Teldrassil.' WHERE `locales_npc_text`.`ID` = 5022;
UPDATE `locales_npc_text` SET `ID` = 5023,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Gut gemacht! Ihr lernt immer mehr über die Wirkweise von Tränken.' WHERE `locales_npc_text`.`ID` = 5023;
UPDATE `locales_npc_text` SET `ID` = 5024,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5024;
UPDATE `locales_npc_text` SET `ID` = 5025,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Elune-adore, $Gmein Bruder:meine Schwester;. Ihr seid hier willkommen.' WHERE `locales_npc_text`.`ID` = 5025;
UPDATE `locales_npc_text` SET `ID` = 5026,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ist es nicht erstaunlich, was man aus den Pflanzen des Waldes alles herstellen kann? Elune hat uns wahrlich mit ihrer Freigiebigkeit gesegnet.' WHERE `locales_npc_text`.`ID` = 5026;
UPDATE `locales_npc_text` SET `ID` = 5027,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Es ist nett von Euch, mich aufzusuchen, aber Eure Fertigkeiten übertreffen die meinen. Sprecht doch einfach mit Sylvanna Mondwald dort drüben. Sie sucht noch nach einem weiteren Schüler.' WHERE `locales_npc_text`.`ID` = 5027;
UPDATE `locales_npc_text` SET `ID` = 5028,`Text0_0_loc3` = 'Hat Euch etwa meine herzallerliebste Kali geschickt? Ich liebe die Kleine einfach über alles.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5028;
UPDATE `locales_npc_text` SET `ID` = 5029,`Text0_0_loc3` = 'Ich nehme nicht an, dass Ihr hier seid, weil Ihr über meine süße Kali reden wollt, oder? Na gut. Dann reden wir halt über Tränke. Hat Euch schon mal jemand gesagt, dass Ihr zu eingleisig denkt?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5029;
UPDATE `locales_npc_text` SET `ID` = 5030,`Text0_0_loc3` = 'Ihr seid über meine Fähigkeiten hinausgewachsen, $N. Geht und belästigt Tally Brausefitz - das ist eine Gnomin, die in der Tüftlerstadt von Eisenschmiede lebt. Ach, und wenn Ihr eh dorthin kommt, dann richtet Kali liebe Grüße von mir aus.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5030;
UPDATE `locales_npc_text` SET `ID` = 5031,`Text0_0_loc3` = 'Lasst Euch von diesen großen, unschuldigen Augen bloß nich'' täuschen. Tally is'' ''ne echte Expertin, gar keine Frage. Ich lern jeden Tag was Neues. Was glaubt''n Ihr, warum ich wohl sonst hier in der Tüftlerstadt bin? Seh ich etwa wie ''n Ingenieur aus? Na eben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5031;
UPDATE `locales_npc_text` SET `ID` = 5032,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5032;
UPDATE `locales_npc_text` SET `ID` = 5033,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5033;
UPDATE `locales_npc_text` SET `ID` = 5034,`Text0_0_loc3` = 'Ihr seht aus wie jemand, der auf Abenteuer aus is''. Also ich hatt davon schon genug, als wir Trolle hierher kamen. Jetzt nehm ich jeden Tag, wie er kommt, und bilde andere Leute darin aus, mit den Abenteuern fertig zu werden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5034;
UPDATE `locales_npc_text` SET `ID` = 5035,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5035;
UPDATE `locales_npc_text` SET `ID` = 5036,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5036;
UPDATE `locales_npc_text` SET `ID` = 5037,`Text0_0_loc3` = 'In den Flaschen da auf dem Tisch sind lauter Tränke, die ich heute gebraut habe. Und da heißt es, ein Topf, den man beobachtet, fängt nie an zu kochen! Ha!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5037;
UPDATE `locales_npc_text` SET `ID` = 5038,`Text0_0_loc3` = 'Die haben gesagt, ich wär doof, aber ich werd''s ihnen zeigen! Ich werd jeden Topf beobachten, den ich hab, und denen beweisen, dass man trotzdem Sachen da drin kochen kann! Ja, ich hab grad ''ne Glückssträhne! Also, wer is'' jetzt doof, hä? Hä?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5038;
UPDATE `locales_npc_text` SET `ID` = 5039,`Text0_0_loc3` = 'Da kann ich Euch nicht helfen. Ihr wisst mehr über Tränke als ich. Und außerdem muss ich auf die Töpfe Acht geben. Redet mit Yelmak da drüben. Der weiß viel über Tränke.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5039;
UPDATE `locales_npc_text` SET `ID` = 5040,`Text0_0_loc3` = 'Bena arbeitet, als wäre sie besessen. Tag und Nacht sitzt sie über diesen Tränken und manchmal vergisst sie darüber sogar, zu schlafen oder zu essen. Ich weiß ja, dass unsere untoten Verbündeten Hilfe brauchen, aber ich fürchte langsam, sie fügt sich damit selbst Schaden zu.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5040;
UPDATE `locales_npc_text` SET `ID` = 5041,`Text0_0_loc3` = 'Ich muss Hunderte von Phiolen zerbrochen haben, als ich damals meine ersten Tränke braute. In meinen groben Händen war das Glas irgendwie so zerbrechlich... Ich hoffe, Ihr werdet vorsichtiger sein, als ich es war.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5041;
UPDATE `locales_npc_text` SET `ID` = 5042,`Text0_0_loc3` = 'Eure Lernfähigkeit ist größer als die meine. Jetzt müsst Ihr mit Bena Winterhuf dort drüben sprechen, die Euch weiter unterrichten kann. Aber bitte, nehmt nicht zu viel von ihrer Zeit in Anspruch. Sie braucht ihre Ruhe.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5042;
UPDATE `locales_npc_text` SET `ID` = 5043,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Schön, mal ein freundliches Gesicht zu sehen. Apotheker Johaan redet nicht viel mit mir, es sei denn, er will etwas. Er ist einfach schon zu lange aus dem Grab raus. Irgendwann werd ich auch so sein, hab ich gehört. Schaurig, das. Aber vielleicht ist es mir dann ja egal.' WHERE `locales_npc_text`.`ID` = 5043;
UPDATE `locales_npc_text` SET `ID` = 5044,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Seid Ihr hier, weil Ihr was über Tränke lernen wollt? Ich kann Euch viel beibringen. Ich bin vielleicht tot, aber mein Gehirn hab ich nicht verloren. Noch nicht.' WHERE `locales_npc_text`.`ID` = 5044;
UPDATE `locales_npc_text` SET `ID` = 5045,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5045;
UPDATE `locales_npc_text` SET `ID` = 5046,`Text0_0_loc3` = 'Ja, es stimmt, einst war ich ein Mensch. Was sonst vielleicht ein Nachteil wäre, gereicht uns jetzt aber zum Vorteil, $N, denn ich kenne die menschlichen Schwächen genauestens.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5046;
UPDATE `locales_npc_text` SET `ID` = 5047,`Text0_0_loc3` = 'Wohlan denn. Schneidet von einem dieser Exemplare noch einen Klumpen Fleisch ab. Prüfen wir einmal, was Ihr bisher gelernt habt. Das kann dann als Grundlage für das weitere Vorgehen dienen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5047;
UPDATE `locales_npc_text` SET `ID` = 5048,`Text0_0_loc3` = 'Euer Wissen um Tränke hat das meine überflügelt, aber Ihr müsst noch viel lernen. Geht die Stufen im Südosten hinunter und sucht Doktor Marsh auf. Er wird Euch lehren, was wahres Leiden - ähem. Er ist ein Verlassener, der Euch weiter ausbilden wird.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5048;
UPDATE `locales_npc_text` SET `ID` = 5049,`Text0_0_loc3` = 'Hört auf, mich so anzusehen. Ich bin vielleicht kein Abenteurer wie Ihr, aber das heißt noch lange nicht, dass ich nicht auch ein paar Muskeln haben kann. Dieses ganze Umgerühre macht einen ganz schön stark.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5049;
UPDATE `locales_npc_text` SET `ID` = 5050,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5050;
UPDATE `locales_npc_text` SET `ID` = 5051,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5051;
UPDATE `locales_npc_text` SET `ID` = 5053,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5053;
UPDATE `locales_npc_text` SET `ID` = 5054,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Lasst Euch von den Knopfaugen und dem Rauschebart bloß nicht täuschen. Vosur ist ein begabter Lehrling und ein guter Freund, auch wenn ich immer einen steifen Nacken bekomme, wenn ich mit ihm rede.' WHERE `locales_npc_text`.`ID` = 5054;
UPDATE `locales_npc_text` SET `ID` = 5055,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ähm, Ihr wisst bereits alles, was ich Euch beibringen könnte. Vielleicht ist es an der Zeit, dass Ihr mit jemand anderem sprecht. Versucht es mal bei Ainethil, einer Nachtelfin, die in Darnassus auf der Terrasse der Handwerker lebt. Die Gute hat echtes Talent!' WHERE `locales_npc_text`.`ID` = 5055;
UPDATE `locales_npc_text` SET `ID` = 5057,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5057;
UPDATE `locales_npc_text` SET `ID` = 5058,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5058;
UPDATE `locales_npc_text` SET `ID` = 5059,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ishnu-dal-dieb, $N. Seid Ihr hier, weil Ihr nach Wissen strebt?' WHERE `locales_npc_text`.`ID` = 5059;
UPDATE `locales_npc_text` SET `ID` = 5060,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5060;
UPDATE `locales_npc_text` SET `ID` = 5063,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5063;
UPDATE `locales_npc_text` SET `ID` = 5064,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Selbst die kleinsten Veränderungen bei den Zutaten eines Trankes können diesen zutiefst verändern. Geduld und Genauigkeit, $Gmein Junge:mein Kind;, sind Dinge, die Euch in jeder Lebenslage von Nutzen sein werden.' WHERE `locales_npc_text`.`ID` = 5064;
UPDATE `locales_npc_text` SET `ID` = 5065,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Meine Fertigkeit im Herstellen von Tränken ist der Euren unterlegen. Ihr solltet lieber mit Ainethil dort drüben sprechen. Sie wird sehen, dass Ihr bereit für weiterführende Lektionen seid.' WHERE `locales_npc_text`.`ID` = 5065;
UPDATE `locales_npc_text` SET `ID` = 5067,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ach, wieder jung zu sein und zum ersten Mal den Zauber der Tränke zu entdecken... Vielen Dank, dass Ihr mich an all das erinnert habt, $N. Und nun geht und teilt Eure jugendliche Begeisterung mit Milla Fairancora dort drüben.' WHERE `locales_npc_text`.`ID` = 5067;
UPDATE `locales_npc_text` SET `ID` = 5068,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich habe in all den Jahren viel über Tränke gelernt, $N, und all dieses Wissen steht Euch zur Verfügung. Ihr müsst nur fragen.' WHERE `locales_npc_text`.`ID` = 5068;
UPDATE `locales_npc_text` SET `ID` = 5069,`Text0_0_loc3` = 'Ich bin beschäftigt, $N. Sagt Whuut, er soll sich darum kümmern, ganz egal, was es ist. Der Kleine hat schon den ganzen Tag im eigenen Saft geschmort. Wenn das noch viel länger so weiter geht, dann wird er noch irgendwem wehtun.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5069;
UPDATE `locales_npc_text` SET `ID` = 5070,`Text0_0_loc3` = 'Es ist mir eine Ehre, Euch kennen zu lernen, $N, aber Eure Fertigkeiten im Tränkebrauen sind größer als die meinen. Sucht Doktor Herbert Halsey auf, das ist ein Untoter, den Ihr im Apothekarium von Unterstadt findet. Er kann Euch von jetzt an weiterhelfen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5070;
UPDATE `locales_npc_text` SET `ID` = 5072,`Text0_0_loc3` = 'Da habt Ihr den falschen Orc erwischt. Anfänger bilde ich nicht aus. Redet mit dem jungen Whuut da drüben... aber verwendet keine zu komplizierten Wörter.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5072;
UPDATE `locales_npc_text` SET `ID` = 5073,`Text0_0_loc3` = 'Noch $Gein Schüler:eine Schülerin;, wie? Na gut. Mal sehen, ob ich Euch helfen kann.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5073;
UPDATE `locales_npc_text` SET `ID` = 5074,`Text0_0_loc3` = 'Glyx und ich haben eine ungeschriebene Übereinkunft: Er beschafft mir meine Vorräte, und ich mache ihn reich. Doch, ernsthaft! Was dieser Halsabschneider mir für so einen jämmerlichen Beutel zerkrümeltes Königsblut abknöpft - das treibt selbst einem erwachsenen Goblin das Wasser in die Augen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5074;
UPDATE `locales_npc_text` SET `ID` = 5075,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5075;
UPDATE `locales_npc_text` SET `ID` = 5078,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5078;
UPDATE `locales_npc_text` SET `ID` = 5079,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Als ich die Untoten zum ersten Mal sah, musste ich weinen, denn ich konnte mir das Grauen und die Einsamkeit, denen sie ausgesetzt sind, kaum vorstellen. Aber dann sprach die Erdenmutter zu mir, ihre Stimme sang in meinem Blut. Und sie sprach: "Rette sie."' WHERE `locales_npc_text`.`ID` = 5079;
UPDATE `locales_npc_text` SET `ID` = 5080,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Könnt Ihr nicht spüren, welchen Schmerz die Erdenmutter ob ihrer verlorenen Kinder, der Untoten, empfindet? Sagt schnell, was Ihr zu sagen habt, $N. Jede Sekunde kommt mich teuer zu stehen. Wir müssen ihnen helfen, ihren Weg zurück zu ihr zu finden!' WHERE `locales_npc_text`.`ID` = 5080;
UPDATE `locales_npc_text` SET `ID` = 5081,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich kann es mit meinem Gewissen nicht vereinbaren, Zeit mit der Ausbildung von Anfängern zu verbringen, solange unsere Verbündeten, die Untoten, so schrecklich leiden müssen. Bitte sprecht stattdessen mit Kray dort drüben - er kann Euch vorläufig weiterhelfen.' WHERE `locales_npc_text`.`ID` = 5081;
UPDATE `locales_npc_text` SET `ID` = 5083,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Eure Fertigkeiten im Tränkebrauen übertreffen die meinen. Sucht Doktor Herbert Halsey auf; das ist ein Untoter, den Ihr im Apothekarium von Unterstadt findet. Er verfügt über das Wissen, um Euch weiter auszubilden, und außerdem könnt Ihr dort aus erster Hand mit ansehen, welches Leid seinem Volk widerfährt.' WHERE `locales_npc_text`.`ID` = 5083;
UPDATE `locales_npc_text` SET `ID` = 5084,`Text0_0_loc3` = 'Noch eine Störung. Wie soll ich denn so bitte schön arbeiten? Nein, darauf müsst Ihr nicht antworten - sehe ich etwa so aus, als ob mich interessieren würde, was Ihr zu sagen habt? Warum geht Ihr nicht einfach und nervt Herrn Ich-bin-ja-ach-so-wichtig stattdessen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5084;
UPDATE `locales_npc_text` SET `ID` = 5085,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5085;
UPDATE `locales_npc_text` SET `ID` = 5087,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5087;
UPDATE `locales_npc_text` SET `ID` = 5088,`Text0_0_loc3` = 'Noch ein Schüler. Na toll. Deswegen gehört Lydon auch zur Königlichen Snobvereinigung und kriegt die Forschungsaufträge, während ich mich immer und immer wieder als Lehrer betätigen darf. Wenn ich noch einen einzigen weiteren Preis für gutes Lehren gewinne, dann komm ich niemals davon weg.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5088;
UPDATE `locales_npc_text` SET `ID` = 5089,`Text0_0_loc3` = 'Jetzt, wo Algernon mir meine Vorräte liefert, kann ich bequem hier arbeiten und muss nie wieder auch nur ein einziges Kraut suchen gehen. Und wenn ich nie wieder raus in die Natur müsste, wär''s mir nur recht!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5089;
UPDATE `locales_npc_text` SET `ID` = 5090,`Text0_0_loc3` = 'Ich kann Euch kein Wissen mehr vermitteln. Geht durch den Bogengang und dann die Rampe im Südosten hinunter. Sprecht dort mit Doktor Herbert Halsey. Das ist ein Verlassener, der Euch die weiterführenden Techniken erläutern kann.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5090;
UPDATE `locales_npc_text` SET `ID` = 5092,`Text0_0_loc3` = 'Ich finde Eure Dreistigkeit, einfach so zu mir zu kommen und um Ausbildung zu bitten, nicht sonderlich komisch. Geht durch den Bogengang und dann die Rampe im Südosten hinunter. Sprecht dort mit Doktor Martin Felben. Das ist ein Verlassener, der Euch die Grundlagen beibringen kann.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5092;
UPDATE `locales_npc_text` SET `ID` = 5093,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5093;
UPDATE `locales_npc_text` SET `ID` = 5094,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Dieser Trank scheint von Mal zu Mal weniger Schaden zu heilen, wenn ich ihn braue, obwohl ich jedes Mal dieselben Zutaten verwende. Das lässt nur eine Schlussfolgerung zu: Die Pflanzen verändern sich. Aber warum und in was, kann ich nicht sagen.' WHERE `locales_npc_text`.`ID` = 5094;
UPDATE `locales_npc_text` SET `ID` = 5095,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Es ist an der Zeit, dass Ihr einen fortgeschritteneren Lehrer aufsucht. In der Festung Mondfeder in Feralas lebt die Nachtelfin Kylanna Windraunen. Sie ist ein genialer Kopf und kann Euch zweifellos weiter ausbilden.' WHERE `locales_npc_text`.`ID` = 5095;
UPDATE `locales_npc_text` SET `ID` = 5097,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Eure Fertigkeiten im Tränkebrauen müssen erst noch wachsen, ehe ich Euch als $GSchüler:Schülerin; aufnehmen kann. Vielleicht können Euch in der Zwischenzeit ja Sylvanna Mondwald oder Milla Fairancora dort drüben helfen.' WHERE `locales_npc_text`.`ID` = 5097;
UPDATE `locales_npc_text` SET `ID` = 5098,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Eure Ausbildung macht gute Fortschritte. Ihr könnt stolz auf das sein, was Ihr erreicht habt.' WHERE `locales_npc_text`.`ID` = 5098;
UPDATE `locales_npc_text` SET `ID` = 5099,`Text0_0_loc3` = 'Meine Kollegen und ich sammeln die tödlichsten Substanzen, die wir kennen, und testen ihre Auswirkungen an unseren Feinden. Sobald ich alles zusammen habe, was ich brauche, werde ich Seuchen brauen, wie die Welt sie noch nie gesehen hat.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5099;
UPDATE `locales_npc_text` SET `ID` = 5100,`Text0_0_loc3` = 'Unglaublich. Eure Tränke zeugen von einem Wissen, das dem meinen gleichkommt. Ich kann Euch nicht weiter ausbilden. Sprecht lieber mit dem Orc Rogvar - Ihr findet ihn in Steinard in den Sümpfen des Elends. Er ist für seine bahnbrechenden Experimente bekannt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5100;
UPDATE `locales_npc_text` SET `ID` = 5102,`Text0_0_loc3` = 'Ihr seid noch nicht weit genug, um bei mir zu lernen. Sprecht mit Doktor Martin Felben dort drüben, oder geht die Rampe im Südosten hinauf und sucht Doktor Marsch auf. Das sind Verlassene, die Euch das beibringen können, was Ihr braucht.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5102;
UPDATE `locales_npc_text` SET `ID` = 5103,`Text0_0_loc3` = 'Wollt Ihr etwas lernen? Dann konzentriert Euch, $N. Es gilt, viel Stoff durchzunehmen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5103;
UPDATE `locales_npc_text` SET `ID` = 5104,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Einst hieß ich die Dunkelheit willkommen, denn sie war die Schwester der Nacht. Doch nun ist es ständig dunkel, auch bei Tag, und die Wälder seufzen und sehnen sich nach Nahrung. Wenn wir nicht schnell handeln, dann wird sich diese Verseuchung immer weiter ausbreiten und bald die ganze Welt bedecken.' WHERE `locales_npc_text`.`ID` = 5104;
UPDATE `locales_npc_text` SET `ID` = 5105,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5105;
UPDATE `locales_npc_text` SET `ID` = 5106,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Eure Fertigkeiten zeigen, dass Ihr Euch Eurer Kunst nachhaltig verschrieben habt, doch ich möchte erst noch einen weiteren Beweis für Eure Hingabe sehen. Ich werde Euch eine Aufgabe übertragen. Wenn Ihr sie erfüllt, dann bin ich es zufrieden.' WHERE `locales_npc_text`.`ID` = 5106;
UPDATE `locales_npc_text` SET `ID` = 5108,`Text0_0_loc3` = 'Kriegshäuptling Thrall selbst hat mich gebeten, hier heraus zu kommen. Es war mir eine Ehre, seinem Befehl nachzukommen, und es fällt mir auch nicht schwer, hier zu sein. Ich hätte mir keinen besseren Posten als Steinard wünschen können - fast jedes Kraut, das ich vielleicht brauchen könnte, wächst hier draußen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5108;
UPDATE `locales_npc_text` SET `ID` = 5110,`Text0_0_loc3` = 'Hmmm, ja, wie ich sehe, habt Ihr ein gewisses, wenn auch kleines, Talent, was das Tränkebrauen angeht. Ob Ihr würdig seid, $Gmein Schüler:meine Schülerin; zu werden, bleibt jedoch abzuwarten. Ich habe eine Aufgabe für Euch. Beeindruckt mich.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5110;
UPDATE `locales_npc_text` SET `ID` = 5111,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5111;
UPDATE `locales_npc_text` SET `ID` = 5112,`Text0_0_loc3` = 'Nur eine winzigkleine Anpassung... ah, jetzt!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5112;
UPDATE `locales_npc_text` SET `ID` = 5113,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5113;
UPDATE `locales_npc_text` SET `ID` = 5114,`Text0_0_loc3` = 'Ihr wollt also $GIngenieur:Ingenieurin; werden, hm? Tja, da seid Ihr hier genau richtig, bei Muradin!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5114;
UPDATE `locales_npc_text` SET `ID` = 5115,`Text0_0_loc3` = 'Wo ist mein Spinner? Irgendwo hatte ich den doch gerade noch...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5115;
UPDATE `locales_npc_text` SET `ID` = 5116,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5116;
UPDATE `locales_npc_text` SET `ID` = 5117,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5117;
UPDATE `locales_npc_text` SET `ID` = 5118,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich habe gehört, das neue Henweigh 6000 rollt bald vom Band. Ich kann es kaum noch erwarten!' WHERE `locales_npc_text`.`ID` = 5118;
UPDATE `locales_npc_text` SET `ID` = 5119,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5119;
UPDATE `locales_npc_text` SET `ID` = 5120,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich würde Euch liebend gern unterrichten! Meiner Schwester damit zeigen, dass sie nicht die Einzige ist, die was von der Ingenieurskunst versteht...' WHERE `locales_npc_text`.`ID` = 5120;
UPDATE `locales_npc_text` SET `ID` = 5121,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Hallo! Ich heiße Jenna! Und Ihr?' WHERE `locales_npc_text`.`ID` = 5121;
UPDATE `locales_npc_text` SET `ID` = 5122,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5122;
UPDATE `locales_npc_text` SET `ID` = 5123,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr wollt ausgebildet werden? Von mir? Phantastisch! Das Einzige, was noch besser ist, als Kugeln und Sprengkörper herzustellen, ist, dieses Wissen mit jemandem zu teilen! Dann mal los!' WHERE `locales_npc_text`.`ID` = 5123;
UPDATE `locales_npc_text` SET `ID` = 5124,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ah, heute ist ein wunderbarer Tag, um etwas in die Luft zu jagen!' WHERE `locales_npc_text`.`ID` = 5124;
UPDATE `locales_npc_text` SET `ID` = 5125,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr müsst langsam anfangen, Sachen zu lernen, die über meinen Horizont gehen. Redet mit Lilliam Spindelfunks, der vor dem Zelt da drüben steht. Der wird Euch zeigen, wo''s langgeht.' WHERE `locales_npc_text`.`ID` = 5125;
UPDATE `locales_npc_text` SET `ID` = 5126,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Diese Art von Arbeit ist nichts für zaghafte Gemüter. Ein Onkel von mir hat eine Hand dabei verloren! Der arme alte Stummel... Na, wie dem auch sei, wenn Ihr immer noch Interesse habt, dann kann ich Euch vielleicht ein paar Sachen zeigen.' WHERE `locales_npc_text`.`ID` = 5126;
UPDATE `locales_npc_text` SET `ID` = 5127,`Text0_0_loc3` = 'Wie? Wollt Ihr etwas?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5127;
UPDATE `locales_npc_text` SET `ID` = 5128,`Text0_0_loc3` = 'Da kann ich Euch nich'' helfen! Ihr seid schon soweit, bei jemandem zu lernen, der schon länger dabei is'' als ich. Wartet mal... Ihr seid so $Geiner:eine; von der Allianz, also müsst Ihr mit Lilliam Spindelfunks in Sturmwind reden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5128;
UPDATE `locales_npc_text` SET `ID` = 5129,`Text0_0_loc3` = 'Ja, ich zeige Euch ein paar Sachen. Schauen wir mal, wie Ihr Euch macht, vielleicht überrascht Ihr mich ja!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5129;
UPDATE `locales_npc_text` SET `ID` = 5130,`Text0_0_loc3` = '(schnüffelt) Riecht Ihr das? Findet Ihr den Geruch von Schmierfett nicht auch einfach nur toll?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5130;
UPDATE `locales_npc_text` SET `ID` = 5131,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5131;
UPDATE `locales_npc_text` SET `ID` = 5133,`Text0_0_loc3` = 'Ich bau gern Sachen. Aber noch viel lieber jag ich Sachen in die Luft!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5133;
UPDATE `locales_npc_text` SET `ID` = 5134,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5134;
UPDATE `locales_npc_text` SET `ID` = 5135,`Text0_0_loc3` = 'Ich kann ein paar Sachen. Soll ich sie Euch zeigen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5135;
UPDATE `locales_npc_text` SET `ID` = 5136,`Text0_0_loc3` = 'Wer sagt, Orcs sin'' nich'' schlau? Man braucht viel Hirnschmalz, um Ingenieur zu werden!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5136;
UPDATE `locales_npc_text` SET `ID` = 5137,`Text0_0_loc3` = 'Wisst Ihr, wer richtig schlau is''? Nogg! Der is'' sogar noch schlauer als ich. Geht einfach hin und sagt Nogg, Thund hätt Euch geschickt! Der is'' der da an der Schalttafel da drüben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5137;
UPDATE `locales_npc_text` SET `ID` = 5138,`Text0_0_loc3` = 'Ihr denkt also, Ihr seid schlau genug? Ihr glaubt, Ihr habt es drauf? Das werden wir gleich sehen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5138;
UPDATE `locales_npc_text` SET `ID` = 5139,`Text0_0_loc3` = 'Die Dunkle Fürstin hat gesagt, das Wissen sei unsere stärkste Waffe in diesem Krieg.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5139;
UPDATE `locales_npc_text` SET `ID` = 5140,`Text0_0_loc3` = 'Ihr seid bereit für höhere Dinge. Sprecht mit Franklin Lloyd, rechts von mir. Er wird Euch von jetzt an anleiten.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5140;
UPDATE `locales_npc_text` SET `ID` = 5141,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5141;
UPDATE `locales_npc_text` SET `ID` = 5142,`Text0_0_loc3` = 'Ich bin nie zu beschäftigt, um mit Fremden zu sprechen! Fast nie jedenfalls...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5142;
UPDATE `locales_npc_text` SET `ID` = 5143,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5143;
UPDATE `locales_npc_text` SET `ID` = 5144,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5144;
UPDATE `locales_npc_text` SET `ID` = 5145,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5145;
UPDATE `locales_npc_text` SET `ID` = 5146,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5146;
UPDATE `locales_npc_text` SET `ID` = 5147,`Text0_0_loc3` = 'Wartet mal... vierzehn plus sieben, zwei im Sinn... äh, ja?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5147;
UPDATE `locales_npc_text` SET `ID` = 5148,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5148;
UPDATE `locales_npc_text` SET `ID` = 5149,`Text0_0_loc3` = 'Ohne Gegenleistung tu ich nichts! Ich bring Euch bei, was ich weiß, aber erst möchte ich, dass Ihr eine kleine Aufgabe für mich erledigt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5149;
UPDATE `locales_npc_text` SET `ID` = 5150,`Text0_0_loc3` = 'Irgendwann könnt Ihr von mir lernen, aber noch nicht jetzt. Warum redet Ihr nicht erst einmal mit Glimm Springspross, hier rechts von mir? Sobald Ihr etwas Erfahrung habt, könnt Ihr gern wieder zu mir kommen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5150;
UPDATE `locales_npc_text` SET `ID` = 5151,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5151;
UPDATE `locales_npc_text` SET `ID` = 5152,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die Pläne für meinen sechs Meter großen Truthahn-Beträufler sind beinahe fertig! Jetzt brauche ich nur noch einen sechs Meter großen Truthahn...' WHERE `locales_npc_text`.`ID` = 5152;
UPDATE `locales_npc_text` SET `ID` = 5153,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5153;
UPDATE `locales_npc_text` SET `ID` = 5154,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5154;
UPDATE `locales_npc_text` SET `ID` = 5155,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5155;
UPDATE `locales_npc_text` SET `ID` = 5156,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5156;
UPDATE `locales_npc_text` SET `ID` = 5157,`Text0_0_loc3` = 'Wenn der Tag doch nur ein paar Stunden mehr hätte...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5157;
UPDATE `locales_npc_text` SET `ID` = 5158,`Text0_0_loc3` = 'Ihr habt Euch das Vorrecht verdient, bei einem Lehrer für Fortgeschrittene lernen zu dürfen. Geht doch rüber zu meinem Kumpel Roxxik.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5158;
UPDATE `locales_npc_text` SET `ID` = 5159,`Text0_0_loc3` = 'Jeder Tag stellt eine Prüfung dar, da bildet der heutige Tag keine Ausnahme. Wenn Ihr diese Prüfung besteht, dann werde ich Euch einen Teil meines Wissens anvertrauen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5159;
UPDATE `locales_npc_text` SET `ID` = 5160,`Text0_0_loc3` = 'Ihr habt Euch noch nicht das Recht verdient, Euch von mir ausbilden lassen zu dürfen. Sprecht erst mit Thund da drüben am Tisch. Kommt wieder, wenn er sagt, dass Ihr bereit dazu seid.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5160;
UPDATE `locales_npc_text` SET `ID` = 5161,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5161;
UPDATE `locales_npc_text` SET `ID` = 5162,`Text0_0_loc3` = 'Ich hab keine Zeit für Geschwätz, ich bin ein Profi!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5162;
UPDATE `locales_npc_text` SET `ID` = 5163,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5163;
UPDATE `locales_npc_text` SET `ID` = 5164,`Text0_0_loc3` = 'Mal sehen, wie fähig Ihr seid, ja? Wenn Ihr diese Aufgabe zu meiner Zufriedenheit erledigt, dann werde ich die Zeit aufwenden, die es kostet, um Euch auszubilden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5164;
UPDATE `locales_npc_text` SET `ID` = 5165,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5165;
UPDATE `locales_npc_text` SET `ID` = 5167,`Text0_0_loc3` = 'Wenn meine Berechnungen korrekt sind, wird das Rad bald veraltet sein.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5167;
UPDATE `locales_npc_text` SET `ID` = 5168,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5168;
UPDATE `locales_npc_text` SET `ID` = 5170,`Text0_0_loc3` = 'Die Dinge, die ich Euch beibringen würde, würden Euch das Gehirn zu Brei werden lassen. Kommt wieder, wenn Ihr genug gelernt habt!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5170;
UPDATE `locales_npc_text` SET `ID` = 5171,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5171;
UPDATE `locales_npc_text` SET `ID` = 5172,`Text0_0_loc3` = 'Manchmal überrasche ich sogar mich selbst!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5172;
UPDATE `locales_npc_text` SET `ID` = 5173,`Text0_0_loc3` = 'Ich kann nicht lehren, was ich nicht weiß! Wenn es nur jemanden gäbe, der mehr weiß als ich... wartet, da fällt mir gerade jemand ein! Geht und sucht Buzzek Pratzenschwing in Gadgetzan auf!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5173;
UPDATE `locales_npc_text` SET `ID` = 5175,`Text0_0_loc3` = 'Netter Versuch, aber Ihr braucht etwas mehr Erfahrung, bevor ich Euch ausbilden kann! Zurück in den Schützengraben!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5175;
UPDATE `locales_npc_text` SET `ID` = 5176,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5176;
UPDATE `locales_npc_text` SET `ID` = 5177,`Text0_0_loc3` = 'Ich arbeite an einer Methode, Öl unter der Erde zwischen großen Städten zu transportieren, aber alle behaupten, damit würde ich in die Röhre kucken.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5177;
UPDATE `locales_npc_text` SET `ID` = 5179,`Text0_0_loc3` = 'Zeigt mir, was Ihr draufhabt! Es wird Euch nicht Leid tun, das verspreche ich!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5179;
UPDATE `locales_npc_text` SET `ID` = 5180,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5180;
UPDATE `locales_npc_text` SET `ID` = 5181,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Seid gegrüßt! Was führt Euch an meine Türschwelle?' WHERE `locales_npc_text`.`ID` = 5181;
UPDATE `locales_npc_text` SET `ID` = 5182,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich fürchte, ich kann jemandem mit Eurer Erfahrung nichts beibringen. Aber Ihr habt das Ende des Weges noch nicht erreicht. Sprecht mit Lucan Cordell hinter dem Tresen dort. Er wird Euch durch den nächsten Abschnitt Eurer Reise leiten.' WHERE `locales_npc_text`.`ID` = 5182;
UPDATE `locales_npc_text` SET `ID` = 5183,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Hmm... tja, Ihr scheint fähig genug zu sein. Wenn Ihr wirklich lernen wollt, dann wird Euch nichts im Weg stehen! Ich werde Euch auf Euren ersten Schritten entlang des Weges leiten, der hoffentlich lang und erfüllend sein wird.' WHERE `locales_npc_text`.`ID` = 5183;
UPDATE `locales_npc_text` SET `ID` = 5184,`Text0_0_loc3` = 'Es gibt andere Möglichkeiten als Hammer und Amboss, um Stahl zu stärken.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5184;
UPDATE `locales_npc_text` SET `ID` = 5185,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5185;
UPDATE `locales_npc_text` SET `ID` = 5186,`Text0_0_loc3` = 'Ihr seid aber reichlich neugierig, was? Exzellent! Passt gut auf, das hier könnte Eure Haut retten, wenn es hart auf hart kommt!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5186;
UPDATE `locales_npc_text` SET `ID` = 5187,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die Göttin hat mir Weisheit gewährt, Fremdling. Wenn es ihr Wille ist, werde ich sie an Euch weitergeben.' WHERE `locales_npc_text`.`ID` = 5187;
UPDATE `locales_npc_text` SET `ID` = 5188,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Eure Fertigkeiten übersteigen mein Wissen. Es ist an der Zeit für Euch, neuen Herausforderungen gegenüberzutreten. Taladan wird Euch bei der Vorbereitung helfen. Er steht dort drüben am Tisch.' WHERE `locales_npc_text`.`ID` = 5188;
UPDATE `locales_npc_text` SET `ID` = 5189,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5189;
UPDATE `locales_npc_text` SET `ID` = 5190,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Elunes Geheimnisse sind mannigfaltig, aber sie können nicht mit jedem Dahergelaufenen geteilt werden.' WHERE `locales_npc_text`.`ID` = 5190;
UPDATE `locales_npc_text` SET `ID` = 5191,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5191;
UPDATE `locales_npc_text` SET `ID` = 5192,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5192;
UPDATE `locales_npc_text` SET `ID` = 5193,`Text0_0_loc3` = 'Ich habe keine Zeit zu verschwenden, Fremdling. Erklärt Euer Anliegen oder verschwindet!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5193;
UPDATE `locales_npc_text` SET `ID` = 5194,`Text0_0_loc3` = 'Hmm, tja, Ihr habt es also bis hierher geschafft... Dann sprecht mal mit Lavinia Raab dort drüben bei dem anderen Bücherregal. Wenn Ihr es Euch mit ihr nicht verderbt, könnt Ihr vielleicht noch Einiges lernen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5194;
UPDATE `locales_npc_text` SET `ID` = 5195,`Text0_0_loc3` = 'Ich habe keine Zeit zu verschwenden, Fremdling. Erklärt Euer Anliegen oder verschwindet!',`Text0_1_loc3` = 'Ich habe keine Zeit zu verschwenden, Fremdling. Erklärt Euer Anliegen oder verschwindet!' WHERE `locales_npc_text`.`ID` = 5195;
UPDATE `locales_npc_text` SET `ID` = 5196,`Text0_0_loc3` = 'Was würde ich nicht alles geben, um an einem Tag wie diesem dort draußen im Flachland zu sein... aber meine Pflicht hält mich hier.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5196;
UPDATE `locales_npc_text` SET `ID` = 5197,`Text0_0_loc3` = 'Ich kann Euch nichts lehren, was Ihr nicht schon wisst. Das Wissen meines Vaters übersteigt mein eigenes aber um vieles. Geht hinein und sprecht mit ihm. Viel Glück!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5197;
UPDATE `locales_npc_text` SET `ID` = 5198,`Text0_0_loc3` = 'Es gibt viele Geheimnisse, die mit Geduld und Entschlossenheit enthüllt werden können. Lasst uns also beginnen...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5198;
UPDATE `locales_npc_text` SET `ID` = 5199,`Text0_0_loc3` = 'Throm-ka! Was sucht Ihr denn, Fremdling?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5199;
UPDATE `locales_npc_text` SET `ID` = 5200,`Text0_0_loc3` = 'Ihr seid hier falsch. Sprecht mit dem Experten Godan dort drüben. Von ihm werdet Ihr mehr erfahren.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5200;
UPDATE `locales_npc_text` SET `ID` = 5201,`Text0_0_loc3` = 'Es ist glorreich, im Kampf zu sterben! Aber mit dem, was ich Euch lehre, sterbt Ihr vielleicht nicht so schnell, hm?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5201;
UPDATE `locales_npc_text` SET `ID` = 5202,`Text0_0_loc3` = 'Wer seid Ihr? Was wollt Ihr?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5202;
UPDATE `locales_npc_text` SET `ID` = 5203,`Text0_0_loc3` = 'Sehe ich aus wie der Große Zampano der Verzauberer? Eure Fertigkeiten erfordern die Führung eines Fachmanns! Geht und sucht Kitta Feuerwind am Turm von Azora auf.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5203;
UPDATE `locales_npc_text` SET `ID` = 5204,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5204;
UPDATE `locales_npc_text` SET `ID` = 5205,`Text0_0_loc3` = 'Das soll ein Witz sein, nicht wahr? Ich schätze, Ihr sucht nach dem ANDEREN kleinen Burschen, Thonys Säulenstein. Er ist unten. Und jetzt verschwindet!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5205;
UPDATE `locales_npc_text` SET `ID` = 5206,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5206;
UPDATE `locales_npc_text` SET `ID` = 5207,`Text0_0_loc3` = 'Wenn Ihr habt, was nötig ist, werde ich Euch gern den Weg weisen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5207;
UPDATE `locales_npc_text` SET `ID` = 5208,`Text0_0_loc3` = 'Ein wahrer Profi kennt seine Grenzen und ich schäme mich nicht zuzugeben, dass Eure Fertigkeiten die meinen übersteigen. Ich möchte, dass Ihr Kitta Feuerwind am Turm von Azora besucht. Sie ist sehr berühmt und das mit gutem Grund.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5208;
UPDATE `locales_npc_text` SET `ID` = 5209,`Text0_0_loc3` = 'Ihr scheint recht selbstsicher zu sein. Da seid Ihr doch bestimmt mit einem Test Eurer Fertigkeiten einverstanden...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5209;
UPDATE `locales_npc_text` SET `ID` = 5210,`Text0_0_loc3` = 'Ihr seid nicht bereit. Sprecht mit Betty Quinn, sie steht dort drüben an der Treppe. Kehrt zu mir zurück, wenn Ihr eine höhere Erleuchtung erreicht habt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5210;
UPDATE `locales_npc_text` SET `ID` = 5211,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5211;
UPDATE `locales_npc_text` SET `ID` = 5212,`Text0_0_loc3` = 'Wenn Ihr Naga jagen wollt, werdet Ihr alle Hilfe benötigen, die Ihr bekommen könnt. Tief im Wasser könnt Ihr blubbernde Felsspalten finden, die genug Luftblasen erzeugen, dass Ihr unter Wasser atmen könnt. Schwimmt nahe heran, damit Ihr die Blasen nutzen könnt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5212;
UPDATE `locales_npc_text` SET `ID` = 5213,`Text0_0_loc3` = 'Es ist wahr, dass ich über Fertigkeiten verfüge, die ich jene lehre, die würdig sind. Könntet Ihr dazugehören?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5213;
UPDATE `locales_npc_text` SET `ID` = 5214,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5214;
UPDATE `locales_npc_text` SET `ID` = 5215,`Text0_0_loc3` = 'Ich glaube, dass Ihr dieser Aufgabe gewachsen seid. Werdet Ihr die Herausforderung annehmen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5215;
UPDATE `locales_npc_text` SET `ID` = 5216,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5216;
UPDATE `locales_npc_text` SET `ID` = 5217,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5217;
UPDATE `locales_npc_text` SET `ID` = 5218,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wenn Ihr eine wahrlich lohnende Suche wünscht, dann ist ein der Verzauberkunst gewidmetes Leben das richtige für Euch!' WHERE `locales_npc_text`.`ID` = 5218;
UPDATE `locales_npc_text` SET `ID` = 5219,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5219;
UPDATE `locales_npc_text` SET `ID` = 5220,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5220;
UPDATE `locales_npc_text` SET `ID` = 5223,`Text0_0_loc3` = 'Die Verzauberkunst wird seit Generationen in unserer Familie gepflegt. Ich habe sie schon in jungen Jahren gelernt, so wie mein Sohn jetzt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5223;
UPDATE `locales_npc_text` SET `ID` = 5224,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5224;
UPDATE `locales_npc_text` SET `ID` = 5225,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5225;
UPDATE `locales_npc_text` SET `ID` = 5226,`Text0_0_loc3` = 'Mot hat Euch doch wohl nicht zu mir geschickt, oder? Er sollte es besser wissen. Ihr seid noch nicht bereit, das zu verstehen, was ich Euch lehre. Sucht meinen Sohn Mot auf, er ist da draußen, und beendet zuerst die Ausbildung bei ihm.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5226;
UPDATE `locales_npc_text` SET `ID` = 5227,`Text0_0_loc3` = 'Ah, sehr gut. Ich erkenne an, dass Ihr ein würdiger Schüler seid. Jetzt kann Eure Ausbildung bei mir beginnen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5227;
UPDATE `locales_npc_text` SET `ID` = 5228,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich bekomme nicht viel Besuch. Ihr sucht also nach etwas Bestimmtem, ja?' WHERE `locales_npc_text`.`ID` = 5228;
UPDATE `locales_npc_text` SET `ID` = 5229,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wenn ich hier nicht tagein, tagaus beschäftigt wäre, hätte ich das Können erworben, um jemanden wie Euch auszubilden! Leider ist das nicht der Fall. Sprecht mit Hgarth in Sonnenfels im Steinkrallengebirge.' WHERE `locales_npc_text`.`ID` = 5229;
UPDATE `locales_npc_text` SET `ID` = 5230,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Hmm, eine Hand wäscht die andere. Wenn Ihr tut, was ich sage, und es gut macht, können wir vielleicht Eure Ausbildung erörtern.' WHERE `locales_npc_text`.`ID` = 5230;
UPDATE `locales_npc_text` SET `ID` = 5231,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Nein, nein, Ihr benötigt VIEL mehr Ausbildung, bevor Ihr bereit seid, von mir zu lernen! Geht und sprecht mit diesem verklemmten Pfuscher Malcomb Wynn. Das ist der Bücherwurm da drüben.' WHERE `locales_npc_text`.`ID` = 5231;
UPDATE `locales_npc_text` SET `ID` = 5232,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5232;
UPDATE `locales_npc_text` SET `ID` = 5233,`Text0_0_loc3` = 'Seid gegrüßt! Wollt Ihr vielleicht die Mysterien des Arkanen ergründen? Hm?',`Text0_1_loc3` = 'Seid gegrüßt! Wollt Ihr vielleicht die Mysterien des Arkanen ergründen? Hm?' WHERE `locales_npc_text`.`ID` = 5233;
UPDATE `locales_npc_text` SET `ID` = 5238,`Text0_0_loc3` = 'Einige Abenteurer glauben, dass die Verzauberei nicht funktioniere. Im Allgemeinen ändern sie ihre Meinung, wenn ich sie zum Kampf herausfordere!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5238;
UPDATE `locales_npc_text` SET `ID` = 5239,`Text0_0_loc3` = 'Ich bin Euren Fertigkeiten nicht gewachsen, Fremdling. Sprecht mit Hgarth in Sonnenfels im Steinkrallengebirge. Er wird Euch mehr Wissen vermitteln können als ich es kann.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5239;
UPDATE `locales_npc_text` SET `ID` = 5240,`Text0_0_loc3` = 'Ihr könntet möglicherweise geeignet sein. Lasst es uns herausfinden!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5240;
UPDATE `locales_npc_text` SET `ID` = 5241,`Text0_0_loc3` = 'Ihr seid nicht geeignet, um bei mir zu lernen, Fremdling. Wendet Euch an meinen Assistenten Jhag dort drüben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5241;
UPDATE `locales_npc_text` SET `ID` = 5242,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5242;
UPDATE `locales_npc_text` SET `ID` = 5243,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Was führt Euch zum Turm?' WHERE `locales_npc_text`.`ID` = 5243;
UPDATE `locales_npc_text` SET `ID` = 5244,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr habt den Gipfel Eurer Ausbildung fast erreicht. Um Euer Talent als Verzauberer wirklich voll auszuschöpfen, müsst Ihr noch eine Lehrerin aufsuchen. Ihr Name ist Annora und Ihr findet sie in Uldaman.' WHERE `locales_npc_text`.`ID` = 5244;
UPDATE `locales_npc_text` SET `ID` = 5246,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr seid eifrig, das ist gut. Ihr seid aber noch nicht bereit, von jemandem wie mir zu lernen.' WHERE `locales_npc_text`.`ID` = 5246;
UPDATE `locales_npc_text` SET `ID` = 5247,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Eure Fertigkeiten werden wahrhaft außergewöhnlich. Setzen wir jetzt also Eure Ausbildung fort...' WHERE `locales_npc_text`.`ID` = 5247;
UPDATE `locales_npc_text` SET `ID` = 5248,`Text0_0_loc3` = 'Seid Ihr gekommen, das Wissen von Hgarth zu suchen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5248;
UPDATE `locales_npc_text` SET `ID` = 5249,`Text0_0_loc3` = 'Es gibt nur eine, deren Wissen das meine übersteigt. Ihr seid bereit, ihr Wissen zu empfangen. Sie heißt Annora und Ihr findet sie... in Uldaman.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5249;
UPDATE `locales_npc_text` SET `ID` = 5251,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5251;
UPDATE `locales_npc_text` SET `ID` = 5252,`Text0_0_loc3` = 'Ihr habt neue Höhen auf Eurem Weg zur Meisterschaft erreicht. Lasst uns sehen, ob wir Euch noch weiter bringen können.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5252;
UPDATE `locales_npc_text` SET `ID` = 5253,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Seid gegrüßt. Ihr habt meine ewig währende Dankbarkeit dafür, dass Ihr es mit diesen schrecklichen Kreaturen aufgenommen habt! Ich dachte schon, ich müsste mich für immer verstecken!' WHERE `locales_npc_text`.`ID` = 5253;
UPDATE `locales_npc_text` SET `ID` = 5255,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Eure Ausbildung ist fast abgeschlossen. Nur noch eine letzte Aufgabe steht zwischen Euch und dem Höhepunkt all Eurer Bemühungen.' WHERE `locales_npc_text`.`ID` = 5255;
UPDATE `locales_npc_text` SET `ID` = 5256,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Es gibt noch immer Lektionen, die Ihr von anderen lernen müsst, bevor ich mein Wissen mit Euch teilen kann. Seid fleißig und gebt die Hoffnung nicht auf.' WHERE `locales_npc_text`.`ID` = 5256;
UPDATE `locales_npc_text` SET `ID` = 5257,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Seit mein Ehemann an die Front gezogen ist, hat Helene hier mir Kraft gegeben. Ihr gutes Herz und ihr scharfes Schwert haben mir durch harte Zeiten geholfen. Ich weiß nicht, was ich ohne sie getan hätte.' WHERE `locales_npc_text`.`ID` = 5257;
UPDATE `locales_npc_text` SET `ID` = 5258,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Oh je, das tut mir Leid. Ich habe nichts Neues, was ich Euch beibringen kann. Wie wäre es, wenn Ihr mit Simon Gerber in der Altstadt von Sturmwind sprecht? Das ist ein Mensch mit starken Muskeln und traumhaften roten Haaren.' WHERE `locales_npc_text`.`ID` = 5258;
UPDATE `locales_npc_text` SET `ID` = 5259,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5259;
UPDATE `locales_npc_text` SET `ID` = 5260,`Text0_0_loc3` = 'Ich weiß bereits viel über Stil. Jetzt muss ich nur noch die Techniken der Lederverarbeitung meistern. Gut, dass Simon ein echter Kumpel ist und mir dabei hilft.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5260;
UPDATE `locales_npc_text` SET `ID` = 5261,`Text0_0_loc3` = 'Es gibt nichts, was ich Euch lehren könnte, außer vielleicht ein besseres Auge für Farbe und Details. Sprecht mit Simon Gerber dort drüben, er weiß viel über die fortgeschrittenen Techniken für die Lederverarbeitung.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5261;
UPDATE `locales_npc_text` SET `ID` = 5262,`Text0_0_loc3` = 'Dieses Outfit bringt gut das Blau in meinen Augen zur Geltung, meint Ihr nicht? Es ist klar, dass ich über viele Fertigkeiten verfüge. Also kann ich es mir leisten, ein paar meiner Tricks preiszugeben. Hört gut zu, ja?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5262;
UPDATE `locales_npc_text` SET `ID` = 5263,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Willkommen in unserem Heim, $N. Radnaal und ich können Euch wenig bieten, aber wir heißen Euch gern hier willkommen.' WHERE `locales_npc_text`.`ID` = 5263;
UPDATE `locales_npc_text` SET `ID` = 5264,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5264;
UPDATE `locales_npc_text` SET `ID` = 5265,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr müsst Eure Stiche kleiner machen, $N. Dann werden weniger Gegenstände aus Euren Köchern und Munitionsbeuteln fallen. Außerdem mag niemand eine ausgebeulte Tunika.' WHERE `locales_npc_text`.`ID` = 5265;
UPDATE `locales_npc_text` SET `ID` = 5273,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wenn Ihr an Ledernäharbeiten interessiert seid, seid Ihr am richtigen Ort. Faldron und Telonis sind wahre Künstler und ich hoffe, viel von ihnen zu lernen.' WHERE `locales_npc_text`.`ID` = 5273;
UPDATE `locales_npc_text` SET `ID` = 5274,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Oh, es tut mir Leid, aber Ihr wisst jetzt mehr über Leder als ich. Sprecht mit Faldron dort drüben, wenn Ihr mehr lernen wollt.' WHERE `locales_npc_text`.`ID` = 5274;
UPDATE `locales_npc_text` SET `ID` = 5275,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Es ist ja so aufregend, hier in Darnassus zu sein, umgeben von talentierten Menschen! Ich weiß einfach, dass ich meine Familie stolz machen werde. Und wenn ich sehr viel Glück habe, bittet mich die Schildwache vielleicht, ihren Streitkräften an der Front beizutreten!' WHERE `locales_npc_text`.`ID` = 5275;
UPDATE `locales_npc_text` SET `ID` = 5276,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Dieser Balg ist noch nicht bereit zur Verarbeitung. Ich wünschte, es würde nicht so lange dauern, Leder herzustellen. Es muss doch eine Möglichkeit geben, diese ganze Warterei zu verkürzen. Das kann doch für meine Gesundheit nicht gut sein, oder?' WHERE `locales_npc_text`.`ID` = 5276;
UPDATE `locales_npc_text` SET `ID` = 5277,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ah, welch Unglück! Meine Fertigkeiten sind nicht gut genug, um Euch von Nutzen zu sein. Kann man nicht ändern. Sprecht mit Fimble Feinspindel dort drüben beim Webstuhl. Er arbeitet schon länger als ich mit Leder.' WHERE `locales_npc_text`.`ID` = 5277;
UPDATE `locales_npc_text` SET `ID` = 5278,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Au prima, endlich was zu tun während der Wartezeit. Ihr rettet mir wahrlich das Leben, $N!' WHERE `locales_npc_text`.`ID` = 5278;
UPDATE `locales_npc_text` SET `ID` = 5279,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Gefällt Euch der Teppich? Ich habe das Biest selbst getötet und gehäutet. Mögen alle Zentauren ebenso leicht fallen.' WHERE `locales_npc_text`.`ID` = 5279;
UPDATE `locales_npc_text` SET `ID` = 5280,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich kann Euch nichts mehr beibringen. Sprecht mit Karolek, wenn Ihr Eure Fertigkeiten in der Lederverarbeitung weiter verfeinern wollt.' WHERE `locales_npc_text`.`ID` = 5280;
UPDATE `locales_npc_text` SET `ID` = 5281,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Zu dumm, dass wir nicht die Menschen und ihre Verbündeten häuten können. Diese ganze Haut ist leider verloren. Aber wer wollte schon das dünne Leder tragen, das man daraus machen könnte...' WHERE `locales_npc_text`.`ID` = 5281;
UPDATE `locales_npc_text` SET `ID` = 5282,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Haut, wunderbare Haut. Sie hält die Innereien... drin. Euch ist wahrscheinlich aufgefallen, dass es meiner Haut nicht so gut geht. Wenn ich nur daran denke, dass ich mir mal um meine Poren Sorgen gemacht habe. Jetzt ist kaum noch Haut übrig, um die ich mich sorgen kann. Irgendwie ist das sehr befreiend.' WHERE `locales_npc_text`.`ID` = 5282;
UPDATE `locales_npc_text` SET `ID` = 5283,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5283;
UPDATE `locales_npc_text` SET `ID` = 5284,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Bevor die Seuche kam, wusste ich nichts über Leder. Jetzt weiß ich genug, um andere zu lehren. Der Tod hat mir viel Zeit gelassen.' WHERE `locales_npc_text`.`ID` = 5284;
UPDATE `locales_npc_text` SET `ID` = 5285,`Text0_0_loc3` = 'Fleisch, Haut, Knochen... ein guter Jäger lässt nichts verderben. Jedes Stückchen Leder kann gerettet werden, um vielleicht die Rüstung eines Verbündeten zu verstärken. Daher ist jeder von uns allein zwar stark, aber zusammen werden wir unbesiegbar.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5285;
UPDATE `locales_npc_text` SET `ID` = 5286,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5286;
UPDATE `locales_npc_text` SET `ID` = 5287,`Text0_0_loc3` = 'Wir leben und sterben und das Land bleibt, auf ewig, unberührt von unseren Schritten. Daher kann das Land nicht uns gehören, $N. Vielmehr gehören wir ihm. Daher schützen wir das Land, denn ohne das Land können wir nicht hoffen zu überleben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5287;
UPDATE `locales_npc_text` SET `ID` = 5288,`Text0_0_loc3` = 'Die Höhlen unter uns sind eine Quelle merkwürdiger und wundersamer Bälge, aber Ihr müsst Euch in Acht nehmen, wenn Ihr in ihre Tiefen vordringt, $N. Denkt daran, dass Eure eigene Haut viel wertvoller ist.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5288;
UPDATE `locales_npc_text` SET `ID` = 5289,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5289;
UPDATE `locales_npc_text` SET `ID` = 5291,`Text0_0_loc3` = 'Cairne selbst lebt hier, sagt man. Wenn ich ein passendes Geschenk angefertigt habe, werde ich ihn aufsuchen. Vielleicht hat er Worte der Weisheit für mich.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5291;
UPDATE `locales_npc_text` SET `ID` = 5292,`Text0_0_loc3` = 'Euer Talent geht über das meinige hinaus. Sprecht mit Tarn, sein Wissen über Leder ist viel größer als meines. Ich bin sicher, er würde einen weiteren Schüler willkommen heißen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5292;
UPDATE `locales_npc_text` SET `ID` = 5293,`Text0_0_loc3` = 'Säubert Eure Hände, bevor Ihr mit einem Projekt beginnt. Die natürliche Schönheit des Leders würde leiden, wenn es mit Blut oder Schmutz verunreinigt würde.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5293;
UPDATE `locales_npc_text` SET `ID` = 5294,`Text0_0_loc3` = 'Als ich meinen Weg aus dem Grab grub, dachte ich, meine Familie würde mich mit offenen Armen empfangen. Stattdessen verscheuchte sie mich aus dem Dorf und schrie in einer Sprache, die ich nicht mehr verstehen konnte.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5294;
UPDATE `locales_npc_text` SET `ID` = 5295,`Text0_0_loc3` = 'Ich kann Euch nicht helfen; Eure Arbeit geht über meine hinaus. Sprecht mit Arthur Moor, dort drüben bei den Särgen. Er kann Euch weiter ausbilden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5295;
UPDATE `locales_npc_text` SET `ID` = 5296,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5296;
UPDATE `locales_npc_text` SET `ID` = 5314,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr übersteigt meine beschränkten Fertigkeiten. Sucht die Nachtelfin Ainethil auf der Terrasse der Handwerker in Darnassus auf. Sie kann Euch mehr beibringen. Ich wünschte nur, ich könnte mit Euch gehen.' WHERE `locales_npc_text`.`ID` = 5314;
UPDATE `locales_npc_text` SET `ID` = 5316,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Meine Lehren wären in diesem Stadium zu fortgeschritten für Euch. Wenn Ihr mehr über Tränke lernen möchtet, sprecht stattdessen mit Tel''Athir hier.' WHERE `locales_npc_text`.`ID` = 5316;
UPDATE `locales_npc_text` SET `ID` = 5317,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Hört gut zu, $N, denn ich habe viel Wissen mitzuteilen.' WHERE `locales_npc_text`.`ID` = 5317;
UPDATE `locales_npc_text` SET `ID` = 5318,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5318;
UPDATE `locales_npc_text` SET `ID` = 5319,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5319;
UPDATE `locales_npc_text` SET `ID` = 5320,`Text0_0_loc3` = 'Randal ist ein kleiner Geck, aber man kann sich auf ihn verlassen, was mehr ist, als ich von vielen Schülern sagen kann, die ich aufnehme. Nun ja, man kann sie wohl nicht dafür schelten, dass sie auf Abenteuer aus sind.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5320;
UPDATE `locales_npc_text` SET `ID` = 5321,`Text0_0_loc3` = 'Es tut mir Leid, aber Ihr habt die Grenzen dessen erreicht, was ich Euch lehren kann. Wenn Ihr mehr lernen wollt, geht zu Telonis auf der Terrasse der Handwerker in Darnassus. Er ist ziemlich talentiert, wie ich höre.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5321;
UPDATE `locales_npc_text` SET `ID` = 5323,`Text0_0_loc3` = 'Kommt zu mir zurück, wenn Ihr mehr Ausbildung genossen habt. Randal Worth dort drüben kann Euch dabei helfen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5323;
UPDATE `locales_npc_text` SET `ID` = 5324,`Text0_0_loc3` = 'Hmm, ich denke, ich kann einen weiteren Schüler aufnehmen. Also los, steht nicht einfach so rum. Sagt mir, was Ihr lernen wollt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5324;
UPDATE `locales_npc_text` SET `ID` = 5325,`Text0_0_loc3` = 'Darianna ist viel versprechend, aber sie ist so voller Energie, dass es für sie schwierig ist, still zu halten und zuzuhören. Mit ihrer Kraft und Beweglichkeit fürchte ich, dass es nicht lange dauert, bis sie zum Kampf gerufen wird. Wir müssen sie schnell ausbilden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5325;
UPDATE `locales_npc_text` SET `ID` = 5326,`Text0_0_loc3` = 'Wenn Ihr für weitere Ausbildung hier seid, habt Ihr meine Fähigkeiten überschätzt. Sprecht mit Telonis dort drüben, er kann Euch helfen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5326;
UPDATE `locales_npc_text` SET `ID` = 5328,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5328;
UPDATE `locales_npc_text` SET `ID` = 5329,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5329;
UPDATE `locales_npc_text` SET `ID` = 5330,`Text0_0_loc3` = 'Viel zu tun! Ich war nicht mehr so beschäftigt, seit mein Cousin aus Versehen allen Stoff und alles Leder in Gnomeregan verdampfen ließ. Mein Gott, war das hier vielleicht zugig danach. Ja, ja, $N, das waren noch Zeiten...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5330;
UPDATE `locales_npc_text` SET `ID` = 5331,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5331;
UPDATE `locales_npc_text` SET `ID` = 5333,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5333;
UPDATE `locales_npc_text` SET `ID` = 5334,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5334;
UPDATE `locales_npc_text` SET `ID` = 5335,`Text0_0_loc3` = 'Die stärksten Bälge kommen von Kodos, die sich zum Sterben in diese Einöde begeben haben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5335;
UPDATE `locales_npc_text` SET `ID` = 5336,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5336;
UPDATE `locales_npc_text` SET `ID` = 5338,`Text0_0_loc3` = 'Ich biete Lehrlingen keine Ausbildung. Sucht hier im Brachland nach dem Tauren Waldor in den Höhlen des Wehklagens. Er kann Euch bei allem helfen, was Ihr benötigt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5338;
UPDATE `locales_npc_text` SET `ID` = 5339,`Text0_0_loc3` = 'Seit Ihr gekommen, um Euch weiter ausbilden zu lassen? Dann streift den Staub der Straße von Eurer Kleidung und lasst uns mit der Arbeit beginnen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5339;
UPDATE `locales_npc_text` SET `ID` = 5340,`Text0_0_loc3` = 'Meine Axt hat viele Schlachten gesehen und ich habe sie immer griffbereit. Feindliche Streitkräfte haben schon etliche Male ihren Weg in unsere Hauptstadt erkämpft, aber habt keine Angst: Wir konnten sie immer wieder zurücktreiben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5340;
UPDATE `locales_npc_text` SET `ID` = 5341,`Text0_0_loc3` = 'Ich kann Euch nicht mit weiterer Ausbildung dienen: Eure Fertigkeit bei der Lederverarbeitung kommt der meinen gleich. Ihr solltet mit Una dort drüben sprechen. Ich werde Eure Fertigkeit bestätigen, falls das nötig ist.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5341;
UPDATE `locales_npc_text` SET `ID` = 5343,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5343;
UPDATE `locales_npc_text` SET `ID` = 5344,`Text0_0_loc3` = 'Ich bin zufrieden mit Eurem bisherigen Fortschritt. Ganz sicher hat die Erdenmutter Eure Hände inspiriert.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5344;
UPDATE `locales_npc_text` SET `ID` = 5345,`Text0_0_loc3` = 'Wir sind auf diesem Kontinent im Moment nicht sehr fest etabliert, aber die Menschen können uns nicht für immer zurückschlagen. Dann werden sie für unsere Sklaverei bezahlen. Wir werden ihnen für ihren Verrat die lügnerischen Zungen herausschneiden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5345;
UPDATE `locales_npc_text` SET `ID` = 5346,`Text0_0_loc3` = 'Ich würde Euch ausbilden, wenn ich könnte, aber Ihr seid zu gut für mich. Ich sage Euch etwas: Sprecht mit der Taurin Una in den nordwestlichen Läden des Zentralplateaus von Donnerfels. Sie hat wirklich Talent.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5346;
UPDATE `locales_npc_text` SET `ID` = 5348,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5348;
UPDATE `locales_npc_text` SET `ID` = 5350,`Text0_0_loc3` = 'Ob ich beschäftigt bin? Was soll das heißen? Glaubt Ihr etwa, dass ich hier den ganzen Tag stehe und Daumen drehe? Wenn Ihr hier seid, um etwas zu kaufen, dann sprecht mit Handor.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5350;
UPDATE `locales_npc_text` SET `ID` = 5351,`Text0_0_loc3` = 'Ihr werdet von mir nichts Neues lernen, Ihr wisst bereits zu viel. Wenn Ihr mehr Ausbildung wollt, dann sprecht mit der Taurin Una in den nordwestlichen Läden des Zentralplateaus von Donnerfels. Richtet ihr meine Grüße aus, ja?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5351;
UPDATE `locales_npc_text` SET `ID` = 5353,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5353;
UPDATE `locales_npc_text` SET `ID` = 5354,`Text0_0_loc3` = 'Die verwinkelten Straßen von Orgrimmar wurden geschaffen, um Eindringlinge abzuschrecken. Könnt Ihr Euch vorstellen, wie schnell sich einer dieser kümmerlichen Gnome hier verirrt? Ha! Wenn er Glück hat, dann bringe ich ihn zur Strecke und behalte ihn als Haustier.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5354;
UPDATE `locales_npc_text` SET `ID` = 5355,`Text0_0_loc3` = 'Vor meinem Tod war ich Bibliothekar; ich hatte nichts mit Kriegen und Seuchen zu tun. Ich habe nie darum gebeten, zu dem hier zu werden. Auf der anderen Seite werde ich nicht kampflos aufgeben, jetzt, wo meine Gedanken wieder meine eigenen sind.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5355;
UPDATE `locales_npc_text` SET `ID` = 5356,`Text0_0_loc3` = 'Ich freue mich sagen zu können, dass Eure Talente die meinen übersteigen. Und jetzt geht weg. Ähm, sprecht mit der Taurin Una in den nordwestlichen Läden des Zentralplateaus von Donnerfels. Sie ist sehr bewandert, ganz gewiss.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5356;
UPDATE `locales_npc_text` SET `ID` = 5358,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5358;
UPDATE `locales_npc_text` SET `ID` = 5359,`Text0_0_loc3` = 'Ich hatte mich zuerst im Magieviertel eingerichtet, aber dort war es sehr laut. Das hier ist eine deutliche Verbesserung, abgesehen von ein paar hartnäckigen Schülern, die mich hier aufspüren, obwohl ich umgezogen bin.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5359;
UPDATE `locales_npc_text` SET `ID` = 5360,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Elune hat den Nachtelfen viele Gaben gewährt. Im Gegenzug ist es unsere Verantwortung, diese Wälder vor der dämonischen Besudelung zu bewahren, die die Horde immer mit sich bringt.' WHERE `locales_npc_text`.`ID` = 5360;
UPDATE `locales_npc_text` SET `ID` = 5361,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5361;
UPDATE `locales_npc_text` SET `ID` = 5363,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5363;
UPDATE `locales_npc_text` SET `ID` = 5364,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Überprüft Eure Maße immer doppelt, bevor Ihr beginnt, das Leder zu zerschneiden. Wir können es uns nicht leisten, Material zu verschwenden.' WHERE `locales_npc_text`.`ID` = 5364;
UPDATE `locales_npc_text` SET `ID` = 5373,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5373;
UPDATE `locales_npc_text` SET `ID` = 5374,`Text0_0_loc3` = 'Im Silberwald entlang den Rändern des nördlichen Gezeitenstroms liegt ein verfallenes Boot am Strand. Westlich davon befindet sich das, was Ihr sucht. Schwimmt Ihr jedoch zu weit hinaus, werdet Ihr vor Erschöpfung im Meer sterben. Was Ihr sucht, befindet sich auf dem Meeresboden in einer Schließkassette und wartet darauf, dass Ihr es an Euch nehmt.$B$BEs wird für Euch schwer werden, einen Tauchgang in solche Tiefen ohne Hilfe zu überleben, doch fasst Euch ein Herz; ein blubbernder Felsspalt bietet neue Atemluft und hilft bei der Suche nach dieser Hälfte des Anhängers. Benutzt ihn weise.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5374;
UPDATE `locales_npc_text` SET `ID` = 5375,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Im nördlichen Teil von Dunkelküste verläuft der Klippenquell. Er fließt von den Ländern herunter, die als der Teufelswald bekannt sind. Wo der Fluss in den Nebelrand fließt, müsst Ihr gen Norden reisen, dorthin, wo der tiefste Teil des Ozeans beginnt. Bei einer Steinformation, die einige wegen ihrer Form die Steinklaue nennen, könnt Ihr finden, was Ihr sucht.$B$BDoch seid gewarnt - bei der Steinklaue sammeln sich Drescher und greifen alle an, die in ihr Gebiet eindringen.' WHERE `locales_npc_text`.`ID` = 5375;
UPDATE `locales_npc_text` SET `ID` = 5376,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Im nordöstlichen Brachland liegt ein Teich, der durch Verschmutzung gekippt ist. Die Einheimischen nennen ihn das Schlickermoor; die Venture Company hat das einst kristallblaue Wasser in einen Sumpf aus industriellen Nebenprodukten und Abfällen verwandelt. Im Herzen dieses Moores liegt, was Ihr sucht, $N.$B$BDoch seid gewarnt - die Venture Company toleriert keine Eindringlinge und sie sieht jeden, der nicht für sie arbeitet, als einen solchen an. Als Beschützer der Natur wird sie besonders gegen Euch eingestellt sein, da sie diese Aktivitäten heftigst ablehnt.' WHERE `locales_npc_text`.`ID` = 5376;
UPDATE `locales_npc_text` SET `ID` = 5393,`Text0_0_loc3` = 'Das Gras ist weich und üppig. Die Luft ist frisch, voller Licht und Geräusche. Alles, was ich gesehen habe, deutet darauf hin, dass Teldrassil ein Traum ist, der wahr geworden ist. Warum ist dann mein Herz voller düsterer Vorahnungen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5393;
UPDATE `locales_npc_text` SET `ID` = 5394,`Text0_0_loc3` = 'Ihr seid weiter fortgeschritten als meine Fertigkeit, die ich Euch lehren könnte. Reist jetzt zum Nistgipfel im Hinterland und sprecht mit dem Zwerg Drakk Steinhand. Seine Arbeit ist großartig, viel besser als meine eigene.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5394;
UPDATE `locales_npc_text` SET `ID` = 5396,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5396;
UPDATE `locales_npc_text` SET `ID` = 5397,`Text0_0_loc3` = 'Versucht nicht, dem Leder Euren Willen aufzuzwingen. Lasst vielmehr seine natürliche Veranlagung diktieren, welche Form und Funktion es annehmen wird. Das hier ist kein Kampf, der gewonnen werden soll. Arbeitet mit dem Leder, nicht dagegen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5397;
UPDATE `locales_npc_text` SET `ID` = 5398,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die Nadel ist klein, aber der Faden ist lang und die Stiche sind fein. Unser Pakt mit Thrall und den anderen wird halten. Die Horde wird nicht aus diesen Ländern vertrieben werden.' WHERE `locales_npc_text`.`ID` = 5398;
UPDATE `locales_npc_text` SET `ID` = 5399,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Sogar meine Talente haben eine Grenze und Ihr habt sie erreicht. Herzlichen Glückwunsch, $N. Euer nächster Halt sollte das Camp Mojache in Feralas sein. Die Tauren Hahrana Eisenhaut lebt dort; sie ist für ihre Fertigkeit bekannt.' WHERE `locales_npc_text`.`ID` = 5399;
UPDATE `locales_npc_text` SET `ID` = 5401,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Es ist so lange her, seitdem ich selbst ein Novize war, dass ich nicht glaube, für Euch der beste Lehrer zu sein. Sprecht lieber mit Tran oder Mak dort drüben. Einer von ihnen sollte Eure Fragen beantworten können.' WHERE `locales_npc_text`.`ID` = 5401;
UPDATE `locales_npc_text` SET `ID` = 5402,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Cairne hat uns in ein Land gebracht, das reich an Möglichkeiten für die Zukunft ist. Wir hatten Glück und daher geben wir dieses Glück an andere weiter. Aus diesem Grund begann ich zu unterrichten, $N.' WHERE `locales_npc_text`.`ID` = 5402;
UPDATE `locales_npc_text` SET `ID` = 5403,`Text0_0_loc3` = 'Ich kann verblüffende Dinge mit den Materialien tun, die ich hier draußen finde. Bei Muradins Bart, es gibt phantastische Kreaturen in diesen Hügeln! Stellt Euch ihnen entgegen, $N, und bringt mir ein paar großartige Geschichten zurück.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5403;
UPDATE `locales_npc_text` SET `ID` = 5405,`Text0_0_loc3` = 'Es geht doch nichts über eine kleine Herausforderung. Meint Ihr nicht auch? Und darum werdet Ihr genau tun, was ich sage.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5405;
UPDATE `locales_npc_text` SET `ID` = 5406,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5406;
UPDATE `locales_npc_text` SET `ID` = 5407,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Alle Anfragen für meine Arbeiten müssen persönlich vom Käufer gemacht werden. Orwin bittet mich häufig, das zu überdenken, aber ich werde hier mehr gebraucht. Seht Euch um: Viele dieser Kämpfer vertrauen mir, was ihre Rüstung angeht.' WHERE `locales_npc_text`.`ID` = 5407;
UPDATE `locales_npc_text` SET `ID` = 5408,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich wünschte nur, ich hätte mehr Schüler wie Euch. Nicht viele werden so weit in feindliches Gebiet vordringen. Ich verbeuge mich vor Eurer Courage.' WHERE `locales_npc_text`.`ID` = 5408;
UPDATE `locales_npc_text` SET `ID` = 5409,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5409;
UPDATE `locales_npc_text` SET `ID` = 5410,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5410;
UPDATE `locales_npc_text` SET `ID` = 5413,`Text0_0_loc3` = 'Ich erkenne auf den ersten Blick, dass Eure Garderobe Pflege bitter nötig hat.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5413;
UPDATE `locales_npc_text` SET `ID` = 5414,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5414;
UPDATE `locales_npc_text` SET `ID` = 5415,`Text0_0_loc3` = 'Ihr würdet also gern ein paar Dinge lernen? Ich wäre erfreut, Euch dabei zu helfen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5415;
UPDATE `locales_npc_text` SET `ID` = 5416,`Text0_0_loc3` = 'Das Leben ist wie ein sorgfältig gewebter Wandteppich.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5416;
UPDATE `locales_npc_text` SET `ID` = 5417,`Text0_0_loc3` = 'Hmm... bei den Dingen, die Ihr lernen müsst, bin ich etwas überfragt. Sprecht mit Sellandus gleich hier rechts den Weg entlang. Er hat mir alles beigebracht, was ich weiß!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5417;
UPDATE `locales_npc_text` SET `ID` = 5418,`Text0_0_loc3` = 'Sehr gut, lasst Eure Reise auf dem Weg der Schneiderei beginnen und verliert dabei niemals den Faden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5418;
UPDATE `locales_npc_text` SET `ID` = 5419,`Text0_0_loc3` = 'Nadelarbeit erfordert eine ruhige Hand. Und Geduld... vor allem Geduld.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5419;
UPDATE `locales_npc_text` SET `ID` = 5420,`Text0_0_loc3` = 'Es ist Zeit für Euch, nach Darnassus zu reisen und mit Me''lynn zu sprechen. Sie wird Euch von nun an leiten.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5420;
UPDATE `locales_npc_text` SET `ID` = 5421,`Text0_0_loc3` = 'Ich wäre erfreut, Euch unter meine Fittiche zu nehmen. Lasst uns beginnen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5421;
UPDATE `locales_npc_text` SET `ID` = 5422,`Text0_0_loc3` = 'Willkommen in meinem Laden. Was kann ich für Euch tun?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5422;
UPDATE `locales_npc_text` SET `ID` = 5423,`Text0_0_loc3` = 'Wartet einen Moment, ich bin nur ein Assistent. Wenn Ihr die wirklich tollen Sachen lernen wollt, dann sprecht mit Steinbraue dort drüben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5423;
UPDATE `locales_npc_text` SET `ID` = 5424,`Text0_0_loc3` = 'Exzellent! Ich hatte auf einen neuen Schüler gehofft. Also gut, dann lasst uns direkt beginnen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5424;
UPDATE `locales_npc_text` SET `ID` = 5425,`Text0_0_loc3` = 'Wir verwenden viele Tierpelze und Häute bei unseren Kreationen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5425;
UPDATE `locales_npc_text` SET `ID` = 5426,`Text0_0_loc3` = 'Ich weiß ein paar Dinge, aber ich bin kein Experte. Zu Eurem Glück ist Magar aber einer. Er ist im hinteren Teil des Ladens. Seht, ob er Euch helfen kann, aber verärgert ihn ja nicht. Er ist sehr reizbar.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5426;
UPDATE `locales_npc_text` SET `ID` = 5427,`Text0_0_loc3` = 'Ich werde Euch beibringen, was ich weiß.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5427;
UPDATE `locales_npc_text` SET `ID` = 5428,`Text0_0_loc3` = 'Kein Teil der Tiere, die wir jagen, wird verschwendet.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5428;
UPDATE `locales_npc_text` SET `ID` = 5429,`Text0_0_loc3` = 'Ihr sucht nach größerem Wissen. Tepa ist mit solchem Wissen gesegnet. Sie sollte irgendwo in der Nähe sein...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5429;
UPDATE `locales_npc_text` SET `ID` = 5430,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5430;
UPDATE `locales_npc_text` SET `ID` = 5431,`Text0_0_loc3` = 'Ich freue mich, Eure Bekanntschaft zu machen. Kann ich Euch helfen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5431;
UPDATE `locales_npc_text` SET `ID` = 5432,`Text0_0_loc3` = 'Ich bin kenntnisreich, aber ich bin kein Experte. Wisst Ihr, mit wem Ihr sprechen solltet? Rhiannon! Sie ist nicht weit weg. Fragt in Unterstadt nach ihr.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5432;
UPDATE `locales_npc_text` SET `ID` = 5433,`Text0_0_loc3` = 'So... Ihr möchtet also Schneider werden? Sehr gut, wir werden sehen, ob Ihr das Zeug dazu habt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5433;
UPDATE `locales_npc_text` SET `ID` = 5434,`Text0_0_loc3` = 'Wir bekommen hier draußen nicht viel Besuch. Kann ich Euch für etwas interessieren?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5434;
UPDATE `locales_npc_text` SET `ID` = 5435,`Text0_0_loc3` = 'Ich bin auch nur ein Schüler. Rhiannon ist die Expertin. Sie ist bei den Kisten dort drüben. Von ihr könnt Ihr sehr viel lernen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5435;
UPDATE `locales_npc_text` SET `ID` = 5436,`Text0_0_loc3` = 'Ja, ich glaube, Ihr habt Potenzial. Vielleicht kann ich Euch das eine oder andere beibringen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5436;
UPDATE `locales_npc_text` SET `ID` = 5437,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Es gibt keine bessere Methode, um den Kopf frei zu bekommen, als zu weben.' WHERE `locales_npc_text`.`ID` = 5437;
UPDATE `locales_npc_text` SET `ID` = 5438,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Meine Kenntnisse werden immer noch erweitert und Euer Wissen übersteigt das meine. Wenn Ihr mehr lernen wollt, müsst Ihr mit Me''lynn sprechen. Sie wird Euch leiten.' WHERE `locales_npc_text`.`ID` = 5438;
UPDATE `locales_npc_text` SET `ID` = 5439,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5439;
UPDATE `locales_npc_text` SET `ID` = 5440,`Text0_0_loc3` = 'He, Mann, was kann ich für Euch tun?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5440;
UPDATE `locales_npc_text` SET `ID` = 5441,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5441;
UPDATE `locales_npc_text` SET `ID` = 5442,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5442;
UPDATE `locales_npc_text` SET `ID` = 5443,`Text0_0_loc3` = 'Ich fürchte, dass ich nicht mehr sanft oder bescheiden bin. Das Alter und zahllose Schlachten haben meine Haut und Seele verhärtet. Vielleicht hat mich Cairne daher nach Sonnenfels geschickt... die sanften Winde und die warme Sonne lindern den Schmerz.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5443;
UPDATE `locales_npc_text` SET `ID` = 5453,`Text0_0_loc3` = 'Seid gegrüßt. Unsere Fledermäuse sind gut ausgebildet und wohl genährt. Ihr werdet keine schnelleren finden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5453;
UPDATE `locales_npc_text` SET `ID` = 5454,`Text0_0_loc3` = 'Wie kann ich zu Diensten sein?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5454;
UPDATE `locales_npc_text` SET `ID` = 5455,`Text0_0_loc3` = 'Seid gegrüßt und willkommen. Zu lächeln fällt mir nicht leicht, aber glaubt mir, ich bin erfreut, Euch dienen zu können.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5455;
UPDATE `locales_npc_text` SET `ID` = 5473,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Der Hippogryph ist ein nobles Tier, stolz und schnell. Es ist eine Ehre, ihn zu trainieren, und er kann seinen Reiter schnell über große Entfernungen transportieren.' WHERE `locales_npc_text`.`ID` = 5473;
UPDATE `locales_npc_text` SET `ID` = 5474,`Text0_0_loc3` = 'Um unsere Verbindung zum Festland aufrechtzuerhalten, haben wir Hippogryphen, die regelmäßig zwischen Rut''theran und Dunkelküste hin und her fliegen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5474;
UPDATE `locales_npc_text` SET `ID` = 5475,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die Fische vor der Küste von Teldrassil sind sehr groß und robust. Man behauptet, dass das schnelle Wachstum unseres neuen Weltenbaums die Ursache sei.' WHERE `locales_npc_text`.`ID` = 5475;
UPDATE `locales_npc_text` SET `ID` = 5476,`Text0_0_loc3` = 'Obwohl die Gewässer der Dunkelküste gefährlicher als die von Teldrassil sind, finden viele Fischer dort ihr Auskommen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5476;
UPDATE `locales_npc_text` SET `ID` = 5477,`Text0_0_loc3` = 'Es ist mir eine Ehre, Abenteurern zu dienen, denn sie sind zukünftige Helden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5477;
UPDATE `locales_npc_text` SET `ID` = 5479,`Text0_0_loc3` = 'Die schnellsten Greife diesseits vom Nistgipfel. Wollt Ihr''s mal versuchen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5479;
UPDATE `locales_npc_text` SET `ID` = 5480,`Text0_0_loc3` = 'Am nördlichen Dock könnt Ihr ein Schiff nehmen, das Euch nach Rut''theran und Darnassus bringt. Am südlichen Dock könnt Ihr eine Passage über das große Meer zum Hafen von Sturmwind buchen. Beim westlichen Dock am Ende des Stegs findet Ihr ein Schiff, das Euch zur Azurmythosinsel nahe der Exodar bringt. Ich wünsche Euch eine sichere Reise!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5480;
UPDATE `locales_npc_text` SET `ID` = 5481,`Text0_0_loc3` = 'Dieses Dock hier? Tja, Ihr könnt das Schiff kriegen, das zu diesem Nachtelfen-Ort fährt. Wie war der Name? Ähm, Auberdine, glaube ich. Ich bin mir nicht ganz sicher, ich belade und entlade nur Waren.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5481;
UPDATE `locales_npc_text` SET `ID` = 5494,`Text0_0_loc3` = 'Diese Fässer zur Ausgrabungsstätte zu bringen ist harte Arbeit, aber es gibt so viele, die davon abhängig sind, dass wir das Sprengpulver dorthin bringen.$B$BWir müssen alles in unserer Macht Stehende gegen die Dunkeleisenzwerge tun!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5494;
UPDATE `locales_npc_text` SET `ID` = 5495,`Text0_0_loc3` = 'Meine Windreiter sind dafür ausgebildet, schnell durch die heiße Luft des Brachlandes zu fliegen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5495;
UPDATE `locales_npc_text` SET `ID` = 5496,`Text0_0_loc3` = 'Das Brachland mit seiner heißen Sonne und feindlichen Bewohnern ist der perfekte Ort, um Eure Kraft und Seelenstärke zu testen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5496;
UPDATE `locales_npc_text` SET `ID` = 5497,`Text0_0_loc3` = 'Seid gegrüßt. Wenn Ihr wegen Lederwaren hier seid, werdet Ihr feststellen, dass meine Waren gut gearbeitet sind.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5497;
UPDATE `locales_npc_text` SET `ID` = 5498,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5498;
UPDATE `locales_npc_text` SET `ID` = 5500,`Text0_0_loc3` = 'Wenn man auf dem Rücken eines Windreiters reitet, sieht man, dass sein Ruf von Kraft und Tempo wohl verdient ist.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5500;
UPDATE `locales_npc_text` SET `ID` = 5501,`Text0_0_loc3` = 'Das hier ist ein gefährliches Geschäft!$B$BMiran hat sich gerade mit einer Lieferung auf den Weg gemacht. Er sollte aber in ein paar Minuten zurück sein.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5501;
UPDATE `locales_npc_text` SET `ID` = 5513,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5513;
UPDATE `locales_npc_text` SET `ID` = 5515,`Text0_0_loc3` = 'Seht Euch mein Fleisch an. Ihr solltet nicht ohne ausreichend Proviant ins Brachland ziehen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5515;
UPDATE `locales_npc_text` SET `ID` = 5516,`Text0_0_loc3` = 'Man hat erst gelebt, wenn man vom Rücken eines Windreiters auf die Welt hinabgesehen hat.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5516;
UPDATE `locales_npc_text` SET `ID` = 5517,`Text0_0_loc3` = 'Ihr könnt an diesem Dock eine Passage nach Theramore buchen. Beherzigt meine Worte: Das Bereisen dieser Länder ist für Menschen der Allianz nicht gerade sicher. Achtet auf Eure Schritte und auf das, was hinter Euch vorgeht.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5517;
UPDATE `locales_npc_text` SET `ID` = 5519,`Text0_0_loc3` = 'Schönen guten Tag.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5519;
UPDATE `locales_npc_text` SET `ID` = 5520,`Text0_0_loc3` = 'Ihr seid meinem Kenntnisstand weit überlegen. Vielleicht kann Euch Georgio Bolero, der Meister von Duncans Textilien um die Ecke, beim Verbessern Eurer Fertigkeiten helfen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5520;
UPDATE `locales_npc_text` SET `ID` = 5522,`Text0_0_loc3` = 'Sprecht mit Lawrence Schneider bei Duncans Textilien, links um die Ecke. Er ist bewandert im Umgang mit Anfängern.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5522;
UPDATE `locales_npc_text` SET `ID` = 5523,`Text0_0_loc3` = 'Ihr habt Euren Wert wohl gezeigt. Eure Unterweisung kann nun fortgesetzt werden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5523;
UPDATE `locales_npc_text` SET `ID` = 5524,`Text0_0_loc3` = 'Ah, dieses Gewebe aus Darnassus ist das Feinste!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5524;
UPDATE `locales_npc_text` SET `ID` = 5525,`Text0_0_loc3` = 'Na, Ihr seid ein kleiner Besserwisser, was? Ihr wisst mehr als ich, das kann ich Euch sagen! Bildet Euch darauf nichts ein, aber wenn Ihr mehr lernen wollt, dann sprecht mit Georgio Bolero in Sturmwind. Man sagt, dass er viel Ahnung von Schneiderei hat.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5525;
UPDATE `locales_npc_text` SET `ID` = 5527,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5527;
UPDATE `locales_npc_text` SET `ID` = 5528,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5528;
UPDATE `locales_npc_text` SET `ID` = 5529,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Hört Ihr den Ruf der Jagd, $C? Ihr müsst genau hinhören, denn der Ruf kommt aus Eurem Inneren. Er kommt tief aus dem Inneren von uns allen.' WHERE `locales_npc_text`.`ID` = 5529;
UPDATE `locales_npc_text` SET `ID` = 5530,`Text0_0_loc3` = 'Ich habe viele der Dinge hergestellt, die Ihr in dem Laden seht. Was meint Ihr? Wählt Eure Antwort sorgfältig, Euer Leben könnte davon abhängen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5530;
UPDATE `locales_npc_text` SET `ID` = 5531,`Text0_0_loc3` = 'Ihr müsst von jemandem lernen, der mehr weiß als ich. Sprecht mit Josef Gregorian in Unterstadt. Na los, was macht Ihr immer noch hier?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5531;
UPDATE `locales_npc_text` SET `ID` = 5533,`Text0_0_loc3` = 'Wie? Warum belästigt Ihr mich? He, ich wette, Ihr könnt nicht einmal eine qualitativ hochwertige Robe herstellen! Sprecht mit Snang und kommt zurück, wenn Ihr meines Talents würdig seid!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5533;
UPDATE `locales_npc_text` SET `ID` = 5534,`Text0_0_loc3` = 'Wurde auch Zeit, dass Ihr mal ein paar Resultate vorzuweisen habt! Jetzt werde ich Euch mal Resultate zeigen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5534;
UPDATE `locales_npc_text` SET `ID` = 5535,`Text0_0_loc3` = 'Man sagt, ein Stich zur rechten Zeit spart... ähm, elf...? Ah, ich weiß es nicht mehr. Sowieso ein dummes Sprichwort.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5535;
UPDATE `locales_npc_text` SET `ID` = 5536,`Text0_0_loc3` = 'Nicht schlecht... für einen Amateur! Trotzdem wisst Ihr schon so viel wie ich. Wenn Ihr mehr lernen wollt, müsst Ihr mit Josef Gregorian in Unterstadt sprechen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5536;
UPDATE `locales_npc_text` SET `ID` = 5539,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5539;
UPDATE `locales_npc_text` SET `ID` = 5540,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Was für ein toller Tag! Er inspiriert mich zu einem neuen Design.' WHERE `locales_npc_text`.`ID` = 5540;
UPDATE `locales_npc_text` SET `ID` = 5541,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Eure Talente sind überragend! Ihr seid wahrlich begabt. Um Eure Suche nach Wissen fortzusetzen, solltet Ihr mit Georgio Bolero in Sturmwind sprechen. Er wird hoch geachtet und kann Eure Ausbildung voranbringen.' WHERE `locales_npc_text`.`ID` = 5541;
UPDATE `locales_npc_text` SET `ID` = 5543,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5543;
UPDATE `locales_npc_text` SET `ID` = 5544,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr habt Eure Sache gut gemacht. Ich bin stolz darauf, Euch meinen Schüler nennen zu dürfen!' WHERE `locales_npc_text`.`ID` = 5544;
UPDATE `locales_npc_text` SET `ID` = 5545,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Willkommen in unserem Zelt. Kann ich Euch helfen?' WHERE `locales_npc_text`.`ID` = 5545;
UPDATE `locales_npc_text` SET `ID` = 5546,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Euer Wissen ist groß. Ihr müsst von jemandem lernen, der weiser ist als ich. Es werden viele Geschichten über die Weisheit von Josef Gregorian erzählt. Ihr findet ihn in Unterstadt.' WHERE `locales_npc_text`.`ID` = 5546;
UPDATE `locales_npc_text` SET `ID` = 5549,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5549;
UPDATE `locales_npc_text` SET `ID` = 5551,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Sind die Muster nicht wunderschön? Jedes spricht mit seiner eigenen Stimme.' WHERE `locales_npc_text`.`ID` = 5551;
UPDATE `locales_npc_text` SET `ID` = 5552,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Mein Wissen ist begrenzt, aber ich lerne immer noch. Es ist an der Zeit für Euch, weiterzuziehen. Sucht Josef Gregorian in Unterstadt auf und sagt ihm, dass Ihr bereit seid.' WHERE `locales_npc_text`.`ID` = 5552;
UPDATE `locales_npc_text` SET `ID` = 5554,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5554;
UPDATE `locales_npc_text` SET `ID` = 5555,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5555;
UPDATE `locales_npc_text` SET `ID` = 5556,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die Schneiderei kann sehr nützlich sein. Ich musste mal meinen Arm wieder annähen.' WHERE `locales_npc_text`.`ID` = 5556;
UPDATE `locales_npc_text` SET `ID` = 5557,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr habt Euch hochgearbeitet und es ist an der Zeit, dass Ihr den Chef seht. Geht zu Josef und sagt ihm, dass Ihr bereit seid.' WHERE `locales_npc_text`.`ID` = 5557;
UPDATE `locales_npc_text` SET `ID` = 5559,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wie kann ich meine Geheimnisse mit einem Anfänger teilen? Sprecht zuerst mit Victor Ward.' WHERE `locales_npc_text`.`ID` = 5559;
UPDATE `locales_npc_text` SET `ID` = 5560,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Gut, Ihr zeigt Entschlossenheit! Das gefällt mir. Also, dann fangen wir mal an!' WHERE `locales_npc_text`.`ID` = 5560;
UPDATE `locales_npc_text` SET `ID` = 5561,`Text0_0_loc3` = 'Ja, ich führe diesen Laden jetzt schon eine ganze Zeit. Die Geschäfte gingen in letzter Zeit ziemlich gut. Es kommen viele Abenteurer hier durch...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5561;
UPDATE `locales_npc_text` SET `ID` = 5562,`Text0_0_loc3` = 'Ich kenne nur eine einzige Person, die mehr über die Kunst der Schneiderei weiß als ich. Das Letzte, was ich gehört habe, war, dass man diese Person auf der Insel Theramore finden kann. Diese Person solltet Ihr aufsuchen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5562;
UPDATE `locales_npc_text` SET `ID` = 5564,`Text0_0_loc3` = 'Ihr braucht mehr Ausbildung, bevor ich mein Wissen mit Euch teilen kann, fürchte ich.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5564;
UPDATE `locales_npc_text` SET `ID` = 5565,`Text0_0_loc3` = 'Gut! Ich sehe, dass meine Bemühungen nicht vergebens sein werden!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5565;
UPDATE `locales_npc_text` SET `ID` = 5566,`Text0_0_loc3` = 'Stoff ist ganz in Ordnung. Persönlich arbeite ich aber lieber mit menschlicher Haut.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5566;
UPDATE `locales_npc_text` SET `ID` = 5567,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5567;
UPDATE `locales_npc_text` SET `ID` = 5569,`Text0_0_loc3` = 'Ich werde Euch anweisen, wenn die Zeit gekommen ist... und diese Zeit ist nicht jetzt. Ihr müsst zuerst noch mehr lernen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5569;
UPDATE `locales_npc_text` SET `ID` = 5570,`Text0_0_loc3` = 'Gut! Ich sehe, dass meine Bemühungen nicht vergebens sein werden!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5570;
UPDATE `locales_npc_text` SET `ID` = 5571,`Text0_0_loc3` = 'Ich habe Könige eingekleidet. Mit der richtigen Ausbildung könnt Ihr das auch.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5571;
UPDATE `locales_npc_text` SET `ID` = 5573,`Text0_0_loc3` = 'Ihr seid weit gereist. Es gibt noch eine weitere Aufgabe, die Ihr erfüllen müsst...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5573;
UPDATE `locales_npc_text` SET `ID` = 5574,`Text0_0_loc3` = 'Ich kann Euch erst helfen, wenn Ihr mehr Grundlagen gelernt habt. Kommt zu mir zurück, wenn Ihr bereit seid.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5574;
UPDATE `locales_npc_text` SET `ID` = 5575,`Text0_0_loc3` = 'Der Ort hier ist ein wenig zugig... aber was kümmert es mich? Ich bin tot.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5575;
UPDATE `locales_npc_text` SET `ID` = 5577,`Text0_0_loc3` = 'Ihr macht Euch sehr gut in der Kunst der Schneiderei, muss ich sagen. Tja, dann lasst uns mal sehen, wie gut Ihr WIRKLICH seid...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5577;
UPDATE `locales_npc_text` SET `ID` = 5578,`Text0_0_loc3` = 'Wer hat Euch hergeschickt? Ihr seid noch nicht bereit für meinen Unterricht! Man wird Euch sagen, wann Ihr bereit seid.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5578;
UPDATE `locales_npc_text` SET `ID` = 5579,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich genieße es ungemein, hier draußen zu sitzen und den Geräuschen des großen Meeres zu lauschen. Oh, was? Ja, es gibt ein Schiff, das Euch zum Hafen von Menethil bringen wird. Es legt von Zeit zu Zeit hier an.' WHERE `locales_npc_text`.`ID` = 5579;
UPDATE `locales_npc_text` SET `ID` = 5580,`Text0_0_loc3` = 'Denkt an meine Worte! Ihr werdet nirgendwo in den östlichen Königreichen Greife finden, die schneller sind als die hier in Thelsamar!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5580;
UPDATE `locales_npc_text` SET `ID` = 5581,`Text0_0_loc3` = 'Wenn Ihr mich fragt, gibt es keinen besseren Beruf als Minenarbeiter. Und wenn Ihr mich fragt, gibt es keinen besseren Minenarbeiter als einen Zwerg!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5581;
UPDATE `locales_npc_text` SET `ID` = 5582,`Text0_0_loc3` = 'Die Zwerge der Wildhämmer haben vielleicht schnelle Rösser, aber können diese Nistgipfelgreife die Hitze der großen Schmiede aushalten? Das glaube ich nicht!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5582;
UPDATE `locales_npc_text` SET `ID` = 5583,`Text0_0_loc3` = '$GDer beste Minenarbeiter:Die beste Minenarbeiterin; hat $Gseine:ihre; Hacke fest im Griff, ein derbes Herz in $Gseiner:ihrer; Brust und dicken, schwarzen Staub in $Gseinem:ihrem; Haar.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5583;
UPDATE `locales_npc_text` SET `ID` = 5584,`Text0_0_loc3` = 'Die stabile Kiste steht in der Mitte des Sägewerks, umgeben von Trümmern und äußerst eifrigen Anhängern des Scharlachroten Kreuzzugs.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5584;
UPDATE `locales_npc_text` SET `ID` = 5593,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Der Bär wandert einen Pfad entlang, der nahe dem Steinkrallenpfad liegt, westlich vom Mystralsee und dem Außenposten der Allianz, der Silberwindzuflucht. Er ist zwar die einfachste der drei Kreaturen, die Euch auf der Jagd im Eschental begegnen werden, aber man sollte ihn nicht unterschätzen. Seine Anwesenheit ist eine beständige Bedrohung für Reisende, die sich zwischen dem Eschental und dem Steinkrallengebirge bewegen.' WHERE `locales_npc_text`.`ID` = 5593;
UPDATE `locales_npc_text` SET `ID` = 5594,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die stärkste aller Bestien, die Teil der Jagd im Eschental ist, ist Scharfkralle, dem die selbsternannten Wächter dieses Landes, die Nachtelfen, erlauben frei umherzuziehen. Scharfkralle sucht südlich von hier nach Beute und erfreut sich daran, ahnungslose und unwissende Reisende zu zerreißen.$B$BSolch ein Biest zur Strecke zu bringen, ist das Höchste, das ein Jäger tun kann, $N. Ich wünsche Euch Glück bei der Jagd nach dieser Kreatur!' WHERE `locales_npc_text`.`ID` = 5594;
UPDATE `locales_npc_text` SET `ID` = 5595,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Dieser Nachtsäbler verhält sich angeblich in der Anwesenheit von Nachtelfen und ihren Verbündeten friedlich, was ihn zu einem noch würdigeren Gegner macht. Shattumbras Jagdroute verläuft vom Rajenbaum im Westen bis hin zu Ländern, die weiter westlich von hier liegen. Ihr Fell hat die Farbe von Pech und ihre Geschwindigkeit darf nicht unterschätzt werden.' WHERE `locales_npc_text`.`ID` = 5595;
UPDATE `locales_npc_text` SET `ID` = 5613,`Text0_0_loc3` = 'Willkommen $C... willkommen an der Frontlinie bei unserer Eroberung des Eschentals! Wenn Ihr etwas zu tun haben wollt, dann seid Ihr am richtigen Ort. Ich suche immer nach tauglichen Individuen, die dabei helfen, der Horde Ruhm zu bringen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5613;
UPDATE `locales_npc_text` SET `ID` = 5633,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5633;
UPDATE `locales_npc_text` SET `ID` = 5653,`Text0_0_loc3` = 'Wenn Ihr etwas über das Kommen und Gehen der Karawanen und Händler hier im Splitterholzposten wissen wollt, dann bin ich zu Eurem Glück der Orc, nach dem Ihr sucht. Aber zu Eurem Unglück mag ich keine Menschen.$B$BWenn Ihr also etwas wollt, dann macht es schnell.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5653;
UPDATE `locales_npc_text` SET `ID` = 5654,`Text0_0_loc3` = 'Die Elemente sprechen mit uns, $C. Sie teilen mit uns Wissen, das wir lernen müssen, und sie teilen mit uns Geheimnisse, die geheim gehalten werden müssen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5654;
UPDATE `locales_npc_text` SET `ID` = 5673,`Text0_0_loc3` = 'Durch rigoroses Abtrainieren musste ich vielen Schülern das abgewöhnen, was sie bisher gelernt hatten, damit ich sie von neuem unterrichten konnte. Der Dienst, den ich anbiete, ist jedes Mal schwieriger durchzuführen. Aus diesem Grunde wird er mit jeder Umschulung teurer. Wünscht Ihr, dass ich Euch die Talente nehme, die Ihr erlernt habt?',`Text0_1_loc3` = 'Durch rigoroses Abtrainieren musste ich vielen Schülern das abgewöhnen, was sie bisher gelernt hatten, damit ich sie von neuem unterrichten konnte. Der Dienst, den ich anbiete, ist jedes Mal schwieriger durchzuführen. Aus diesem Grunde wird er mit jeder Umschulung teurer. Wünscht Ihr, dass ich Euch die Talente nehme, die Ihr erlernt habt?' WHERE `locales_npc_text`.`ID` = 5673;
UPDATE `locales_npc_text` SET `ID` = 5674,`Text0_0_loc3` = 'Wenn Ihr es wünscht, dann liegt es neben der Ausbildung in meiner Macht, das Wissen über Eure Talente aus Eurem Gehirn zu löschen. Doch ich warne Euch: Diese Prozedur wird mit jeder Durchführung schwieriger und daher auch teurer.',`Text0_1_loc3` = 'Wenn Ihr es wünscht, dann liegt es neben der Ausbildung in meiner Macht, das Wissen über Eure Talente aus Eurem Gehirn zu löschen. Doch ich warne Euch: Diese Prozedur wird mit jeder Durchführung schwieriger und daher auch teurer.' WHERE `locales_npc_text`.`ID` = 5674;
UPDATE `locales_npc_text` SET `ID` = 5693,`Text0_0_loc3` = 'Das Arkane verdirbt nur die Schwachen. Darum übt unbedingt weiter, sonst ereilt Euch ein ähnliches Schicksal.',`Text0_1_loc3` = 'Das Arkane verdirbt nur die Schwachen. Darum übt unbedingt weiter, sonst ereilt Euch ein ähnliches Schicksal.' WHERE `locales_npc_text`.`ID` = 5693;
UPDATE `locales_npc_text` SET `ID` = 5694,`Text0_0_loc3` = 'Willkommen, $G mein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BSchon bald wird die Zeit kommen, da sich der Smaragdgrüne Traum Eurer bemächtigen wird. Dann werdet Ihr viele Jahre lang eine Art Winterschlaf halten und unter den reinsten aller Formen wandeln. Dann und dort werdet Ihr Eure endgültige Ausbildung erhalten.',`Text0_1_loc3` = 'Willkommen, $G mein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BSchon bald wird die Zeit kommen, da sich der Smaragdgrüne Traum Eurer bemächtigen wird. Dann werdet Ihr viele Jahre lang eine Art Winterschlaf halten und unter den reinsten aller Formen wandeln. Dann und dort werdet Ihr Eure endgültige Ausbildung erhalten.' WHERE `locales_npc_text`.`ID` = 5694;
UPDATE `locales_npc_text` SET `ID` = 5695,`Text0_0_loc3` = 'Was kann ich für Euch tun?',`Text0_1_loc3` = 'Was kann ich für Euch tun?' WHERE `locales_npc_text`.`ID` = 5695;
UPDATE `locales_npc_text` SET `ID` = 5696,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5696;
UPDATE `locales_npc_text` SET `ID` = 5713,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich will Euch nichts Böses. Ich spähe die Stacheleber aus und mache mir Notizen über ihre Aktivitäten. Meine Leute haben etwas Merkwürdiges bemerkt - eine Ausstrahlung oder Dunkelheit -, das aus diesem Bereich kommt. Ich bin gekommen, um das zu untersuchen, und was ich gefunden habe, ist recht verstörend.$B$BEs scheint einen untoten Diplomaten zu geben, der die Kunst des Arkanen äußerst gut beherrscht. Ich glaube nicht, dass er einer der Verlassenen ist... Ich glaube, es ist schlimmer.' WHERE `locales_npc_text`.`ID` = 5713;
UPDATE `locales_npc_text` SET `ID` = 5714,`Text0_0_loc3` = 'Ihr wisst genug, um gefährlich zu sein, aber für mich sieht es immer noch so aus, als würdet Ihr Ausbildung benötigen, $N.',`Text0_1_loc3` = 'Ihr wisst genug, um gefährlich zu sein, aber für mich sieht es immer noch so aus, als würdet Ihr Ausbildung benötigen, $N.' WHERE `locales_npc_text`.`ID` = 5714;
UPDATE `locales_npc_text` SET `ID` = 5715,`Text0_0_loc3` = 'Euer Wille ist nicht stark genug, um die Kräfte zu manipulieren, die wir kontrollieren, $C.',`Text0_1_loc3` = 'Euer Wille ist nicht stark genug, um die Kräfte zu manipulieren, die wir kontrollieren, $C.' WHERE `locales_npc_text`.`ID` = 5715;
UPDATE `locales_npc_text` SET `ID` = 5716,`Text0_0_loc3` = '$N, wie kann ich Eure Ausbildung in den Augen der Erdenmutter voranbringen?',`Text0_1_loc3` = '$N, wie kann ich Eure Ausbildung in den Augen der Erdenmutter voranbringen?' WHERE `locales_npc_text`.`ID` = 5716;
UPDATE `locales_npc_text` SET `ID` = 5717,`Text0_0_loc3` = 'Vielfältig sind die Wege der Erdenmutter. Mögen Eure Vorfahren über Euch wachen, $C.',`Text0_1_loc3` = 'Vielfältig sind die Wege der Erdenmutter. Mögen Eure Vorfahren über Euch wachen, $C.' WHERE `locales_npc_text`.`ID` = 5717;
UPDATE `locales_npc_text` SET `ID` = 5719,`Text0_0_loc3` = 'Es ist weise, dass Ihr Euch in Eurem Wunsch nach mehr Hexenmeisterwissen vor mir erniedrigt, $N.',`Text0_1_loc3` = 'Es ist weise, dass Ihr Euch in Eurem Wunsch nach mehr Hexenmeisterwissen vor mir erniedrigt, $N.' WHERE `locales_npc_text`.`ID` = 5719;
UPDATE `locales_npc_text` SET `ID` = 5720,`Text0_0_loc3` = 'Ich kann es mir nicht leisten, mit Euresgleichen meine Zeit zu verschwenden, $C.',`Text0_1_loc3` = 'Ich kann es mir nicht leisten, mit Euresgleichen meine Zeit zu verschwenden, $C.' WHERE `locales_npc_text`.`ID` = 5720;
UPDATE `locales_npc_text` SET `ID` = 5721,`Text0_0_loc3` = 'Ich kann schon allein aus der Art, wie Ihr dort steht, erkennen, dass Ihr $Gkein Krieger:keine Kriegerin; seid. $B$BWarum macht Ihr nicht etwas, das sicher ist, wie zum Beispiel ein Buch lesen.',`Text0_1_loc3` = 'Ich kann schon allein aus der Art, wie Ihr dort steht, erkennen, dass Ihr $Gkein Krieger:keine Kriegerin; seid. $B$BWarum macht Ihr nicht etwas, das sicher ist, wie zum Beispiel ein Buch lesen.' WHERE `locales_npc_text`.`ID` = 5721;
UPDATE `locales_npc_text` SET `ID` = 5722,`Text0_0_loc3` = 'Die Dunkelheit umgibt Euch nicht, $C. Hört mit dem Geschwätz auf und geht mir aus den Augen! Hinfort mit Euch!',`Text0_1_loc3` = 'Die Dunkelheit umgibt Euch nicht, $C. Hört mit dem Geschwätz auf und geht mir aus den Augen! Hinfort mit Euch!' WHERE `locales_npc_text`.`ID` = 5722;
UPDATE `locales_npc_text` SET `ID` = 5723,`Text0_0_loc3` = 'Ich fürchte, Euer Dunkelheit-Quotient ist nicht ausreichend, als dass sich noch länger mit Euch zu reden lohnt. Geht weiter bitte.',`Text0_1_loc3` = 'Ich fürchte, Euer Dunkelheit-Quotient ist nicht ausreichend, als dass sich noch länger mit Euch zu reden lohnt. Geht weiter bitte.' WHERE `locales_npc_text`.`ID` = 5723;
UPDATE `locales_npc_text` SET `ID` = 5724,`Text0_0_loc3` = 'Ihr seid $Gkein Krieger:keine Kriegerin;... Wie wollt Ihr mehr als einen Tag unter meiner Ausbildung bestehen? $B$B$GEin:Eine:c; $C, $Gder sich für einen Krieger hält:die sich für eine Kriegerin hält:c;. Ha!',`Text0_1_loc3` = 'Ihr seid $Gkein Krieger:keine Kriegerin;... Wie wollt Ihr mehr als einen Tag unter meiner Ausbildung bestehen? $B$B$GEin:Eine:c; $C, $Gder sich für einen Krieger hält:die sich für eine Kriegerin hält:c;. Ha!' WHERE `locales_npc_text`.`ID` = 5724;
UPDATE `locales_npc_text` SET `ID` = 5725,`Text0_0_loc3` = 'Ich sehe in Euch nicht den Geist des Kriegers. Ich kann Euch nichts beibringen, was Euch helfen würde, $C.',`Text0_1_loc3` = 'Ich sehe in Euch nicht den Geist des Kriegers. Ich kann Euch nichts beibringen, was Euch helfen würde, $C.' WHERE `locales_npc_text`.`ID` = 5725;
UPDATE `locales_npc_text` SET `ID` = 5733,`Text0_0_loc3` = 'Die Zerschmetterte Hand konnte Spione überall in die obere Schwarzfelszitadelle einschleusen. Obwohl die Informationen nur langsam kamen, haben wir Entdeckungen gemacht, die nicht ignoriert werden können.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5733;
UPDATE `locales_npc_text` SET `ID` = 5734,`Text0_0_loc3` = 'Es scheint, als wäre der Einfluss des schwarzen Drachenschwarms gewachsen und über die Grenzen der brennenden Steppe hinaus bis in das Königreich Sturmwind gelangt! Der Thronräuber wurde inzwischen demaskiert und vertrieben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5734;
UPDATE `locales_npc_text` SET `ID` = 5735,`Text0_0_loc3` = 'So ist es. Die Brutmutter des schwarzen Drachenschwarms hat Illusion und Betrügerei angewendet, um sich als Mitglied des königlichen Hofes zu verkleiden. Und was noch schlimmer ist, sie übernahm die Kontrolle über den derzeitigen Hochlord von Sturmwind, Bolvar Fordragon.$B$BKein Wunder, dass solche närrischen militärischen Entscheidungen gefällt wurden. Die in letzter Zeit erfolgten Bewegungen der Streitkräfte der Allianz hatten uns sehr verwundert. Wir wissen jetzt, dass der schwarze Drachenschwarm versucht hatte, das Reich zu teilen - es von Innen zu zerstören.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5735;
UPDATE `locales_npc_text` SET `ID` = 5736,`Text0_0_loc3` = '<Thrall lacht.>$B$BIhr müsst noch so viel lernen, $R.$B$BIhr seid zu jung, um Euch an die Kriege und das Blutvergießen zu erinnern, das weit in der Vergangenheit liegt. Zu jung, um Euch an die Bürde des Fluchs zu erinnern, die von unseren Leuten getragen wird.$B$BUnsere Position gegenüber der Allianz ist im Moment recht angreifbar. Scharmützel und kleinere Kämpfe finden statt, aber es herrscht Frieden - etwas, das unsere Leute seit Jahrtausenden nicht mehr kannten. Würdet Ihr das aufgeben und uns wieder in die Klauen des Chaos stürzen, nur diesmal gegen einen neuen Feind? Einer, der erst ruhen wird, wenn wir alle vernichtet sind?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5736;
UPDATE `locales_npc_text` SET `ID` = 5737,`Text0_0_loc3` = 'Nein, $N, das habt Ihr nicht. Ich gebe Euch aber keine Schuld, denn Eure Gedanken gleichen den meinen, als ich noch jung war. Ungestüm, rebellisch und häufig ohne einen zusammenhängenden Gedanken! Eigenschaften, die ich mir nicht mehr leisten kann.$B$BAber genug gejammert. $R, ich habe eine Aufgabe für Euch, eine, die Ihr vielleicht nicht überleben werdet.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5737;
UPDATE `locales_npc_text` SET `ID` = 5738,`Text0_0_loc3` = '<Thrall lacht.>$B$BDas merke ich mir... Und jetzt passt auf, $N. Die Brutmutter des schwarzen Drachenschwarms, Onyxia, wurde von ihrer Machtposition in Sturmwind verjagt. Sie ist entkommen und zu ihrem Unterschlupf in den Düstermarschen geflüchtet.$B$BWir dürfen nicht zulassen, dass sie so nahe an unseren großen Städten lebt. Wir müssen zuschlagen bevor wir geschlagen werden!$B$BIch will, dass sie vernichtet wird, $N.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5738;
UPDATE `locales_npc_text` SET `ID` = 5739,`Text0_0_loc3` = 'Willkommen, $Gmein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BWir sind die Bewahrer des Gleichgewichts, jetzt und immerdar, während Malfurion in Träumen liegt. Vergesst das niemals.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5739;
UPDATE `locales_npc_text` SET `ID` = 5740,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5740;
UPDATE `locales_npc_text` SET `ID` = 5753,`Text0_0_loc3` = 'Ich sehe hier am Hafen einige interessante Leute kommen und gehen. Passt auf Euch auf, $N. Man weiß nie genau, wem man vertrauen kann, bei den ganzen zwielichtigen Aktivitäten, die hier ablaufen...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5753;
