UPDATE `locales_npc_text` SET `ID` = 5773,`Text0_0_loc3` = 'In dieser Region des Steinkrallengebirges gibt es viele gefährliche Höhlen. Die Erforschung dieser Höhlen hält viele Abenteuer für die bereit, die ihr Leben für Ruhm und Ehre riskieren wollen.$B$BVielleicht sucht Ihr nach Reichtümern, die Ihr nur erreichen könnt, wenn Ihr Eurem Herz und Eurer Seele folgt, $C.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5773;
UPDATE `locales_npc_text` SET `ID` = 5793,`Text0_0_loc3` = 'Fisch sehr lecker. Ihr kommen lernen angeln? Fisch essen? Mmmmm...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5793;
