UPDATE `locales_npc_text` SET `ID` = 5798,`Text0_0_loc3` = 'Wir leiden vielleicht unter einer Dürre, aber an Fleisch herrscht kein Mangel! Mögt Ihr ein wenig in meinen Waren stöbern?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5798;
