UPDATE `locales_npc_text` SET `ID` = 5820,`Text0_0_loc3` = 'Meine Frage an Euch, $C, ist diese:$BAus welchem der Aspekte, die von den Titanen geschaffen wurden, wurde die schändliche Kreatur erschaffen, die als Todesschwinge bekannt ist?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5820;
