UPDATE `locales_npc_text` SET `ID` = 5822,`Text0_0_loc3` = 'Beantwortet mir folgende Frage, $N:$BWas für eine Kreatur war der Lichkönig, bevor er die Seuche über Lordaeron brachte?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5822;
