UPDATE `locales_npc_text` SET `ID` = 5834,`Text0_0_loc3` = 'Die Thoriumbruderschaft besitzt das Wissen, das erforderlich ist, um die hochheiligen Waffen und Rüstungsteile des Feuerlords herzustellen, aber uns fehlen die Leute, um die Materialien zu beschaffen, die für die Herstellung notwendig sind.$B$BIch, Lokhtos Düsterfeilsch, wurde von der Bruderschaft ermächtigt, die erhabenen Anleitungen für Materialien einzutauschen, die vom geschmolzenen Kern gesammelt wurden.$B$BPräsentiert Euer Angebot, $R.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5834;
UPDATE `locales_npc_text` SET `ID` = 5835,`Text0_0_loc3` = 'Ihr möchtet Eure Dämonen mächtiger machen? Das wird Euch einiges kosten, aber Ihr seid hier richtig.',`Text0_1_loc3` = 'Ihr möchtet Eure Dämonen mächtiger machen? Das wird Euch einiges kosten, aber Ihr seid hier richtig.' WHERE `locales_npc_text`.`ID` = 5835;
