UPDATE `locales_npc_text` SET `ID` = 5981,`Text0_0_loc3` = 'Wir sind alle Teile eines viel größeren Puzzles, $N. So wie Ihr ein Teil eines Puzzles zu seinem Ziel führt, so werden auch wir durch einen anderen geführt.$B$BUnd vielleicht werden die, die uns leiten, selbst von einer noch größeren Macht geleitet.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 5981;
