UPDATE `locales_npc_text` SET `ID` = 5983,`Text0_0_loc3` = 'Die gute alte Erma, sie kümmert sich jetzt schon seit Jahren um die hiesigen Tiere. Ich bin sicher, dass sie sich auch gern um alle kümmern wird, die Ihr ihr bringt. Ihr findet sie beim Gasthaus Zur Höhle des Löwen.',`Text0_1_loc3` = 'Die gute alte Erma, sie kümmert sich jetzt schon seit Jahren um die hiesigen Tiere. Ich bin sicher, dass sie sich auch gern um alle kümmern wird, die Ihr ihr bringt. Ihr findet sie beim Gasthaus Zur Höhle des Löwen.' WHERE `locales_npc_text`.`ID` = 5983;
UPDATE `locales_npc_text` SET `ID` = 5984,`Text0_0_loc3` = 'Jenova Steinschild drüben im Zwergendistrikt dürfte Euren Vorstellungen entsprechen. Sie kümmert sich um die Tiere der Jäger, während die bei ihrem Vater ausgebildet werden.',`Text0_1_loc3` = 'Jenova Steinschild drüben im Zwergendistrikt dürfte Euren Vorstellungen entsprechen. Sie kümmert sich um die Tiere der Jäger, während die bei ihrem Vater ausgebildet werden.' WHERE `locales_npc_text`.`ID` = 5984;
