UPDATE `locales_npc_text` SET `ID` = 6062,`Text0_0_loc3` = 'Dieses Monument ist all denen gewidmet, die beim Schutz von Sturmwind gefallen sind. Unsere Leute haben Unglaubliches durchgemacht, um ihre Freiheit zu bewahren und ihr Schicksal in die eigenen Hände zu nehmen. Hier erinnern wir uns an jedes einzelne Opfer, das unsere Bürger während des ersten Krieges gebracht haben, der unser Volk erschütterte.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6062;
