UPDATE `locales_npc_text` SET `ID` = 6093,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Das Blut unserer Feinde soll das Schlachtfeld tränken. Die großen Elementare werden unserem Ruf folgen und alles zerschmettern, was sich uns in den Weg stellt.' WHERE `locales_npc_text`.`ID` = 6093;
UPDATE `locales_npc_text` SET `ID` = 6095,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Gute Arbeit, Soldat! Ich muss zum Stützpunkt zurück! Gebt Ihr mir Deckung?' WHERE `locales_npc_text`.`ID` = 6095;
UPDATE `locales_npc_text` SET `ID` = 6096,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wurde auch Zeit, dass mal jemand kommt. Was hat Euch so lange aufgehalten? Ach, egal. Gebt mir Deckung, Soldat. Ich muss zum Stützpunkt zurück!' WHERE `locales_npc_text`.`ID` = 6096;
UPDATE `locales_npc_text` SET `ID` = 6098,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Es wartet noch viel Arbeit auf Euch, $N. Kämpft weiter gegen unsere Feinde!' WHERE `locales_npc_text`.`ID` = 6098;
UPDATE `locales_npc_text` SET `ID` = 6099,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ja, $N. Die Elemente rühren sich. Schon bald werden wir ihre Macht über unseren Feinden entfesseln. Bei unseren Schlägen wird ihnen Hören und Sehen vergehen!' WHERE `locales_npc_text`.`ID` = 6099;
UPDATE `locales_npc_text` SET `ID` = 6100,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich habe Kontakt hergestellt, $N. Es ist Lokholar der Eislord. Er erachtet unsere Sache als würdig - unsere Opfer an die Erde als ausreichend. Er wird bald kommen... macht Euch bereit.' WHERE `locales_npc_text`.`ID` = 6100;
UPDATE `locales_npc_text` SET `ID` = 6101,`Text0_0_loc3` = 'Ich brauche ein paar gute Flügelmänner, die mich sicher hier weg und zurück zum Stützpunkt bringen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6101;
