UPDATE `locales_npc_text` SET `ID` = 6107,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die Flotte ist bereit, $N. Ihr müsst dieses Signal nehmen und im östlichen Krater auf dem zentralen Schlachtfeld platzieren. Verteidigt das Signal gegen Angreifer, bis die Flotte die Koordinaten des Signals erfasst hat!' WHERE `locales_npc_text`.`ID` = 6107;
