UPDATE `locales_npc_text` SET `ID` = 6153,`Text0_0_loc3` = 'Wenn Ihr nach Informationen sucht, die der Zirkel des Cenarius irgendwo archiviert haben könnte, bin ich genau der richtige Ansprechpartner. Ihr müsst allerdings schon wissen, welche Informationen Ihr genau benötigt, damit ich Euch wirklich helfen kann.$B$BVersteht Ihr, wie das funktioniert? Das ist genial!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6153;
UPDATE `locales_npc_text` SET `ID` = 6154,`Text0_0_loc3` = 'Hört nicht auf die Herzen der Erde - die Antwort liegt im Inneren. Ihr kennt die Wahrheit.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6154;
UPDATE `locales_npc_text` SET `ID` = 6155,`Text0_0_loc3` = 'In diesen Wüsten lebt die Vergangenheit, $C. Wir müssen vorsichtig sein, damit wir sie nicht wecken.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6155;
UPDATE `locales_npc_text` SET `ID` = 6158,`Text0_0_loc3` = 'Ihr seid bereit für die allerletzte Aufgabe, $N. Die feurigen Runen, die in den Boden des geschmolzenen Kerns eingraviert wurden, leiten ins Reich des Feuers und sie sind für uns nicht nur ein Affront, sondern auch gleichzeitig eine Machtquelle für die Feuerlords. Sie müssen ausgelöscht werden!$B $BUm eine Rune zu löschen, braucht Ihr eine Phiole mit wässriger Quintessenz. Gießt das auf die Rune, um ihre Flamme zu löschen und somit nutzlos zu machen und den Feind zu schwächen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6158;
UPDATE `locales_npc_text` SET `ID` = 6159,`Text0_0_loc3` = 'Willkommen bei der Gildenregistratur von Unterstadt. Was wollt Ihr?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6159;
