UPDATE `locales_npc_text` SET `ID` = 6164,`Text0_0_loc3` = 'Ich glaub nicht, dass Ihr irgendwas lernen wollt, was ich Euch beibringen kann. $G Ein:Eine:C; $C sollte sich auch einen entsprechenden Lehrer suchen.',`Text0_1_loc3` = 'Ich glaub nicht, dass Ihr irgendwas lernen wollt, was ich Euch beibringen kann. $G Ein:Eine:C; $C sollte sich auch einen entsprechenden Lehrer suchen.' WHERE `locales_npc_text`.`ID` = 6164;
UPDATE `locales_npc_text` SET `ID` = 6165,`Text0_0_loc3` = 'Ihr seht aus wie jemand, in dessen Gegenwart ich auf meinen Geldbeutel achten sollte. Kann ich Euch mit etwas behilflich sein?',`Text0_1_loc3` = 'Ihr seht aus wie jemand, in dessen Gegenwart ich auf meinen Geldbeutel achten sollte. Kann ich Euch mit etwas behilflich sein?' WHERE `locales_npc_text`.`ID` = 6165;
UPDATE `locales_npc_text` SET `ID` = 6167,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6167;
UPDATE `locales_npc_text` SET `ID` = 6173,`Text0_0_loc3` = 'Man hat uns von Darnassus hergeschickt, um den Sturmlanzenklan in Zeiten der Not beizustehen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6173;
UPDATE `locales_npc_text` SET `ID` = 6174,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Mein Glaube leitet meine Aktionen, $R. Ich weiß, dass Ivus kommen wird.' WHERE `locales_npc_text`.`ID` = 6174;
UPDATE `locales_npc_text` SET `ID` = 6175,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich habe mich in dem Traum verloren. Ich spüre seine Lebenskraft, aber meine Kräfte sind noch nicht stark genug, um Kontakt zu ihm aufzunehmen.' WHERE `locales_npc_text`.`ID` = 6175;
UPDATE `locales_npc_text` SET `ID` = 6176,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ja. $N, ich habe den Waldlord gefunden. Wir müssen unsere Anstrengungen verdoppeln! Es werden weitere Kristalle benötigt!' WHERE `locales_npc_text`.`ID` = 6176;
UPDATE `locales_npc_text` SET `ID` = 6177,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Der Waldlord weiß um die Zerstörung in diesem Tal, $N. Bald müssen wir zum Kreis des Rufens. Macht Euch bereit!' WHERE `locales_npc_text`.`ID` = 6177;
UPDATE `locales_npc_text` SET `ID` = 6178,`Text0_0_loc3` = 'Dieser tollkühne Narr Mulverick hat mich abgeschossen. Ich fürchte, wenn ich es nicht zurück zum Stützpunkt schaffe, ist alles verloren! Mulverick und seine Truppe aus Kriegsreitern müssen aufgehalten werden!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6178;
UPDATE `locales_npc_text` SET `ID` = 6179,`Text0_0_loc3` = 'Ich kann es nicht glauben... Ich war auf einer routinemäßigen Aufklärungsmission in der zentralen DMZ, als plötzlich die Hölle losbrach. Dort wurde ich Zeuge, wie der Schwadronskommandant der Horde Mulverick sowohl Ichman als auch Slidore ausschaltete! Wut überkam mich... ich trennte mich von meinem Flügelmann und streckte diesen Angeber Mulverick nieder. Leider verletzte er meinen Greifen, bevor er zu Boden ging, und zwang mich ebenfalls hinunter. So fingen mich schließlich diese Wilden.$B$BIch muss zum Stützpunkt zurück! Hilfe!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6179;
UPDATE `locales_npc_text` SET `ID` = 6180,`Text0_0_loc3` = 'Ich hörte noch ihr Hohngelächter, als mein Greif zu Boden stürzte und verbrannte, $C.$B$B"Slidore, du stinkst!", riefen sie.$B$BNiemand spricht so mit Slidore! Helft mir hier raus, damit ich denen zeigen kann, wer hier der Stinker ist!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6180;
UPDATE `locales_npc_text` SET `ID` = 6193,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Dieser Wald kann einen ganz schön verwirren! Seid vorsichtig da draußen.' WHERE `locales_npc_text`.`ID` = 6193;
UPDATE `locales_npc_text` SET `ID` = 6194,`Text0_0_loc3` = 'Schöne Grüße an Euch und die Euren!',`Text0_1_loc3` = 'Schöne Grüße an Euch und die Euren!' WHERE `locales_npc_text`.`ID` = 6194;
UPDATE `locales_npc_text` SET `ID` = 6217,`Text0_0_loc3` = 'Unsere Einheiten sind zu ''Erfahren'' ausgebaut, aber ich habe nicht genug Vorräte, um sie zu ''Veteranen'' auszubauen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6217;
UPDATE `locales_npc_text` SET `ID` = 6218,`Text0_0_loc3` = 'Unsere Einheiten sind zu ''Veteranen'' ausgebaut, aber ich habe nicht genug Vorräte, um sie zu ''Champions'' auszubauen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6218;
UPDATE `locales_npc_text` SET `ID` = 6219,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6219;
UPDATE `locales_npc_text` SET `ID` = 6220,`Text0_0_loc3` = 'Ich habe genügend Vorräte, um unsere Truppen zu ''Erfahren'' auszubauen',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6220;
UPDATE `locales_npc_text` SET `ID` = 6222,`Text0_0_loc3` = 'Unsere Einheiten sind zu ''Champions'' ausgebaut, aber ich kann noch weitere Vorräte auftreiben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6222;
UPDATE `locales_npc_text` SET `ID` = 6223,`Text0_0_loc3` = 'Ich kann keine weiteren Vorräte unterbringen. Das, was ich habe, reicht aus.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6223;
UPDATE `locales_npc_text` SET `ID` = 6233,`Text0_0_loc3` = 'Seid Ihr hier, um den Umgang mit einer neuen Waffe zu lernen, oder nur, um meine Zeit zu vergeuden?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6233;
UPDATE `locales_npc_text` SET `ID` = 6235,`Text0_0_loc3` = 'Sagt mir, für welche Waffe Ihr Ausbildung sucht oder verschwindet.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6235;
UPDATE `locales_npc_text` SET `ID` = 6250,`Text0_0_loc3` = 'Ein fröhliches Winterhauchfest wünsch ich,... äh... $GJungchen:Mädchen;. Brave Kinder bekommen besondere, äh, Sonderkonditionen bei allen Verkäufern von Kokelwälder. Lecker.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6250;
UPDATE `locales_npc_text` SET `ID` = 6255,`Text0_0_loc3` = 'Ein leerer Magen marschiert nicht gern, und das ist die Wahrheit! Wenn Eure Offensive erfolgreich sein soll, sorgt dafür, dass Eure Truppen wohlgenährt und gut ausgerüstet sind.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6255;
UPDATE `locales_npc_text` SET `ID` = 6257,`Text0_0_loc3` = 'Einige sagen, dass Stärke und Tapferkeit das Wichtigste für den Erfolg einer Armee sind. Und das ist wahr! Doch braucht eine Armee auch Vorräte, um in guter Verfassung zu bleiben, und es ist meine Aufgabe, dafür zu sorgen, dass sie das bekommen, was sie benötigen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6257;
UPDATE `locales_npc_text` SET `ID` = 6273,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Seid gegrüßt, Soldat. Seid Ihr hier, um der Sache zu dienen?' WHERE `locales_npc_text`.`ID` = 6273;
UPDATE `locales_npc_text` SET `ID` = 6274,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Gut gemacht, Soldat. Noch ein paar mehr und unsere Ställe werden vor Reittieren überquellen, auf denen unsere Kavallerie in die Schlacht ziehen kann!' WHERE `locales_npc_text`.`ID` = 6274;
UPDATE `locales_npc_text` SET `ID` = 6275,`Text0_0_loc3` = 'He, Mann. Bei mir seid Ihr genau richtig, wenn Ihr lernen wollt, wie man eine neue Waffe führt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6275;
UPDATE `locales_npc_text` SET `ID` = 6276,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6276;
UPDATE `locales_npc_text` SET `ID` = 6277,`Text0_0_loc3` = 'Was willst du lernen, Mann?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6277;
UPDATE `locales_npc_text` SET `ID` = 6279,`Text0_0_loc3` = 'Es wäre mir eine Ehre, Euch im Umgang mit den Waffen zu unterweisen, die ich beherrsche, so Ihr die Fähigkeit, sie zu erlernen, in Euch tragt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6279;
UPDATE `locales_npc_text` SET `ID` = 6281,`Text0_0_loc3` = 'Wenn Ihr weitere Ausbildung sucht, werde ich Euch gerne sagen, an wen Ihr Euch wenden müsst. Welche Waffe interessiert Euch?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6281;
UPDATE `locales_npc_text` SET `ID` = 6282,`Text0_0_loc3` = 'Tod dem Sturmlanzenklan!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6282;
UPDATE `locales_npc_text` SET `ID` = 6284,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6284;
UPDATE `locales_npc_text` SET `ID` = 6286,`Text0_0_loc3` = 'Ich nehme an, Ihr seid hier, um Euch im Umgang mit einer neuen Waffe ausbilden zu lassen, ja? Beeilt Euch, ich bin sehr beschäftigt!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6286;
UPDATE `locales_npc_text` SET `ID` = 6287,`Text0_0_loc3` = 'Ich werde Euch den Weg weisen, sofern Ihr mir sagt, was Euch interessiert.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6287;
UPDATE `locales_npc_text` SET `ID` = 6288,`Text0_0_loc3` = '$N, es tut gut, Veteranen wie Euch in der Schlacht zu sehen! Ich muss Euch leider mitteilen, dass unsere Vorräte im Moment zu gering sind, um einen Bodenangriff gegen die Horde starten zu können.$B$BSprecht mit unserem Rüstmeister; er hat Missionen zur Vorratsbeschaffung für Euch.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6288;
UPDATE `locales_npc_text` SET `ID` = 6289,`Text0_0_loc3` = 'Seid Ihr gekommen, um Euch im Kampf mit Waffen ausbilden zu lassen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6289;
UPDATE `locales_npc_text` SET `ID` = 6290,`Text0_0_loc3` = 'Für welche Waffen interessiert Ihr Euch denn?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6290;
UPDATE `locales_npc_text` SET `ID` = 6291,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die Anmut, mit der ich kämpfe, kann ich Euch leider nicht beibringen, aber ich kann Euch in der Benutzung diverser Waffen unterweisen.' WHERE `locales_npc_text`.`ID` = 6291;
UPDATE `locales_npc_text` SET `ID` = 6292,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wenn es Euch lieber ist, kann Ich Euch den Weg zu den anderen Waffenmeistern weisen. Wofür genau interessiert Ihr Euch?' WHERE `locales_npc_text`.`ID` = 6292;
UPDATE `locales_npc_text` SET `ID` = 6293,`Text0_0_loc3` = 'Ihr wollt Euch im Umgang mit einer neuen Waffe ausbilden lassen, richtig? Dann steht da nicht rum und haltet Maulaffen feil, $GBursche:Mädel;!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6293;
UPDATE `locales_npc_text` SET `ID` = 6294,`Text0_0_loc3` = 'Welche Waffenfertigkeit wolltet Ihr erlernen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6294;
UPDATE `locales_npc_text` SET `ID` = 6295,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Hallo auch! Seid Ihr hier, um die Feinheiten im Umgang mit einer neuen Waffe zu erlernen?' WHERE `locales_npc_text`.`ID` = 6295;
UPDATE `locales_npc_text` SET `ID` = 6296,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr möchtet Euch also nicht von mir ausbilden lassen? Nun gut, gibt es noch andere Waffen, die Euch interessieren?' WHERE `locales_npc_text`.`ID` = 6296;
UPDATE `locales_npc_text` SET `ID` = 6313,`Text0_0_loc3` = 'Tod den Frostwölfen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6313;
UPDATE `locales_npc_text` SET `ID` = 6315,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6315;
UPDATE `locales_npc_text` SET `ID` = 6316,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die in Alterac beheimateten Widder sind lebenswichtig für die Einsatzfähigkeit unserer Kavallerie!' WHERE `locales_npc_text`.`ID` = 6316;
UPDATE `locales_npc_text` SET `ID` = 6317,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6317;
UPDATE `locales_npc_text` SET `ID` = 6333,`Text0_0_loc3` = 'Grüße, $N. Fasst Euch kurz, denn es gibt viel zu tun.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6333;
UPDATE `locales_npc_text` SET `ID` = 6334,`Text0_0_loc3` = 'Möge Cenarius Euch leiten, $C.$B$BIch bin mit meinen Schwestern hier in diesem verderbten Land, um eine äußerst wichtige Mission für den Zirkel des Cenarius zu erfüllen. Allerdings bitte ich darum, dass sich nur jene, die stark genug sind, an mich wenden - jemand Geringeren zu schicken, wäre schiere Dummheit.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6334;
UPDATE `locales_npc_text` SET `ID` = 6335,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Meine Schwestern und ich kamen aus dem Steinkrallengebirge herunter, um eine Tragödie aus der fernen Vergangenheit zu beenden. Leider sind wir nicht stark genug, um unsere Ziele allein zu erreichen, und suchen jetzt nach Sterblichen wie Euch, die uns helfen könnten.$B$BFalls Ihr stark genug seid, haben wir eine Menge zu besprechen, $C. Falls Ihr es nicht seid, dann werde ich zu Eurer eigenen Sicherheit nicht über die Details meiner Mission reden.' WHERE `locales_npc_text`.`ID` = 6335;
UPDATE `locales_npc_text` SET `ID` = 6336,`Text0_0_loc3` = 'Ich grüße Euch, $R.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6336;
UPDATE `locales_npc_text` SET `ID` = 6353,`Text0_0_loc3` = 'Ich brauche kein Heim, um zu wissen, dass ich in dieser Welt immer noch einen Platz habe, $C. Die Stämme werden das erkennen... alle werden es. Sie kämpfen gegeneinander, und wofür? Krumen, die die Geier übrig gelassen haben... Land, das von der Legion heimgesucht wurde... pah!$B$BDie Zentauren sollten stolz auf ihr Erbe sein... es ist ebenso edel wie das der Nachtelfen oder Orcs. Es wird langsam Zeit, dass wir uns auch dementsprechend verhalten!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6353;
UPDATE `locales_npc_text` SET `ID` = 6354,`Text0_0_loc3` = 'Endlich bin ich von der Verderbnis befreit, die mich einst an diesen Ort band...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6354;
UPDATE `locales_npc_text` SET `ID` = 6373,`Text0_0_loc3` = 'Vielen Dank für Eure Mühen, $N.$B$BPasst gut auf das Szepter auf. Möge es Euch bei Euren Kämpfen gegen das Böse, das in diesen Ort eingedrungen ist, helfen...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6373;
UPDATE `locales_npc_text` SET `ID` = 6375,`Text0_0_loc3` = 'Mein Szepter... Nach so vielen Jahren... Ihr habt es gefunden! Ihr seid wahrlich der Retter dieses Ortes...$B$BBitte hört mir gut zu, $N. Der Kopf und der Griff des Szepters können nur beim Stein von Maraudon zu einem einzigen Gegenstand wieder zusammengefügt werden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6375;
UPDATE `locales_npc_text` SET `ID` = 6377,`Text0_0_loc3` = 'Zwischen den Farben... ein Weg.$B$BBlinzelt hinüber ins Licht.$B$BAus dem Wasser steigt Ihr empor.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6377;
UPDATE `locales_npc_text` SET `ID` = 6378,`Text0_0_loc3` = 'Zwischen dem orangefarbenen und dem lila Durchgang in der Nähe der Türen nach Maraudon findet Ihr einen dritten Weg.$B$BDas Szepter in Eurer Hand ist der Schlüssel dazu.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6378;
UPDATE `locales_npc_text` SET `ID` = 6379,`Text0_0_loc3` = 'Ich bin es langsam leid...$B$BIhr könnt von hier aus die Wasserfälle sehen, $N... Das Szepter verleiht Euch die Macht, zu eben jenem Ort zu reisen, falls Ihr jemals den Wunsch verspürt, noch einmal durch die vorderen Tore Maraudons zu gehen.$B$BIn der Nähe des Eingangs nach Maraudon findet Ihr einen Durchgang, der unter die Höhlen führt. Benutzt das Szepter an dem alten Ritualplatz, um ein Portal zu erzeugen, dann findet Ihr Euch hier wieder.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6379;
UPDATE `locales_npc_text` SET `ID` = 6380,`Text0_0_loc3` = 'Vor langer Zeit begaben sich meine Dryaden-Schwestern und ich in diese Höhlen, um den gequälten Geist meines Onkels Zaetar zu befreien. Die Verderbnis, die sich in diesen Höhlen bereits ausgebreitet hatte, überwältigte uns jedoch schon bald und auch wir fielen ihr zum Opfer.$B$BWir waren dazu verdammt, in diesen Gängen umherzuirren. Die Toxine vergifteten unseren Verstand...$B$BUnd obwohl mein Geist jetzt zur Ruhe gekommen sein mag, weiß ich doch, dass ich bei meiner Mission versagt habe. Solch große Trauer umfängt mich jetzt...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6380;
UPDATE `locales_npc_text` SET `ID` = 6381,`Text0_0_loc3` = 'Ah, Zaetar... <Celebras wendet den Blick ab.>$B$BMein Vater Remulos versuchte, ihm Einhalt zu gebieten...$B$BZaetar war sehr eigensinnig, und als er einer Prinzessin der Erdelementare verfiel, wusste mein Vater, dass ihre Verbindung unter einem Fluch stand, aber Zaetar wollte einfach nicht auf ihn hören.$B$BDie Zentauren sind das Ergebnis seines Fehlers. Sie kamen missgebildet und hasserfüllt zur Welt. Mein Onkel Zaetar wurde von seinen eigenen Leuten getötet...$B$BDie böse Prinzessin Theradras indes wacht immer noch hier in den Kristallhöhlen über seine Gebeine.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6381;
UPDATE `locales_npc_text` SET `ID` = 6383,`Text0_0_loc3` = 'Willkommen bei der Forscherliga. Wenn Ihr Informationen braucht und diese in Büchern zu finden sind, kann ich Euch bestimmt helfen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6383;
UPDATE `locales_npc_text` SET `ID` = 6384,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6384;
UPDATE `locales_npc_text` SET `ID` = 6385,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6385;
UPDATE `locales_npc_text` SET `ID` = 6393,`Text0_0_loc3` = 'Ihr fragt nach Cenarius'' Söhnen Remulos und Zaetar.$B$BMein Vater Remulos war stark und gut aussehend und mein Onkel Zaetar äußerst schlau und von schlanker Gestalt. Obwohl man zwar beide gleichermaßen respektierte, nagte in Zaetar stets das Gefühl, Remulos würde mehr Ruhm und Aufmerksamkeit zuteil werden.$B$BAls meine Schwestern und ich geboren wurden, machte sich Zaetar, blind vor Eifersucht, auf, seinen Bruder auszustechen... Er führte damit nur sein eigenes Ende herbei. Die Trauer, die ich darüber empfinde, hat nie nachgelassen, auch nach all dieser Zeit nicht...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6393;
UPDATE `locales_npc_text` SET `ID` = 6413,`Text0_0_loc3` = 'Seid gegrüßt, $N. Ich bin Doktor Gregory Victor, Cheftraumachirurg der Traumatologie der Horde.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6413;
UPDATE `locales_npc_text` SET `ID` = 6414,`Text0_0_loc3` = 'Schön, Euch wiederzusehen, Doktor $N. Ich habe Eure Fortschritte verfolgt und würde sagen, dass es für Euch an der Zeit ist, ein paar der eher komplizierteren Verbandstechniken zu erlernen.$B$BBesucht mich doch gelegentlich, sobald sich Eure Fertigkeiten verbessern, dann werde ich Euch alles lehren, was ich weiß.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6414;
UPDATE `locales_npc_text` SET `ID` = 6415,`Text0_0_loc3` = 'Seid gegrüßt, $N. Ich bin Doktor Gustav van Howzen, Cheftraumachirurg der Traumatologie der Allianz.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6415;
UPDATE `locales_npc_text` SET `ID` = 6433,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6433;
UPDATE `locales_npc_text` SET `ID` = 6435,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Es ist gut, Veteranan wie Euch im Alteractal zu sehen, $N. Wir haben zu wenig Vorräte, um einen Bodenangriff durch das Tal zu starten. Redet mit unserem Rüstmeister - er wird Euch erklären, wie wir unsere Vorräte auffrischen können.' WHERE `locales_npc_text`.`ID` = 6435;
UPDATE `locales_npc_text` SET `ID` = 6437,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr! $N! Wieso wandert Ihr hier einfach so im Dorf herum? Allianztruppen sind in Alterac eingedrungen! Wir müssen sie vertreiben!$B$BUnsere Vorräte sind zu gering, um einen massiven Angriff zu starten, also sprecht mit unserem Rüstmeister. Er wird Euch erklären, wie wir unsere Vorräte aufstocken können.' WHERE `locales_npc_text`.`ID` = 6437;
UPDATE `locales_npc_text` SET `ID` = 6475,`Text0_0_loc3` = 'Ich bin verantwortlich für die Anwerbung beim Frostwolfklan. Als ein Soldat der Frostwölfe ist es Eure Pflicht, Euch bei mir zu melden, sobald Ihr innerhalb des Klans im Rang aufsteigt.$B$BEhre und Respekt werden hier auf diesem eisigen Schlachtfeld gewonnen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6475;
UPDATE `locales_npc_text` SET `ID` = 6476,`Text0_0_loc3` = 'Seid gegrüßt, $R! Ich bin Leutnant Haggerdin, verantwortlicher Offizier in Sachen Nachschub für die Sturmlanzengarde.$B$BWenn Euer Ruf bei den Sturmlanzen sich verbessert hat, kommt zurück zu mir, und ich werde Euch befördern. Solltet Ihr Euer Abzeichen verlieren, kann ich für Euch einen Ersatz besorgen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6476;
UPDATE `locales_npc_text` SET `ID` = 6491,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6491;
UPDATE `locales_npc_text` SET `ID` = 6513,`Text0_0_loc3` = 'Unsere Vorfahren lehren uns, dass der Einzelne stark sein muss, wir aber nur mit Vielen Sicherheit finden. Wollt Ihr die Ehre von Donnerfels verteidigen, indem Ihr eine Gilde gründet?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6513;
UPDATE `locales_npc_text` SET `ID` = 6514,`Text0_0_loc3` = 'Ihr seht wahrlich wie ein Anführer aus. Seid Ihr gekommen um eine Gilde zu organisieren, um für die Allianz zu kämpfen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6514;
UPDATE `locales_npc_text` SET `ID` = 6533,`Text0_0_loc3` = 'Seid gegrüßt, $C. Ich bin Rokaro.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6533;
UPDATE `locales_npc_text` SET `ID` = 6534,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Was wollt Ihr vom blauen Drachenschwarm?' WHERE `locales_npc_text`.`ID` = 6534;
UPDATE `locales_npc_text` SET `ID` = 6535,`Text0_0_loc3` = 'Meister Nesingwary geht auf eine anstrengende Jagd. Ich finde es wesentlich entspannender, ihm bei den Vorbereitungen für einen Tag im Dschungel zu helfen, als ihn für einen Kampf auszurüsten. Allerdings muss ich zugeben, dass mir die glorreichen Momente auf dem Schlachtfeld doch fehlen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6535;
UPDATE `locales_npc_text` SET `ID` = 6553,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich kenne die Wälder so gut, als wären sie ein Teil von mir... Doch wisset, dass man noch immer viel von ihnen lernen kann und sie bergen auch viel Gefahr.' WHERE `locales_npc_text`.`ID` = 6553;
UPDATE `locales_npc_text` SET `ID` = 6555,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ihr habt Teronis Mission erfüllt, und dafür bin ich Euch sehr dankbar, $N.' WHERE `locales_npc_text`.`ID` = 6555;
UPDATE `locales_npc_text` SET `ID` = 6556,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Hi da! Diese Werkstatt gehört mir gar nicht, nein! Aber eines Tages, wenn ich meine Ausbildung beendet habe, werde ich ein Erfinder, genau wie mein Held Marvon Nietensucher.$B$BIhr habt noch nie von ihm gehört, stimmt''s? Nun es passierte, dass er vor einiger Zeit verschwand. Man sagt er wollte einige uralte Trollruinen studieren, über die er ständig sprach. Er flog mit einem Zeppelin... ich bin mir allerdings nicht sicher wohin...$B$BTanarasin? Tenkeris? Taneranisani?$B$B<Liv schüttelt ihren Kopf.>$B$BNein, das war''s auch nicht...' WHERE `locales_npc_text`.`ID` = 6556;
UPDATE `locales_npc_text` SET `ID` = 6557,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Hallo! Nein, Marvon ist nicht hier, und er wird wohl für eine Weile nicht zurückkommen. In Wahrheit beginne ich bereits, mir Sorgen um ihn zu machen...$B$BIch hoffe, er kommt bald zurück. Es ist nämlich eine Kiste für ihn geliefert worden, und die ist für mich einfach zu schwer, um sie hier hineinzutragen. Ich musste sie erstmal draußen stehen lassen...' WHERE `locales_npc_text`.`ID` = 6557;
UPDATE `locales_npc_text` SET `ID` = 6573,`Text0_0_loc3` = 'Ihr habt bereits alles gelernt, was ich Euch bis jetzt beibringen kann, Doktor $N. Kommt zu mir zurück, wenn Ihr geübter seid, und ich werde Euch in der fortgeschrittenen medizinischen Lehre unterweisen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6573;
UPDATE `locales_npc_text` SET `ID` = 6574,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Dieser Ort... ist verflucht.$B$BIch habe es kaum geschafft zu entkommen. Daher bringe ich es kein zweites mal fertig, in diese Tunnel hinabzusteigen$B$BMein Bruder und meine Schwester hatten weniger Glück... Sie sind noch drinnen gefangen, und leiden unter dem verderbten Nebel welcher von dem dort wachsenden bösen Garten ausgeht.$B$BBitte, Ihr müsst helfen.' WHERE `locales_npc_text`.`ID` = 6574;
UPDATE `locales_npc_text` SET `ID` = 6575,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6575;
UPDATE `locales_npc_text` SET `ID` = 6594,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Braucht Ihr Vorräte?' WHERE `locales_npc_text`.`ID` = 6594;
UPDATE `locales_npc_text` SET `ID` = 6595,`Text0_0_loc3` = 'Soll ich Euch zeigen, was ich im Angebot habe?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6595;
UPDATE `locales_npc_text` SET `ID` = 6654,`Text0_0_loc3` = 'Ihr sucht Anleitung in der Kriegskunst, $R? Dann seid Ihr hier falsch.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6654;
UPDATE `locales_npc_text` SET `ID` = 6655,`Text0_0_loc3` = 'Ihr seid hier, um etwas über gewonnene und verlorene Kämpfe auf diesem Schlachtfeld zu erfahren? Hier gibt es keine Gewinner...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6655;
UPDATE `locales_npc_text` SET `ID` = 6657,`Text0_0_loc3` = 'Beeilt euch, Soldat. Der Geheimdienst hat mir berichtet, dass die Winterax-Trolle einen Meister der Goblin-Ingenieure gefangen genommen haben. Die Frostwolf-Bastarde versuchen bereits, ihn zu retten, damit sie seine technischen Geräte gegen uns verwenden können! Ihr dürft das nicht zulassen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6657;
UPDATE `locales_npc_text` SET `ID` = 6658,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6658;
UPDATE `locales_npc_text` SET `ID` = 6659,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6659;
UPDATE `locales_npc_text` SET `ID` = 6694,`Text0_0_loc3` = 'Hilflos... keine Energie mehr... wieder herstellen... muss wieder herstellen... Zevrim hat Kontrolle... beendet diese Qual...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6694;
UPDATE `locales_npc_text` SET `ID` = 6695,`Text0_0_loc3` = 'Danke, Sterblicher. Es gibt noch einen Dienst, den ich Euch erweisen kann, bevor ich wieder in den Traum zurückkehre.$B$B.Die Tür zu Zevrims Meister, Alzzin, sie blockiert Euren Weg. Ich kann Euch als Mittel der Zerstörung dienlich sein. Die Tür wird geöffnet werden, ich erwarte Euren Befehl.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6695;
