UPDATE `locales_npc_text` SET `ID` = 6791,`Text0_0_loc3` = 'Mehr Ausrüstung kann ich nicht mehr einlagern. Das war es vorerst.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6791;
UPDATE `locales_npc_text` SET `ID` = 6793,`Text0_0_loc3` = 'Ich kann Euch nichts mehr lehren. Ich hörte aber, dass der alte Mann Heming in Beutebucht ein Exemplar von "Der Barsch und du" vorrätig haben soll. Dieses Buch wird Eure Fertigkeiten sicherlich verbessern.',`Text0_1_loc3` = 'Ich kann Euch nichts mehr lehren. Ich hörte aber, dass der alte Mann Heming in Beutebucht ein Exemplar von "Der Barsch und du" vorrätig haben soll. Dieses Buch wird Eure Fertigkeiten sicherlich verbessern.' WHERE `locales_npc_text`.`ID` = 6793;
UPDATE `locales_npc_text` SET `ID` = 6794,`Text0_0_loc3` = 'Ich kann Euch nichts mehr lehren. Ich hörte aber, dass der alte Mann Heming in Beutebucht ein Exemplar von "Der Barsch und du" vorrätig haben soll. Dieses Buch wird Eure Fertigkeiten sicherlich verbessern.',`Text0_1_loc3` = 'Ich kann Euch nichts mehr lehren. Ich hörte aber, dass der alte Mann Heming in Beutebucht ein Exemplar von "Der Barsch und du" vorrätig haben soll. Dieses Buch wird Eure Fertigkeiten sicherlich verbessern.' WHERE `locales_npc_text`.`ID` = 6794;
UPDATE `locales_npc_text` SET `ID` = 6795,`Text0_0_loc3` = 'Bitte, bitte, sagt, dass Ihr hier seid, um mich von diesen Ketten zu befreien. Irgendwo hier muss ein Schlüssel sein...$B$B Tötet doch wenigstens die Wache Slip''kik... Ich helfe Euch auch gerne weiter!$B$BSeht Ihr die beschädigte Falle dort drüben? Die Oger sind einfach zu dumm, um sie zu reparieren. Dabei bräuchte man nur einen Thoriumapparat und etwas Frostöl. Wenn Ihr die wieder repariert, könnt Ihr Slip''kik bestimmt dort hinein locken und BUMM - habt Ihr eine gefangene Ogerwache!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6795;
