UPDATE `locales_npc_text` SET `ID` = 6798,`Text0_0_loc3` = 'Auf den Friedhöfen im Alteractal finden die Geister der Gefallenen Ruhe und Frieden. Wenn Ihr sterbt, erscheint Euer Geist auf dem nächsten Friedhof, der von unserer Fraktion kontrolliert wird.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6798;
UPDATE `locales_npc_text` SET `ID` = 6812,`Text0_0_loc3` = 'Schaut auf dieses verwüstete Feld, und seid Euch der Kraft der Elemente bewusst, $R.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6812;
