UPDATE `locales_npc_text` SET `ID` = 6842,`Text0_0_loc3` = 'Ich weiß viel, hört zu und lasst euch unterweisen, $R.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6842;
UPDATE `locales_npc_text` SET `ID` = 6843,`Text0_0_loc3` = 'In der Zeit vor der Zeit, welche die Ursupatoren später das erste Zeitalter der Entstehung genannt haben, herrschten mein Meister und seine Brüder über diese Welt. Sie waren es, die alten Götter, welche alles erschaffen haben. Und sie waren es, die letzten Endes von denen besiegt wurden, die sich die Former nannten.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6843;
UPDATE `locales_npc_text` SET `ID` = 6844,`Text0_0_loc3` = 'Ihre körperliche Gestalt war geschwächt von den Äonen des Kampfes. Eingesperrt unter der Welt - eine Welt, die sie einst selbst erschaffen hatten. Die Verderbtheit der Titanenformer floss schließlich durch die Oberfläche der Welt und befiel die alten Götter.$B$BEs war der Zeitpunkt, als die Brüder sich gegeneinander wandten. Fünftausend Jahre lang tobte im Kern dieser Welt eine verbitterte Schlacht.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6844;
UPDATE `locales_npc_text` SET `ID` = 6846,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6846;
UPDATE `locales_npc_text` SET `ID` = 6848,`Text0_0_loc3` = 'Die Hauptmänner der Horde und der Allianz haben ihre Stellungen bezogen. Hauptmann Galvangar befehligt die Frostwölfe von der Eisblutgarnison aus und sein Gegenüber, Hauptmann Balinda Steinbruch, versteckt sich im Steinbruchaußenposten.$B$BDie Schlacht hat begonnen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6848;
UPDATE `locales_npc_text` SET `ID` = 6851,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6851;
UPDATE `locales_npc_text` SET `ID` = 6853,`Text0_0_loc3` = 'Das Alteractal ist lange Jahre die Heimat des Frostwolfklans gewesen. Wir werden die Zwerge und ihre Allianz nicht passieren lassen! Wir werden kämpfen!$B$BUm Alterac zurückzuerobern, müssen wir ihren General Vanndar Sturmlanze in Dun Baldar töten.$B$BFür die Horde!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6853;
