UPDATE `locales_npc_text` SET `ID` = 6990,`Text0_0_loc3` = 'Es kommt die Zeit, in der unser Leben sich nicht mehr mit unseren Vorstellungen deckt. Wir kommen nicht weiter, suchen nach Antworten und finden Hoffnung, die an dunklen Plätzen in Flammen aufgeht.$B$BErst dann, $N, gehen wir unsere ersten Schritte der Freiheit entgegen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6990;
