UPDATE `locales_npc_text` SET `ID` = 6996,`Text0_0_loc3` = 'So, Ihr habt mich also gefunden. Ich vermute, dass Ihr gekommen seid, um etwas über Unterdrückung und Kontrolle zu lernen.$B$BIch werde Euch weitaus mehr als das beibringen... Ihr werdet auch etwas über Opferung lernen.$B$B<Daio grinst.>',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6996;
UPDATE `locales_npc_text` SET `ID` = 6997,`Text0_0_loc3` = 'Das wird die Wahl des Dämonen sein, den Ihr beschwören und unterjochen wollt. Wir beherrschen die dunkle Macht, für die wir einen hohen Preis bezahlen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 6997;
