UPDATE `locales_npc_text` SET `ID` = 7041,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ein herrlicher Tag, um am Leben zu sein! Seht Euch nur diese prächtige Schönheit um Euch herum an, $G mein Freund:meine Freundin;. Genießt, was das Leben Euch bietet!' WHERE `locales_npc_text`.`ID` = 7041;
