UPDATE `locales_npc_text` SET `ID` = 7044,`Text0_0_loc3` = 'Ich komme jedes Jahr um diese Zeit nach Silithus. Dieses herrliche Wetter ist einfach phänomenal für meine Gesundheit.$B$BKomm, geht ein wenig mit mir spazieren, $G mein Freund:meine Freundin;. Genießt die Aussicht und überhaupt alles!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7044;
UPDATE `locales_npc_text` SET `ID` = 7045,`Text0_0_loc3` = 'Es ist schön jemanden in dieser weit abgelegenen Region der Welt zu treffen. Kommt, gehen wir ein wenig spazieren. Unsere Körper sollen sich gegenseitig Wärme schenken.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7045;
