UPDATE `locales_npc_text` SET `ID` = 7244,`Text0_0_loc3` = 'Ah, Ihr wart einst ein erfahrener Veteran. Kilram kann Euch erneut dazu verhelfen. Ich weiß, dass Ihr fähig seid, Ihr müsst lediglich fragen und ich lehre Euch die Kunst des Axtschmieds.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7244;
UPDATE `locales_npc_text` SET `ID` = 7245,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Kommt nicht zu nahe, $N. Ich bin ungeschickt und könnte Euch den Schädel mit der Vorderseite meines Hammers einschlagen.' WHERE `locales_npc_text`.`ID` = 7245;
UPDATE `locales_npc_text` SET `ID` = 7246,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich werde Euch die Kunst des Hammerschmieds lehren. Bittet einfach nett darum.' WHERE `locales_npc_text`.`ID` = 7246;
