UPDATE `locales_npc_text` SET `ID` = 7514,`Text0_0_loc3` = 'Ich bin Jin''rokh der Zerstörer, Gesandter von Mugamba, der Faust von Rastakhan!$B$BIch schlage mich mit Kriegern und Heiden herum.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7514;
UPDATE `locales_npc_text` SET `ID` = 7515,`Text0_0_loc3` = 'Ich bin Al''tabim, der Allsehende, ein Gesandter König Rastakhans.$B$BIch bin für Priester, Magier und Hexenmeister zuständig.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7515;
UPDATE `locales_npc_text` SET `ID` = 7516,`Text0_0_loc3` = 'Der Bund von Arathor schwor Arathi für die Menschen von Stromgarde und der Allianz wiederzuerobern. Es wird eine schwere Schlacht, aber sie ist es wert gekämpft zu werden.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7516;
UPDATE `locales_npc_text` SET `ID` = 7517,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Um diesen Kampf zu gewinnen, können wir nicht tatenlos herumsitzen und einfach warten, dass der Sturm uns erfasst. Wir müssen zuschlagen, hart und schnell oder Arathi wird nie wieder unser sein.' WHERE `locales_npc_text`.`ID` = 7517;
UPDATE `locales_npc_text` SET `ID` = 7519,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Es gibt mehrere Schlachtfelder aus denen Ihr auswählen könnt. Zu welchem Kampfmeister soll ich Euch schicken?' WHERE `locales_npc_text`.`ID` = 7519;
UPDATE `locales_npc_text` SET `ID` = 7520,`Text0_0_loc3` = 'Wir müssen die Elfen und ihre feige Allianz aus der Kriegshymnenschlucht vertreiben. Begebt Euch zur Halle der Kriegerhelden im Tal der Ehre und sprecht mit Brakgul Todesbringer, wenn Ihr glaubt, dass Ihr das Zeug dazu habt, die Horde zu unterstützen.',`Text0_1_loc3` = 'Wir müssen die Elfen und ihre feige Allianz aus der Kriegshymnenschlucht vertreiben. Begebt Euch zur Halle der Kriegerhelden im Tal der Ehre und sprecht mit Brakgul Todesbringer, wenn Ihr glaubt, dass Ihr das Zeug dazu habt, die Horde zu unterstützen.' WHERE `locales_npc_text`.`ID` = 7520;
