UPDATE `locales_npc_text` SET `ID` = 7528,`Text0_0_loc3` = 'Die Kriegshymnenschlucht sagt Ihr? Na dann wollt Ihr wahrscheinlich mit der hübschen Lylandris drüben in der Halle der Waffen sprechen.',`Text0_1_loc3` = 'Die Kriegshymnenschlucht sagt Ihr? Na dann wollt Ihr wahrscheinlich mit der hübschen Lylandris drüben in der Halle der Waffen sprechen.' WHERE `locales_npc_text`.`ID` = 7528;
