UPDATE `locales_npc_text` SET `ID` = 7530,`Text0_0_loc3` = 'Dann ist es Thelman Fausttadel, mit dem Ihr sprechen wollt. Ihr findet ihn in der Gegenwart des Königs in der Burg Sturmwind.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7530;
