UPDATE `locales_npc_text` SET `ID` = 7684,`Text0_0_loc3` = 'Hallo $C. Das Alteractal benötigt dringend Eure Hilfe und wird Euch mit größerer Ehre und Anerkennung denn je belohnen. Schließt Ihr Euch uns im Kampf gegen die Sturmlanzengarde an?',`Text0_1_loc3` = 'Hallo $C. Das Alteractal benötigt dringend Eure Hilfe und wird Euch mit größerer Ehre und Anerkennung denn je belohnen. Schließt Ihr Euch uns im Kampf gegen die Sturmlanzengarde an?' WHERE `locales_npc_text`.`ID` = 7684;
