UPDATE `locales_npc_text` SET `ID` = 7689,`Text0_0_loc3` = '$C, wir benötigen dringend Eure Hilfe in der Kriegshymnenschlucht, im Kampf gegen die Schildwachen der Silberschwingen. Ihr könnt auch noch mehr Ehre und Anerkennung bei der Vorhut des Kriegshymnenklans erlangen! Seid Ihr mutig genug, um Euch in die Schlacht zu begeben?',`Text0_1_loc3` = '$C, wir benötigen dringend Eure Hilfe in der Kriegshymnenschlucht, im Kampf gegen die Schildwachen der Silberschwingen. Ihr könnt auch noch mehr Ehre und Anerkennung bei der Vorhut des Kriegshymnenklans erlangen! Seid Ihr mutig genug, um Euch in die Schlacht zu begeben?' WHERE `locales_npc_text`.`ID` = 7689;
