UPDATE `locales_npc_text` SET `ID` = 7693,`Text0_0_loc3` = 'Ich glaube nicht, dass Ihr lange im Alteractal überleben würdet, $C. Kommt und sprecht mit uns, sobald Ihr ein paar Winter mehr unter Eurem Gürtel habt.',`Text0_1_loc3` = 'Ich glaube nicht, dass Ihr lange im Alteractal überleben würdet, $C. Kommt und sprecht mit uns, sobald Ihr ein paar Winter mehr unter Eurem Gürtel habt.' WHERE `locales_npc_text`.`ID` = 7693;
UPDATE `locales_npc_text` SET `ID` = 7694,`Text0_0_loc3` = '$N, die Sturmlanzengarde benötigt mehr denn je Eure Unterstützung im Kampf gegen den Frostwolfklan im Alteractal. Die Möglichkeiten, dort große Ehre und Anerkennung zu erlangen, sind ebenfalls größer denn je! Schließt Ihr Euch uns im Kampf an?',`Text0_1_loc3` = '$N, die Sturmlanzengarde benötigt mehr denn je Eure Unterstützung im Kampf gegen den Frostwolfklan im Alteractal. Die Möglichkeiten, dort große Ehre und Anerkennung zu erlangen, sind ebenfalls größer denn je! Schließt Ihr Euch uns im Kampf an?' WHERE `locales_npc_text`.`ID` = 7694;
UPDATE `locales_npc_text` SET `ID` = 7696,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Fischereimeister Barschfang hat hier in den Gewässern Leckerfisch freigelassen, damit sein Wettbewerb stattfinden kann.$B$BSobald der Gewinner feststeht, werde ich Euch von da an für jeweils fünf Leckerfische, die Ihr mir bringt, Silber geben.' WHERE `locales_npc_text`.`ID` = 7696;
