UPDATE `locales_npc_text` SET `ID` = 7825,`Text0_0_loc3` = 'Im Inneren des Kristalls scheinen sich höhlenartige Tunnelsysteme und Korridore wiederzuspiegeln. Ihr erkennt die feurigen Tiefen des geschmolzenen Kerns.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7825;
