UPDATE `locales_npc_text` SET `ID` = 7828,`Text0_0_loc3` = 'Nicht alle Tauren verachten Euresgleichen, $R. Dennoch ist es mir nicht erlaubt, Euch meine Dienste anzubieten.',`Text0_1_loc3` = 'Nicht alle Tauren verachten Euresgleichen, $R. Dennoch ist es mir nicht erlaubt, Euch meine Dienste anzubieten.' WHERE `locales_npc_text`.`ID` = 7828;
