UPDATE `locales_npc_text` SET `ID` = 7839,`Text0_0_loc3` = 'Ich erhalte regelmäßige Berichte über Eure Aktionen gegen den Abyssischen Rat, $N. Ihr leistet dort draußen hervorragende Arbeit!$B$BMacht weiter so und wir werden Euch dafür entlohnen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7839;
