UPDATE `locales_npc_text` SET `ID` = 7855,`Text0_0_loc3` = 'Leider bin ich viel zu beschäftigt für ein Gespräch, $N. Ich brauche Zeit, um über die vorliegende Situation nachzudenken.$B$BViel Glück auf Euren Reisen! Auf Wiedersehen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7855;
