UPDATE `locales_npc_text` SET `ID` = 7869,`Text0_0_loc3` = '$N, es tut gut, Euch wiederzusehen.$B$BDenkt immer daran, Euch auf Eure Studien zu konzentrieren. Ihr seid unsere Zukunft, und falls Ihr zögert, sieht unsere Zukunft wahrlich düster aus.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7869;
UPDATE `locales_npc_text` SET `ID` = 7870,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7870;
UPDATE `locales_npc_text` SET `ID` = 7872,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Willkommen im Gasthaus Zum Falkenplatz. Kann ich Euch etwas zu essen bringen, oder sucht Ihr ein Zimmer?' WHERE `locales_npc_text`.`ID` = 7872;
UPDATE `locales_npc_text` SET `ID` = 7874,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Grüße, $C. Die Brigade von Eisenschmiede wurde hier stationiert, um den Bau des Zoraschwarms im Auge zu behalten. Die Druiden des Zirkels des Cenarius haben diese Gegend überwacht und erwarten hier etwas Bedeutendes.' WHERE `locales_npc_text`.`ID` = 7874;
UPDATE `locales_npc_text` SET `ID` = 7875,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Der Hauptmann ist momentan beschäftigt. Er studiert gerade Karten der Silithidenbäue, die wir vor Kurzem erhalten haben. Bleibt einen Moment hier, wenn Ihr helfen wollt. Er wird sicherlich bald herauskommen.' WHERE `locales_npc_text`.`ID` = 7875;
UPDATE `locales_npc_text` SET `ID` = 7876,`Text0_0_loc3` = NULL,`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 7876;
